import SwiftUI
import AVKit
import AVFoundation
import Combine

class PlayerViewModel: ObservableObject {
    @Published var player: AVPlayer?
    @Published var isPlaying = false
    @Published var currentTime: CMTime = .zero
    @Published var duration: CMTime = .zero
    @Published var currentFrame: Int = 0
    @Published var video: Video?
    @Published var error: String?

    private var timeObserver: Any?
    private var cancellables = Set<AnyCancellable>()
    private let timeScale: CMTimeScale = 600

    // Metadata integration
    private var metadataManager: MetadataManager?
    private var markerViewModel: MarkerViewModel?

    deinit {
        if let observer = timeObserver {
            player?.removeTimeObserver(observer)
        }
    }

    func setMetadataManager(_ metadataManager: MetadataManager) {
        self.metadataManager = metadataManager
    }

    func setMarkerViewModel(_ markerViewModel: MarkerViewModel) {
        self.markerViewModel = markerViewModel
    }

    func loadVideo(url: URL) {
        error = nil

        let newVideo = Video(url: url)
        self.video = newVideo

        let playerItem = AVPlayerItem(asset: newVideo.asset)
        player = AVPlayer(playerItem: playerItem)

        playerItem.publisher(for: \.status)
            .sink { [weak self] status in
                if status == .readyToPlay {
                    self?.setupPlayer()
                    self?.loadOrCreateMetadata(for: url)
                } else if status == .failed {
                    self?.error = "Failed to load video"
                }
            }
            .store(in: &cancellables)
    }

    private func setupPlayer() {
        guard let player = player,
              let video = video,
              let duration = video.duration else { return }

        self.duration = duration

        if let observer = timeObserver {
            player.removeTimeObserver(observer)
        }

        let interval = CMTime(seconds: 0.01, preferredTimescale: timeScale)
        timeObserver = player.addPeriodicTimeObserver(forInterval: interval, queue: .main) { [weak self] time in
            self?.updateTime(time)
        }

        player.publisher(for: \.rate)
            .sink { [weak self] rate in
                self?.isPlaying = rate > 0
            }
            .store(in: &cancellables)
    }

    private func updateTime(_ time: CMTime) {
        currentTime = time

        if let frameRate = video?.frameRate {
            currentFrame = FramePrecision.frameNumber(from: time, frameRate: frameRate)
        }
    }

    func togglePlayPause() {
        guard let player = player else { return }

        if isPlaying {
            player.pause()
        } else {
            player.play()
        }
    }

    func seek(to progress: Double) {
        guard let player = player,
              duration.isValid && !duration.isIndefinite else { return }

        let targetTime = CMTimeMultiplyByFloat64(duration, multiplier: progress)

        player.seek(to: targetTime, toleranceBefore: .zero, toleranceAfter: .zero) { [weak self] _ in
            self?.updateTime(targetTime)
        }
    }

    func seek(to time: CMTime) {
        guard let player = player else { return }

        player.seek(to: time, toleranceBefore: .zero, toleranceAfter: .zero) { [weak self] _ in
            self?.updateTime(time)
        }
    }

    var progress: Double {
        guard duration.isValid && !duration.isIndefinite && duration.value > 0 else { return 0 }
        return currentTime.seconds / duration.seconds
    }

    var currentTimeFormatted: String {
        FramePrecision.formatTime(currentTime)
    }

    var durationFormatted: String {
        FramePrecision.formatTime(duration)
    }

    // MARK: - Metadata Integration

    private func loadOrCreateMetadata(for videoURL: URL) {
        guard let metadataManager = metadataManager else { return }

        // Try to load existing metadata
        if let existingMetadata = metadataManager.loadMetadataForVideo(at: videoURL) {
            // Load markers from metadata
            markerViewModel?.loadMarkersFromMetadata(existingMetadata)
        } else {
            // Create new metadata for this video
            let frameRate = video?.frameRate
            let duration = self.duration.isValid ? self.duration : nil

            _ = metadataManager.createMetadataForVideo(
                at: videoURL,
                frameRate: frameRate,
                duration: duration
            )
        }
    }
}

import Foundation
import AVFoundation

struct VideoMetadata: Codable {
    let videoFileName: String
    let videoChecksum: String?
    var markers: [Marker]
    var lastModified: Date
    var version: String = "1.0"

    // Video validation metadata
    var videoFrameRate: Float?
    var videoDuration: CMTime?

    init(videoFileName: String, videoChecksum: String? = nil) {
        self.videoFileName = videoFileName
        self.videoChecksum = videoChecksum
        self.markers = []
        self.lastModified = Date()
    }

    // Update last modified timestamp
    mutating func touch() {
        lastModified = Date()
    }
}

// MARK: - CMTime Codable Support
extension VideoMetadata {
    enum CodingKeys: String, CodingKey {
        case videoFileName, videoChecksum, markers, lastModified, version, videoFrameRate, videoDurationValue, videoDurationTimescale
    }

    init(from decoder: Decoder) throws {
        let container = try decoder.container(keyedBy: CodingKeys.self)

        videoFileName = try container.decode(String.self, forKey: .videoFileName)
        videoChecksum = try container.decodeIfPresent(String.self, forKey: .videoChecksum)
        markers = try container.decode([Marker].self, forKey: .markers)
        lastModified = try container.decode(Date.self, forKey: .lastModified)
        version = try container.decodeIfPresent(String.self, forKey: .version) ?? "1.0"
        videoFrameRate = try container.decodeIfPresent(Float.self, forKey: .videoFrameRate)

        // Decode CMTime
        if let durationValue = try container.decodeIfPresent(Int64.self, forKey: .videoDurationValue),
           let durationTimescale = try container.decodeIfPresent(Int32.self, forKey: .videoDurationTimescale) {
            videoDuration = CMTime(value: durationValue, timescale: durationTimescale)
        } else {
            videoDuration = nil
        }
    }

    func encode(to encoder: Encoder) throws {
        var container = encoder.container(keyedBy: CodingKeys.self)

        try container.encode(videoFileName, forKey: .videoFileName)
        try container.encodeIfPresent(videoChecksum, forKey: .videoChecksum)
        try container.encode(markers, forKey: .markers)
        try container.encode(lastModified, forKey: .lastModified)
        try container.encode(version, forKey: .version)
        try container.encodeIfPresent(videoFrameRate, forKey: .videoFrameRate)

        // Encode CMTime
        if let duration = videoDuration {
            try container.encode(duration.value, forKey: .videoDurationValue)
            try container.encode(duration.timescale, forKey: .videoDurationTimescale)
        }
    }
}

