import SwiftUI
import AVKit
import AVFoundation
import Combine

class PlayerViewModel: ObservableObject {
    @Published var player: AVPlayer?
    @Published var isPlaying = false
    @Published var currentTime: CMTime = .zero
    @Published var duration: CMTime = .zero
    @Published var currentFrame: Int = 0
    @Published var video: Video?
    @Published var error: String?
    @Published var playbackRate: Float = 1.0

    private var timeObserver: Any?
    private var timeObserverPlayer: AVPlayer? // Keep reference to the player that has the observer
    private var cancellables = Set<AnyCancellable>()
    private let timeScale: CMTimeScale = 600
    private var lastUpdateTime: CMTime = .zero

    // Smooth scrubbing properties
    private var isScrubbing = false
    private var scrubbingStartTime: Date?
    private var lastSeekTime: CMTime = .zero
    private var debounceTimer: Timer?

    // Performance caching
    private let thumbnailCache = VideoThumbnailCache()
    private let frameCache = FrameCache()
    private let preloadingManager: PreloadingManager
    private let metalRenderer = MetalRenderer()
    private let backgroundLoader = BackgroundAssetLoader()

    // Metadata integration
    private var metadataManager: MetadataManager?
    private var markerViewModel: MarkerViewModel?

    init() {
        preloadingManager = PreloadingManager(thumbnailCache: thumbnailCache, frameCache: frameCache)
    }

    deinit {
        removeTimeObserver()
        debounceTimer?.invalidate()
        preloadingManager.stopPreloading()
    }

    private func removeTimeObserver() {
        if let observer = timeObserver, let observerPlayer = timeObserverPlayer {
            observerPlayer.removeTimeObserver(observer)
            timeObserver = nil
            timeObserverPlayer = nil
        }
    }

    func setMetadataManager(_ metadataManager: MetadataManager) {
        self.metadataManager = metadataManager
    }

    func setMarkerViewModel(_ markerViewModel: MarkerViewModel) {
        self.markerViewModel = markerViewModel
    }

    func loadVideo(url: URL) {
        error = nil

        // Remove existing time observer before creating new player
        removeTimeObserver()

        let newVideo = Video(url: url)
        self.video = newVideo

        // Load video metadata asynchronously before creating player
        Task {
            do {
                let metadata = try await newVideo.loadMetadata()

                // Update on main actor to avoid publishing during view updates
                await MainActor.run {
                    // Update duration and frame rate from async metadata
                    if let duration = metadata.duration {
                        self.duration = duration
                    }

                    // Create player after metadata is loaded
                    let playerItem = AVPlayerItem(asset: newVideo.asset)
                    self.player = AVPlayer(playerItem: playerItem)

                    // Initialize caches with the new asset
                    self.thumbnailCache.setAsset(newVideo.asset)
                    self.frameCache.setAsset(newVideo.asset)
                    self.preloadingManager.setAsset(newVideo.asset)

                    // Start cache warming in background
                    Task {
                        await self.preloadingManager.warmCaches(for: newVideo.asset)
                        await MainActor.run {
                            self.preloadingManager.startPreloading(around: .zero)
                        }
                    }

                    playerItem.publisher(for: \.status)
                        .sink { [weak self] status in
                            Task { @MainActor in
                                if status == .readyToPlay {
                                    self?.setupPlayerWithMetadata(metadata: metadata)
                                    self?.loadOrCreateMetadata(for: url, with: metadata)
                                } else if status == .failed {
                                    self?.error = "Failed to load video"
                                }
                            }
                        }
                        .store(in: &self.cancellables)
                }

            } catch {
                await MainActor.run {
                    self.error = "Failed to load video metadata: \(error.localizedDescription)"
                }
            }
        }
    }

    private func setupPlayer() {
        guard let player = player,
              let video = video,
              let duration = video.duration else { return }

        self.duration = duration

        removeTimeObserver()

        let interval = CMTime(seconds: 0.016, preferredTimescale: timeScale) // ~60fps update rate
        timeObserver = player.addPeriodicTimeObserver(forInterval: interval, queue: .main) { [weak self] time in
            // Ensure updates happen on main thread without triggering view update warnings
            DispatchQueue.main.async {
                self?.updateTime(time)
            }
        }
        timeObserverPlayer = player

        player.publisher(for: \.rate)
            .sink { [weak self] rate in
                DispatchQueue.main.async {
                    self?.isPlaying = rate > 0
                }
            }
            .store(in: &cancellables)
    }

    private func setupPlayerWithMetadata(metadata: Video.VideoMetadata) {
        guard let player = player else { return }

        // Duration is already set from async metadata loading

        removeTimeObserver()

        let interval = CMTime(seconds: 0.016, preferredTimescale: timeScale) // ~60fps update rate
        timeObserver = player.addPeriodicTimeObserver(forInterval: interval, queue: .main) { [weak self] time in
            // Ensure updates happen on main thread without triggering view update warnings
            DispatchQueue.main.async {
                self?.updateTimeWithMetadata(time, frameRate: metadata.frameRate)
            }
        }
        timeObserverPlayer = player

        player.publisher(for: \.rate)
            .sink { [weak self] rate in
                DispatchQueue.main.async {
                    self?.isPlaying = rate > 0
                }
            }
            .store(in: &cancellables)
    }

    private func updateTime(_ time: CMTime) {
        // Simple debouncing: only update if time has changed meaningfully
        let timeDifference = abs(time.seconds - lastUpdateTime.seconds)
        guard timeDifference > 0.005 else { return } // 5ms minimum change

        lastUpdateTime = time
        currentTime = time

        if let frameRate = video?.frameRate {
            currentFrame = FramePrecision.frameNumber(from: time, frameRate: frameRate)
        }
    }

    private func updateTimeWithMetadata(_ time: CMTime, frameRate: Float?) {
        // Simple debouncing: only update if time has changed meaningfully
        let timeDifference = abs(time.seconds - lastUpdateTime.seconds)
        guard timeDifference > 0.005 else { return } // 5ms minimum change

        lastUpdateTime = time
        currentTime = time

        if let frameRate = frameRate {
            currentFrame = FramePrecision.frameNumber(from: time, frameRate: frameRate)
        }
    }

    func togglePlayPause() {
        guard let player = player else { return }

        if isPlaying {
            player.pause()
        } else {
            // Restore the current playback rate when resuming
            player.rate = playbackRate
        }
    }

    // MARK: - Playback Speed Controls

    func setPlaybackRate(_ rate: Float) {
        guard let player = player else { return }

        // Clamp rate to supported range (0.25x to 4.0x)
        let clampedRate = max(0.25, min(4.0, rate))
        playbackRate = clampedRate

        if isPlaying {
            player.rate = clampedRate
        }

        // Save speed preference to metadata
        savePlaybackSpeedToMetadata()
    }

    func increaseSpeed() {
        let newRate: Float
        if playbackRate < 1.0 {
            newRate = min(1.0, playbackRate + 0.25)
        } else {
            newRate = min(4.0, playbackRate + 0.5)
        }
        setPlaybackRate(newRate)
    }

    func decreaseSpeed() {
        let newRate: Float
        if playbackRate <= 1.0 {
            newRate = max(0.25, playbackRate - 0.25)
        } else {
            newRate = max(1.0, playbackRate - 0.5)
        }
        setPlaybackRate(newRate)
    }

    func resetSpeed() {
        setPlaybackRate(1.0)
    }

    private func savePlaybackSpeedToMetadata() {
        // Save speed preference for current video
        if let metadataManager = metadataManager,
           let videoURL = video?.url {
            Task {
                await metadataManager.updatePlaybackRate(for: videoURL, rate: playbackRate)
            }
        }
    }

    func seek(to progress: Double) {
        guard player != nil,
              duration.isValid && !duration.isIndefinite else { return }

        let targetTime = CMTimeMultiplyByFloat64(duration, multiplier: progress)
        performSeek(to: targetTime, isScrubbing: false)
    }

    func seek(to time: CMTime) {
        guard player != nil else { return }
        performSeek(to: time, isScrubbing: false)
    }

    // MARK: - Smooth Scrubbing

    func startScrubbing() {
        isScrubbing = true
        scrubbingStartTime = Date()
        debounceTimer?.invalidate()
    }

    func endScrubbing() {
        isScrubbing = false
        scrubbingStartTime = nil
        debounceTimer?.invalidate()
        debounceTimer = nil
    }

    func scrubTo(progress: Double) {
        guard player != nil,
              duration.isValid && !duration.isIndefinite else { return }

        let targetTime = CMTimeMultiplyByFloat64(duration, multiplier: progress)

        // Preload frames around target time for smooth scrubbing
        preloadingManager.preloadAroundScrubPosition(targetTime, intensity: .medium)

        // Cancel previous debounce timer
        debounceTimer?.invalidate()

        // Debounce rapid scrubbing gestures
        debounceTimer = Timer.scheduledTimer(withTimeInterval: 0.05, repeats: false) { [weak self] _ in
            self?.performSeek(to: targetTime, isScrubbing: true)
        }

        // Update UI immediately for responsiveness
        currentTime = targetTime
        if let frameRate = video?.frameRate {
            currentFrame = FramePrecision.frameNumber(from: targetTime, frameRate: frameRate)
        }
    }

    private func performSeek(to targetTime: CMTime, isScrubbing: Bool) {
        guard let player = player else { return }

        // Calculate adaptive tolerance based on scrubbing speed and context
        let tolerance = calculateTolerance(for: targetTime, isScrubbing: isScrubbing)

        player.seek(to: targetTime, toleranceBefore: tolerance, toleranceAfter: tolerance) { [weak self] finished in
            guard finished else { return }

            DispatchQueue.main.async {
                self?.lastSeekTime = targetTime
                if !isScrubbing {
                    self?.updateTime(targetTime)
                }
            }
        }
    }

    private func calculateTolerance(for targetTime: CMTime, isScrubbing: Bool) -> CMTime {
        if isScrubbing {
            // Calculate scrubbing speed
            let timeSinceStart = scrubbingStartTime.map { Date().timeIntervalSince($0) } ?? 0
            let timeDifference = abs(targetTime.seconds - lastSeekTime.seconds)

            if timeSinceStart > 0 && timeDifference > 0 {
                let scrubbingSpeed = timeDifference / max(timeSinceStart, 0.1)

                // Fast scrubbing: use higher tolerance for performance
                if scrubbingSpeed > 5.0 {
                    return CMTime(seconds: 0.5, preferredTimescale: timeScale)
                }
                // Medium scrubbing: moderate tolerance
                else if scrubbingSpeed > 1.0 {
                    return CMTime(seconds: 0.1, preferredTimescale: timeScale)
                }
            }

            // Slow/precise scrubbing: low tolerance
            return CMTime(seconds: 0.05, preferredTimescale: timeScale)
        } else {
            // Non-scrubbing seeks: precise timing
            return CMTime(seconds: 0.01, preferredTimescale: timeScale)
        }
    }

    // MARK: - Configuration-Aware Scrubbing

    /// Scrub by seconds using smooth seeking (for when playing)
    func scrubBySeconds(_ seconds: Double) {
        guard let player = player else { return }

        let currentTime = player.currentTime()
        let targetTime = CMTimeAdd(currentTime, CMTime(seconds: seconds, preferredTimescale: timeScale))

        // Clamp time to valid boundaries
        let clampedTime = clampTime(targetTime)

        // Use smooth seeking with appropriate tolerance
        performSeek(to: clampedTime, isScrubbing: false)
    }

    /// Scrub by frames using smooth seeking (for when paused)
    func scrubByFrames(_ frameOffset: Int) {
        guard let player = player,
              let frameRate = video?.frameRate else { return }

        let currentFrame = FramePrecision.frameNumber(from: player.currentTime(), frameRate: frameRate)
        let targetFrame = max(0, currentFrame + frameOffset)

        // Ensure we don't go past the end
        var clampedFrame = targetFrame
        if duration.isValid && !duration.isIndefinite {
            let totalFrames = FramePrecision.totalFrames(for: duration, frameRate: frameRate)
            clampedFrame = min(targetFrame, totalFrames - 1)
        }

        let targetTime = FramePrecision.time(from: clampedFrame, frameRate: frameRate)

        // Use smooth seeking with precise tolerance for frame-accurate seeking
        let tolerance = CMTime(seconds: 0.01, preferredTimescale: timeScale)
        player.seek(to: targetTime, toleranceBefore: tolerance, toleranceAfter: tolerance) { [weak self] finished in
            guard finished else { return }

            DispatchQueue.main.async {
                self?.lastSeekTime = targetTime
                self?.updateTime(targetTime)
            }
        }
    }

    /// Clamp time to valid boundaries (0 to duration)
    private func clampTime(_ time: CMTime) -> CMTime {
        guard duration.isValid && !duration.isIndefinite else { return time }

        // Ensure time is not negative
        let positiveTime = CMTimeMaximum(time, .zero)

        // Ensure time doesn't exceed duration
        return CMTimeMinimum(positiveTime, duration)
    }

    var progress: Double {
        guard duration.isValid && !duration.isIndefinite && duration.value > 0 else { return 0 }
        return currentTime.seconds / duration.seconds
    }

    var currentTimeFormatted: String {
        FramePrecision.formatTime(currentTime)
    }

    var durationFormatted: String {
        FramePrecision.formatTime(duration)
    }

    // MARK: - Metadata Integration

    private func loadOrCreateMetadata(for videoURL: URL) {
        guard let metadataManager = metadataManager else { return }

        // Try to load existing metadata
        if let existingMetadata = metadataManager.loadMetadataForVideo(at: videoURL) {
            // Load markers from metadata
            markerViewModel?.loadMarkersFromMetadata(existingMetadata)
        } else {
            // Create new metadata for this video
            let frameRate = video?.frameRate
            let duration = self.duration.isValid ? self.duration : nil

            _ = metadataManager.createMetadataForVideo(
                at: videoURL,
                frameRate: frameRate,
                duration: duration
            )
        }
    }

    private func loadOrCreateMetadata(for videoURL: URL, with metadata: Video.VideoMetadata) {
        guard let metadataManager = metadataManager else { return }

        // Try to load existing metadata
        if let existingMetadata = metadataManager.loadMetadataForVideo(at: videoURL) {
            // Load markers from metadata
            markerViewModel?.loadMarkersFromMetadata(existingMetadata)
        } else {
            // Create new metadata for this video using async metadata
            _ = metadataManager.createMetadataForVideo(
                at: videoURL,
                frameRate: metadata.frameRate,
                duration: metadata.duration
            )
        }
    }

    // MARK: - Performance Monitoring

    func getCacheStats() -> (thumbnails: (count: Int, memory: String), frames: (count: Int, memory: String)) {
        let thumbnailStats = thumbnailCache.getCacheStats()
        let frameStats = frameCache.getCacheStats()
        return (thumbnails: thumbnailStats, frames: frameStats)
    }

    func getThumbnail(at time: CMTime) async -> CGImage? {
        return await thumbnailCache.getThumbnail(at: time)
    }

    func getFrame(at time: CMTime) async -> CVPixelBuffer? {
        return await frameCache.getFrame(at: time)
    }

    // MARK: - Advanced Performance Features

    func getEnhancedFrame(at time: CMTime, options: FrameEnhancementOptions = .default) async -> CVPixelBuffer? {
        guard let frame = await frameCache.getFrame(at: time) else { return nil }
        return metalRenderer.enhanceFrame(frame, options: options)
    }

    func updatePreloadingPosition() {
        guard let player = player else { return }
        preloadingManager.updatePreloadingPosition(player.currentTime())
    }

    func getPerformanceMetrics() -> PlayerPerformanceMetrics {
        let cacheStats = getCacheStats()
        let preloadingStatus = preloadingManager.getPreloadingStatus()
        let metalMetrics = metalRenderer.getPerformanceMetrics()
        let backgroundLoadingStatus = backgroundLoader.getLoadingStatus()

        return PlayerPerformanceMetrics(
            thumbnailCache: cacheStats.thumbnails,
            frameCache: cacheStats.frames,
            preloadingActive: preloadingStatus.isActive,
            metalAcceleration: metalMetrics.isHardwareAccelerated,
            backgroundLoading: backgroundLoadingStatus
        )
    }

    func optimizeForScrubbingMode(_ enabled: Bool) {
        if enabled {
            // Increase preloading intensity for scrubbing
            preloadingManager.startPreloading(around: currentTime)
        } else {
            // Reduce preloading to save resources
            preloadingManager.stopPreloading()
        }
    }
}

// MARK: - Performance Metrics Types

struct PlayerPerformanceMetrics {
    let thumbnailCache: (count: Int, memory: String)
    let frameCache: (count: Int, memory: String)
    let preloadingActive: Bool
    let metalAcceleration: Bool
    let backgroundLoading: (active: Int, cached: Int, progress: Double)
}

