import Foundation
import AVFoundation

class ExportService {

    // MARK: - CSV Export

    func exportMarkersToCSV(_ markers: [Marker], videoFileName: String) -> String {
        var csv = "Video File,Marker ID,Timestamp (Seconds),Timestamp (HH:MM:SS:FF),Frame Number,Rating,Label,Notes\n"

        for marker in markers {
            let seconds = marker.timestamp.seconds
            let timeString = formatTimeAsTimecode(marker.timestamp)
            let frameNumber = Int(marker.timestamp.seconds * 30) // Assuming 30fps, could be made dynamic
            let rating = marker.rating
            let label = marker.label.isEmpty ? "" : marker.label
            let notes = "" // Could be added to Marker model if needed

            // Escape CSV values
            let escapedVideoFileName = escapeCSVValue(videoFileName)
            let escapedLabel = escapeCSVValue(label)
            let escapedNotes = escapeCSVValue(notes)

            csv += "\(escapedVideoFileName),\(marker.id),\(seconds),\(timeString),\(frameNumber),\(rating),\(escapedLabel),\(escapedNotes)\n"
        }

        return csv
    }

    // MARK: - JSON Export

    func exportMarkersToJSON(_ markers: [Marker], videoFileName: String, frameRate: Float? = nil) throws -> String {
        let exportData = MarkerExportData(
            videoFileName: videoFileName,
            frameRate: frameRate,
            exportDate: Date(),
            markerCount: markers.count,
            markers: markers.map { marker in
                MarkerExportItem(
                    id: marker.id,
                    timestampSeconds: marker.timestamp.seconds,
                    timestampTimecode: formatTimeAsTimecode(marker.timestamp),
                    frameNumber: frameRate != nil ? Int(marker.timestamp.seconds * Double(frameRate!)) : nil,
                    rating: marker.rating,
                    label: marker.label.isEmpty ? nil : marker.label
                )
            }
        )

        let encoder = JSONEncoder()
        encoder.outputFormatting = [.prettyPrinted, .sortedKeys]
        encoder.dateEncodingStrategy = .iso8601

        let data = try encoder.encode(exportData)
        return String(data: data, encoding: .utf8) ?? ""
    }

    // MARK: - Segment Export

    func exportSegmentsToCSV(_ segments: [Segment], videoFileName: String) -> String {
        var csv = "Video File,Segment Index,Start Time (Seconds),End Time (Seconds),Duration (Seconds),Start Timecode,End Timecode,Start Marker Rating,End Marker Rating\n"

        for (index, segment) in segments.enumerated() {
            let startSeconds = segment.startTime.seconds
            let endSeconds = segment.endTime.seconds
            let duration = endSeconds - startSeconds
            let startTimecode = formatTimeAsTimecode(segment.startTime)
            let endTimecode = formatTimeAsTimecode(segment.endTime)

            let escapedVideoFileName = escapeCSVValue(videoFileName)

            csv += "\(escapedVideoFileName),\(index),\(startSeconds),\(endSeconds),\(duration),\(startTimecode),\(endTimecode),\(segment.startMarker.rating),\(segment.endMarker.rating)\n"
        }

        return csv
    }

    func exportSegmentsToJSON(_ segments: [Segment], videoFileName: String, frameRate: Float? = nil) throws -> String {
        let exportData = SegmentExportData(
            videoFileName: videoFileName,
            frameRate: frameRate,
            exportDate: Date(),
            segmentCount: segments.count,
            segments: segments.enumerated().map { (index, segment) in
                SegmentExportItem(
                    index: index,
                    startTimeSeconds: segment.startTime.seconds,
                    endTimeSeconds: segment.endTime.seconds,
                    durationSeconds: segment.endTime.seconds - segment.startTime.seconds,
                    startTimecode: formatTimeAsTimecode(segment.startTime),
                    endTimecode: formatTimeAsTimecode(segment.endTime),
                    startMarker: MarkerExportItem(
                        id: segment.startMarker.id,
                        timestampSeconds: segment.startMarker.timestamp.seconds,
                        timestampTimecode: formatTimeAsTimecode(segment.startMarker.timestamp),
                        frameNumber: frameRate != nil ? Int(segment.startMarker.timestamp.seconds * Double(frameRate!)) : nil,
                        rating: segment.startMarker.rating,
                        label: segment.startMarker.label.isEmpty ? nil : segment.startMarker.label
                    ),
                    endMarker: MarkerExportItem(
                        id: segment.endMarker.id,
                        timestampSeconds: segment.endMarker.timestamp.seconds,
                        timestampTimecode: formatTimeAsTimecode(segment.endMarker.timestamp),
                        frameNumber: frameRate != nil ? Int(segment.endMarker.timestamp.seconds * Double(frameRate!)) : nil,
                        rating: segment.endMarker.rating,
                        label: segment.endMarker.label.isEmpty ? nil : segment.endMarker.label
                    )
                )
            }
        )

        let encoder = JSONEncoder()
        encoder.outputFormatting = [.prettyPrinted, .sortedKeys]
        encoder.dateEncodingStrategy = .iso8601

        let data = try encoder.encode(exportData)
        return String(data: data, encoding: .utf8) ?? ""
    }

    // MARK: - Filtering

    func filterMarkersByRating(_ markers: [Marker], minimumRating: Int) -> [Marker] {
        return markers.filter { $0.rating >= minimumRating }
    }

    func filterMarkersByLabel(_ markers: [Marker], labelContains: String) -> [Marker] {
        guard !labelContains.isEmpty else { return markers }
        return markers.filter { $0.label.lowercased().contains(labelContains.lowercased()) }
    }

    // MARK: - Helper Functions

    private func formatTimeAsTimecode(_ time: CMTime) -> String {
        let totalSeconds = Int(time.seconds)
        let hours = totalSeconds / 3600
        let minutes = (totalSeconds % 3600) / 60
        let seconds = totalSeconds % 60
        let frames = Int((time.seconds - Double(totalSeconds)) * 30) // Assuming 30fps

        return String(format: "%02d:%02d:%02d:%02d", hours, minutes, seconds, frames)
    }

    private func escapeCSVValue(_ value: String) -> String {
        if value.contains(",") || value.contains("\"") || value.contains("\n") {
            return "\"" + value.replacingOccurrences(of: "\"", with: "\"\"") + "\""
        }
        return value
    }
}

