import SwiftUI

@main
struct MediaPlayerApp: App {
    init() {
        // Check for accessibility permissions for global shortcuts
        if !KeyboardShortcutManager.hasAccessibilityPermissions() {
            print("[MediaPlayerApp] Global shortcuts require accessibility permissions")
            // Optionally request permissions on first launch
            // _ = KeyboardShortcutManager.requestAccessibilityPermissions()
        }
    }

    var body: some Scene {
        WindowGroup {
            ContentView()
        }
        .commands {
            CommandGroup(replacing: .newItem) {
                Button("Open Video...") {
                    NSApp.sendAction(#selector(NSResponder.showOpenPanel), to: nil, from: nil)
                }
                .keyboardShortcut("O", modifiers: .command)
            }

            CommandGroup(after: .newItem) {
                Button("Keyboard Shortcuts...") {
                    NotificationCenter.default.post(name: .showKeyboardShortcuts, object: nil)
                }
                .keyboardShortcut("K", modifiers: .command)
            }
        }
    }
}

extension NSResponder {
    @objc func showOpenPanel() {
        let panel = NSOpenPanel()
        panel.allowedContentTypes = [.movie, .mpeg4Movie, .quickTimeMovie]
        panel.canChooseFiles = true
        panel.canChooseDirectories = false
        panel.allowsMultipleSelection = false

        if panel.runModal() == .OK {
            if let url = panel.url {
                NotificationCenter.default.post(name: .openVideoFile, object: url)
            }
        }
    }
}

extension Notification.Name {
    static let openVideoFile = Notification.Name("openVideoFile")
    static let showKeyboardShortcuts = Notification.Name("showKeyboardShortcuts")
}

