    // MARK: - Export/Import Functions

    private func exportMarkers(to url: URL) {
        do {
            if url.pathExtension.lowercased() == "csv" {
                // Export as CSV
                let csvContent = generateCSVExport()
                try csvContent.write(to: url, atomically: true, encoding: .utf8)
            } else {
                // Export as JSON (existing functionality)
                try metadataManager.exportMetadata(to: url)
            }

            // Show success message (could be enhanced with user notification)
            print("Markers exported successfully to \(url.lastPathComponent)")
        } catch {
            print("Failed to export markers: \(error)")
        }
    }

    private func importMarkers(from url: URL) {
        do {
            _ = try metadataManager.importMetadata(from: url)

            // Reload markers in the UI
            if let metadata = metadataManager.currentMetadata {
                markerViewModel.loadMarkersFromMetadata(metadata)
            }

            print("Markers imported successfully from \(url.lastPathComponent)")
        } catch {
            print("Failed to import markers: \(error)")
        }
    }

    private func generateCSVExport() -> String {
        let markers = markerViewModel.markers
        let videoFileName = viewModel.video?.url.lastPathComponent ?? "Unknown"
        let frameRate = viewModel.video?.frameRate ?? 30.0

        var csv = "Video File,Marker ID,Timestamp (Seconds),Timestamp (HH:MM:SS:FF),Frame Number,Rating,Label,Created At\n"

        for marker in markers {
            let seconds = marker.timestamp.seconds
            let timeString = formatTimeAsTimecode(marker.timestamp, frameRate: frameRate)
            let frameNumber = Int(marker.timestamp.seconds * Double(frameRate))
            let rating = marker.rating
            let label = escapeCSVValue(marker.label)
            let createdAt = formatDate(marker.createdAt)

            csv += "\(escapeCSVValue(videoFileName)),\(marker.id),\(seconds),\(timeString),\(frameNumber),\(rating),\(label),\(createdAt)\n"
        }

        return csv
    }

    private func formatTimeAsTimecode(_ time: CMTime, frameRate: Float) -> String {
        let totalSeconds = Int(time.seconds)
        let hours = totalSeconds / 3600
        let minutes = (totalSeconds % 3600) / 60
        let seconds = totalSeconds % 60
        let frames = Int((time.seconds - Double(totalSeconds)) * Double(frameRate))

        return String(format: "%02d:%02d:%02d:%02d", hours, minutes, seconds, frames)
    }

    private func escapeCSVValue(_ value: String) -> String {
        if value.contains(",") || value.contains("\"") || value.contains("\n") {
            return "\"" + value.replacingOccurrences(of: "\"", with: "\"\"") + "\""
        }
        return value
    }

    private func formatDate(_ date: Date) -> String {
        let formatter = DateFormatter()
        formatter.dateStyle = .short
        formatter.timeStyle = .short
        return formatter.string(from: date)
    }
}

