import SwiftUI
import AVKit

struct ContentView: View {
    @StateObject private var viewModel = PlayerViewModel()
    @StateObject private var markerViewModel = MarkerViewModel()
    @StateObject private var navigationViewModel = NavigationViewModel()
    @StateObject private var keyboardManager = KeyboardShortcutManager()
    @StateObject private var modalViewModel = ModalViewModel()
    @StateObject private var metadataManager = MetadataManager()
    @State private var showingFileImporter = false

    var body: some View {
        ZStack {
            // Main content
            VStack(spacing: 0) {
            if viewModel.player != nil {
                ZStack {
                    VideoPlayerView(player: viewModel.player)
                        .background(Color.black)

                    VStack {
                        HStack {
                            Spacer()

                            VStack(alignment: .trailing, spacing: 4) {
                                Text(viewModel.currentTimeFormatted)
                                    .font(.system(.headline, design: .monospaced))
                                    .foregroundColor(.white)
                                Text("Frame \(viewModel.currentFrame)")
                                    .font(.system(.caption, design: .monospaced))
                                    .foregroundColor(.white.opacity(0.8))
                            }
                            .padding(8)
                            .background(Color.black.opacity(0.7))
                            .cornerRadius(6)
                            .padding()
                        }

                        Spacer()
                    }
                }

                VStack(spacing: 12) {
                    ProgressBarView(viewModel: viewModel, markerViewModel: markerViewModel, modalViewModel: modalViewModel)
                        .padding(.horizontal)

                    HStack {
                        TimeDisplay(playerViewModel: viewModel, navigationViewModel: navigationViewModel)

                        Spacer()

                        HStack {
                            Text(viewModel.currentTimeFormatted)
                                .font(.system(.caption, design: .monospaced))

                            Text("/")
                                .foregroundColor(.secondary)

                            Text(viewModel.durationFormatted)
                                .font(.system(.caption, design: .monospaced))
                        }
                        .foregroundColor(.secondary)
                    }
                    .padding(.horizontal)
                }
                .padding(.vertical, 12)
                .background(Color(NSColor.controlBackgroundColor))
            } else {
                VStack(spacing: 20) {
                    Image(systemName: "play.rectangle")
                        .font(.system(size: 64))
                        .foregroundColor(.secondary)

                    Text("Open a video file to begin")
                        .font(.headline)
                        .foregroundColor(.secondary)

                    Button("Open Video...") {
                        showingFileImporter = true
                    }
                    .buttonStyle(.borderedProminent)
                }
                .frame(maxWidth: .infinity, maxHeight: .infinity)
                .background(Color(NSColor.windowBackgroundColor))
            }
            }
            .frame(minWidth: 800, minHeight: 600)
            .focusable()
            .defaultFocus()
            .onKeyPress(.space) {
                // Only handle space if no modifiers are pressed
                viewModel.togglePlayPause()
                return .handled
            }
        .fileImporter(
            isPresented: $showingFileImporter,
            allowedContentTypes: [.movie, .mpeg4Movie, .quickTimeMovie],
            allowsMultipleSelection: false
        ) { result in
            switch result {
            case .success(let urls):
                if let url = urls.first {
                    viewModel.loadVideo(url: url)
                }
            case .failure(let error):
                viewModel.error = error.localizedDescription
            }
        }
        .alert("Error", isPresented: .constant(viewModel.error != nil)) {
            Button("OK") {
                viewModel.error = nil
            }
        } message: {
            Text(viewModel.error ?? "")
        }
        .onReceive(NotificationCenter.default.publisher(for: .openVideoFile)) { notification in
            if let url = notification.object as? URL {
                viewModel.loadVideo(url: url)
            }
        }
        .onReceive(NotificationCenter.default.publisher(for: .showKeyboardShortcuts)) { _ in
            modalViewModel.presentShortcuts()
        }
        .onAppear {
            // Connect the ViewModels
            markerViewModel.setPlayerViewModel(viewModel)
            markerViewModel.setMetadataManager(metadataManager)
            viewModel.setMetadataManager(metadataManager)
            viewModel.setMarkerViewModel(markerViewModel)
            keyboardManager.setup(with: markerViewModel, navigationViewModel: navigationViewModel)
        }
        .onChange(of: viewModel.player) { _, newPlayer in
            if let player = newPlayer {
                navigationViewModel.setup(player: player, playerViewModel: viewModel)
            }
        }
        .onChange(of: modalViewModel.activeModal) { _, newModal in
            // Disable keyboard shortcuts when modal is open
            keyboardManager.setEnabled(newModal == nil)
        }

        // Modal overlay
        if let modal = modalViewModel.activeModal {
            switch modal {
            case .markerDetail(let marker):
                MarkerDetailModal(
                    marker: marker,
                    markerViewModel: markerViewModel,
                    isPresented: .constant(true),
                    onDismiss: { modalViewModel.dismissModal() }
                )
            case .shortcuts:
                ShortcutsModal(
                    isPresented: .constant(true),
                    onDismiss: { modalViewModel.dismissModal() }
                )
            }
        }
        }
    }
}

