import Foundation
import AVFoundation
import Combine
import CryptoKit

class MetadataManager: ObservableObject {
    @Published var currentMetadata: VideoMetadata?
    @Published var metadataDirectory: URL?

    private let encoder = JSONEncoder()
    private let decoder = JSONDecoder()
    private let fileManager = FileManager.default

    // UserDefaults key for metadata directory preference
    private let metadataDirectoryKey = "MetadataDirectory"

    init() {
        setupEncoder()
        loadMetadataDirectory()
    }

    private func setupEncoder() {
        encoder.outputFormatting = [.prettyPrinted, .sortedKeys]
        encoder.dateEncodingStrategy = .iso8601
        decoder.dateDecodingStrategy = .iso8601
    }

    private func loadMetadataDirectory() {
        if let directoryData = UserDefaults.standard.data(forKey: metadataDirectoryKey),
           let url = try? NSKeyedUnarchiver.unarchivedObject(ofClass: NSURL.self, from: directoryData) as URL? {
            metadataDirectory = url
        }
    }

    func setMetadataDirectory(_ url: URL) {
        metadataDirectory = url

        // Save to UserDefaults
        if let data = try? NSKeyedArchiver.archivedData(withRootObject: url, requiringSecureCoding: true) {
            UserDefaults.standard.set(data, forKey: metadataDirectoryKey)
        }
    }

    // MARK: - Auto Import/Export

    func loadMetadataForVideo(at videoURL: URL) -> VideoMetadata? {
        let metadataURL = metadataURL(for: videoURL)

        guard fileManager.fileExists(atPath: metadataURL.path) else {
            return nil
        }

        do {
            let data = try Data(contentsOf: metadataURL)
            let metadata = try decoder.decode(VideoMetadata.self, from: data)

            // Validate that this metadata is for the correct video
            if metadata.videoFileName != videoURL.lastPathComponent {
                print("Warning: Metadata filename mismatch")
                return nil
            }

            currentMetadata = metadata
            return metadata
        } catch {
            print("Failed to load metadata: \(error)")
            return nil
        }
    }

    func saveCurrentMetadata() {
        guard var metadata = currentMetadata else { return }

        metadata.touch()

        do {
            try saveMetadata(metadata)
        } catch {
            print("Failed to save metadata: \(error)")
        }
    }

    func createMetadataForVideo(at videoURL: URL, frameRate: Float?, duration: CMTime?) -> VideoMetadata {
        let checksum = calculateChecksum(for: videoURL)
        var metadata = VideoMetadata(videoFileName: videoURL.lastPathComponent, videoChecksum: checksum)

        metadata.videoFrameRate = frameRate
        metadata.videoDuration = duration

        currentMetadata = metadata
        saveCurrentMetadata()

        return metadata
    }

    private func saveMetadata(_ metadata: VideoMetadata) throws {
        let videoURL = URL(fileURLWithPath: metadata.videoFileName)
        let metadataURL = metadataURL(for: videoURL)

        // Ensure the directory exists
        let directory = metadataURL.deletingLastPathComponent()
        if !fileManager.fileExists(atPath: directory.path) {
            try fileManager.createDirectory(at: directory, withIntermediateDirectories: true)
        }

        let data = try encoder.encode(metadata)
        try data.write(to: metadataURL)
    }

    private func metadataURL(for videoURL: URL) -> URL {
        let fileName = videoURL.deletingPathExtension().lastPathComponent + ".json"

        if let metadataDirectory = metadataDirectory {
            return metadataDirectory.appendingPathComponent(fileName)
        } else {
            // Default to same directory as video
            return videoURL.deletingLastPathComponent().appendingPathComponent(fileName)
        }
    }

    private func calculateChecksum(for url: URL) -> String? {
        guard let data = try? Data(contentsOf: url, options: .mappedIfSafe) else {
            return nil
        }

        // Use SHA256 hash for file verification
        let hash = SHA256.hash(data: data)
        return hash.compactMap { String(format: "%02x", $0) }.joined()
    }

    // MARK: - Metadata Updates

    func updateMarkers(_ markers: [Marker]) {
        guard currentMetadata != nil else { return }
        currentMetadata?.markers = markers
        saveCurrentMetadata()
    }

    func updateScrubConfiguration(_ config: ScrubConfiguration) {
        guard currentMetadata != nil else { return }
        currentMetadata?.scrubConfig = config
        saveCurrentMetadata()
    }

    func updateShortcuts(_ shortcuts: [String: String]) {
        guard currentMetadata != nil else { return }
        currentMetadata?.shortcuts = shortcuts
        saveCurrentMetadata()
    }
}

// MARK: - File Management
extension MetadataManager {
    func metadataExists(for videoURL: URL) -> Bool {
        let metadataURL = metadataURL(for: videoURL)
        return fileManager.fileExists(atPath: metadataURL.path)
    }

    func deleteMetadata(for videoURL: URL) throws {
        let metadataURL = metadataURL(for: videoURL)
        if fileManager.fileExists(atPath: metadataURL.path) {
            try fileManager.removeItem(at: metadataURL)
        }
    }

    func exportMetadata(to url: URL) throws {
        guard let metadata = currentMetadata else {
            throw MetadataError.noMetadata
        }

        let data = try encoder.encode(metadata)
        try data.write(to: url)
    }

    func importMetadata(from url: URL) throws -> VideoMetadata {
        let data = try Data(contentsOf: url)
        let metadata = try decoder.decode(VideoMetadata.self, from: data)

        currentMetadata = metadata
        saveCurrentMetadata()

        return metadata
    }
}

// MARK: - Error Types
enum MetadataError: LocalizedError {
    case noMetadata
    case fileNotFound
    case corruptedFile
    case versionMismatch
    case writePermissionDenied

    var errorDescription: String? {
        switch self {
        case .noMetadata:
            return "No metadata available to save"
        case .fileNotFound:
            return "Metadata file not found"
        case .corruptedFile:
            return "Metadata file is corrupted or invalid"
        case .versionMismatch:
            return "Metadata file was created with a newer version"
        case .writePermissionDenied:
            return "Permission denied when trying to save metadata"
        }
    }
}

import Foundation
import AVFoundation

struct VideoMetadata: Codable {
    let videoFileName: String
    let videoChecksum: String?
    var markers: [Marker]
    var scrubConfig: ScrubConfiguration
    var shortcuts: [String: String]
    var lastModified: Date
    var version: String = "1.0"

    // Video validation metadata
    var videoFrameRate: Float?
    var videoDuration: CMTime?

    init(videoFileName: String, videoChecksum: String? = nil) {
        self.videoFileName = videoFileName
        self.videoChecksum = videoChecksum
        self.markers = []
        self.scrubConfig = ScrubConfiguration()
        self.shortcuts = [:]
        self.lastModified = Date()
    }

    // Update last modified timestamp
    mutating func touch() {
        lastModified = Date()
    }
}

// MARK: - CMTime Codable Support
extension VideoMetadata {
    enum CodingKeys: String, CodingKey {
        case videoFileName, videoChecksum, markers, scrubConfig, shortcuts, lastModified, version, videoFrameRate, videoDurationValue, videoDurationTimescale
    }

    init(from decoder: Decoder) throws {
        let container = try decoder.container(keyedBy: CodingKeys.self)

        videoFileName = try container.decode(String.self, forKey: .videoFileName)
        videoChecksum = try container.decodeIfPresent(String.self, forKey: .videoChecksum)
        markers = try container.decode([Marker].self, forKey: .markers)
        scrubConfig = try container.decode(ScrubConfiguration.self, forKey: .scrubConfig)
        shortcuts = try container.decode([String: String].self, forKey: .shortcuts)
        lastModified = try container.decode(Date.self, forKey: .lastModified)
        version = try container.decodeIfPresent(String.self, forKey: .version) ?? "1.0"
        videoFrameRate = try container.decodeIfPresent(Float.self, forKey: .videoFrameRate)

        // Decode CMTime
        if let durationValue = try container.decodeIfPresent(Int64.self, forKey: .videoDurationValue),
           let durationTimescale = try container.decodeIfPresent(Int32.self, forKey: .videoDurationTimescale) {
            videoDuration = CMTime(value: durationValue, timescale: durationTimescale)
        } else {
            videoDuration = nil
        }
    }

    func encode(to encoder: Encoder) throws {
        var container = encoder.container(keyedBy: CodingKeys.self)

        try container.encode(videoFileName, forKey: .videoFileName)
        try container.encodeIfPresent(videoChecksum, forKey: .videoChecksum)
        try container.encode(markers, forKey: .markers)
        try container.encode(scrubConfig, forKey: .scrubConfig)
        try container.encode(shortcuts, forKey: .shortcuts)
        try container.encode(lastModified, forKey: .lastModified)
        try container.encode(version, forKey: .version)
        try container.encodeIfPresent(videoFrameRate, forKey: .videoFrameRate)

        // Encode CMTime
        if let duration = videoDuration {
            try container.encode(duration.value, forKey: .videoDurationValue)
            try container.encode(duration.timescale, forKey: .videoDurationTimescale)
        }
    }
}

