    25‚Üí    private static let userDefaultsKey = "ShortcutConfiguration"
    26‚Üí    
    27‚Üí    // Save to UserDefaults
    28‚Üí    func save() {
    29‚Üí        do {
    30‚Üí            let data = try JSONEncoder().encode(self)
    31‚Üí            UserDefaults.standard.set(data, forKey: Self.userDefaultsKey)
