import Foundation
import AVFoundation
import AppKit

struct Marker: Identifiable, Codable {
    let id = UUID()
    var timestamp: CMTime
    var frameNumber: Int
    var rating: Int = 3  // 1-5 stars
    var label: String = ""
    var createdAt: Date = Date()

    init(timestamp: CMTime, frameNumber: Int) {
        self.timestamp = timestamp
        self.frameNumber = frameNumber
    }

    // Custom coding to handle CMTime
    enum CodingKeys: String, CodingKey {
        case id, frameNumber, rating, label, createdAt
        case timeValue, timeScale
    }

    init(from decoder: Decoder) throws {
        let container = try decoder.container(keyedBy: CodingKeys.self)
        let timeValue = try container.decode(CMTimeValue.self, forKey: .timeValue)
        let timeScale = try container.decode(CMTimeScale.self, forKey: .timeScale)

        self.timestamp = CMTime(value: timeValue, timescale: timeScale)
        self.frameNumber = try container.decode(Int.self, forKey: .frameNumber)
        self.rating = try container.decode(Int.self, forKey: .rating)
        self.label = try container.decode(String.self, forKey: .label)
        self.createdAt = try container.decode(Date.self, forKey: .createdAt)
    }

    func encode(to encoder: Encoder) throws {
        var container = encoder.container(keyedBy: CodingKeys.self)
        try container.encode(timestamp.value, forKey: .timeValue)
        try container.encode(timestamp.timescale, forKey: .timeScale)
        try container.encode(frameNumber, forKey: .frameNumber)
        try container.encode(rating, forKey: .rating)
        try container.encode(label, forKey: .label)
        try container.encode(createdAt, forKey: .createdAt)
    }
}

// MARK: - Convenience
extension Marker {
    var ratingColor: NSColor {
        switch rating {
        case 1: return .systemRed
        case 2: return .systemOrange
        case 3: return .systemYellow
        case 4: return .systemBlue
        case 5: return .systemGreen
        default: return .systemYellow
        }
    }

    var timeString: String {
        let seconds = CMTimeGetSeconds(timestamp)
        let minutes = Int(seconds / 60)
        let remainingSeconds = Int(seconds.truncatingRemainder(dividingBy: 60))
        let milliseconds = Int((seconds.truncatingRemainder(dividingBy: 1)) * 1000)
        return String(format: "%02d:%02d.%03d", minutes, remainingSeconds, milliseconds)
    }
}

