struct ScrubConfiguration: Codable, Equatable {
    var backwardKey: UInt16 = 123 // Left Arrow
    var forwardKey: UInt16 = 124 // Right Arrow
    var backwardModifiers: EventModifiers = []
    var forwardModifiers: EventModifiers = []
    var secondsWhenPlaying: Double = 5.0
    var framesWhenPaused: Int = 1

    // Default configuration
    static let `default` = ScrubConfiguration()

    // Common frame rates for video content
    static let supportedFrameRates: [Float] = [23.976, 24.0, 25.0, 29.97, 30.0, 60.0]
}

extension EventModifiers: Codable {
    private enum CodingKeys: String, CodingKey {
        case rawValue
    }

    public init(from decoder: Decoder) throws {
        let container = try decoder.container(keyedBy: CodingKeys.self)
        let rawValue = try container.decode(Int.self, forKey: .rawValue)
        self.init(rawValue: rawValue)
    }

    public func encode(to encoder: Encoder) throws {
        var container = encoder.container(keyedBy: CodingKeys.self)
        try container.encode(rawValue, forKey: .rawValue)
    }
}

