                        .onChange(of: configuration.nextMarkerModifiers) { _, _ in hasChanges = true }

                        ShortcutField(
                            title: "Previous Marker",
                            keyCode: $configuration.prevMarkerKey,
                            modifiers: $configuration.prevMarkerModifiers,
                            onConflict: { checkForConflict($0, excluding: "Previous Marker") }
                        )
                        .onChange(of: configuration.prevMarkerKey) { _, _ in hasChanges = true }
                        .onChange(of: configuration.prevMarkerModifiers) { _, _ in hasChanges = true }

                        ShortcutField(
                            title: "Delete Marker",
                            keyCode: $configuration.deleteMarkerKey,
                            modifiers: $configuration.deleteMarkerModifiers,
                            onConflict: { checkForConflict($0, excluding: "Delete Marker") }
                        )
                        .onChange(of: configuration.deleteMarkerKey) { _, _ in hasChanges = true }
                        .onChange(of: configuration.deleteMarkerModifiers) { _, _ in hasChanges = true }
                    }
                }

                Spacer()

                // Action buttons
                HStack(spacing: 12) {
                    Button("Reset to Defaults") {
                        resetGeneralDefaults()
                    }
                    .buttonStyle(.borderless)

                    Spacer()

                    Button("Cancel") {
                        cancelChanges()
                    }
                    .buttonStyle(.borderless)
                    .keyboardShortcut(.escape)

                    Button("Save") {
                        saveChanges()
                    }
                    .buttonStyle(.borderedProminent)
                    .keyboardShortcut(.return)
                    .disabled(!hasChanges)
                }
            }
            .padding(.bottom, 20)
        }
        .alert("Shortcut Conflict", isPresented: .constant(conflictAlert != nil)) {
            Button("OK") {
                conflictAlert = nil
            }
        } message: {
            Text(conflictAlert ?? "")
        }
    }

    @ViewBuilder
    private var scrubConfigurationTab: some View {
        ScrubConfigurationTable(
            configurations: $configuration.scrubConfigurations,
            onSave: saveScrubConfigurations
        )
    }

    @ViewBuilder
    private func shortcutSection<Content: View>(
        title: String,
        @ViewBuilder content: () -> Content
    ) -> some View {
        VStack(alignment: .leading, spacing: 12) {
            Text(title)
                .font(.headline)
                .foregroundColor(.primary)

            content()
        }
        .padding(.horizontal, 16)
        .padding(.vertical, 12)
        .background(Color(NSColor.controlBackgroundColor).opacity(0.5))
        .cornerRadius(8)
    }

    @ViewBuilder
    private func shortcutRow(
        title: String,
        description: String,
        isEditable: Bool
    ) -> some View {
        HStack {
            Text(title)
                .frame(maxWidth: .infinity, alignment: .leading)

            Text(description)
                .font(.system(.body, design: .monospaced))
                .foregroundColor(.secondary)
                .padding(.horizontal, 12)
                .padding(.vertical, 6)
                .background(Color(NSColor.controlBackgroundColor))
                .cornerRadius(4)
        }
    }

    private func resetGeneralDefaults() {
        configuration = ShortcutConfiguration.default
        hasChanges = true
    }

    private func saveScrubConfigurations() {
        configuration.save()
        hasChanges = false
    }

    private func saveChanges() {
        configuration.save()
        hasChanges = false
        dismissModal()
    }

    private func cancelChanges() {
        // Reset to original values if needed
        hasChanges = false
        dismissModal()
    }

    private func dismissModal() {
        isPresented = false
        onDismiss()

        // Notify the keyboard shortcut manager to reload configuration
        NotificationCenter.default.post(name: .shortcutConfigurationUpdated, object: nil)
    }

    private func checkForConflict(_ shortcut: String, excluding: String) {
        // Check general shortcuts
        let shortcuts = [
            ("Add Marker", configuration.addMarkerKey, configuration.addMarkerModifiers),
            ("Next Marker", configuration.nextMarkerKey, configuration.nextMarkerModifiers),
            ("Previous Marker", configuration.prevMarkerKey, configuration.prevMarkerModifiers),
            ("Delete Marker", configuration.deleteMarkerKey, configuration.deleteMarkerModifiers)
        ]

        for (name, _, _) in shortcuts where name != excluding {
            if configuration.matchesShortcut(keyCode: configuration.addMarkerKey, modifiers: configuration.addMarkerModifiers) == name {
                conflictAlert = "This shortcut is already assigned to \(name)"
                return
            }
        }

        // Check scrub configurations
        if let scrubMatch = configuration.matchesShortcut(keyCode: configuration.addMarkerKey, modifiers: configuration.addMarkerModifiers),
           scrubMatch.contains("Scrub") {
            conflictAlert = "This shortcut is already assigned to \(scrubMatch)"
        }
    }
}

