struct BaseModal<Content: View>: View {
    @Binding var isPresented: Bool
    let title: String
    let content: Content
    let onDismiss: (() -> Void)?

    init(
        isPresented: Binding<Bool>,
        title: String,
        onDismiss: (() -> Void)? = nil,
        @ViewBuilder content: () -> Content
    ) {
        self._isPresented = isPresented
        self.title = title
        self.onDismiss = onDismiss
        self.content = content()
    }

    var body: some View {
        ZStack {
            // Dimmed background
            Color.black.opacity(0.3)
                .ignoresSafeArea()
                .onTapGesture {
                    dismissModal()
                }

            // Modal container
                VStack(spacing: 0) {
                    // Title bar
                    HStack {
                        Text(title)
                            .font(.headline)
                            .fontWeight(.semibold)

                        Spacer()

