import Foundation
import SwiftUI

extension Notification.Name {
    static let shortcutConfigurationUpdated = Notification.Name("shortcutConfigurationUpdated")
}

struct ShortcutConfiguration: Codable, Equatable {
    // General shortcuts
    var addMarkerKey: UInt16 = 46 // M key
    var addMarkerModifiers: EventModifiers = .command
    var nextMarkerKey: UInt16 = 124 // Right arrow key
    var nextMarkerModifiers: EventModifiers = []
    var prevMarkerKey: UInt16 = 123 // Left arrow key  
    var prevMarkerModifiers: EventModifiers = []
    var deleteMarkerKey: UInt16 = 51 // Delete key
    var deleteMarkerModifiers: EventModifiers = []

    // Scrub configurations
    var scrubConfigurations: [ScrubConfigurationRow] = [ScrubConfigurationRow.default]

    static let `default` = ShortcutConfiguration()

    // UserDefaults key
    private static let userDefaultsKey = "ShortcutConfiguration"

    // Save to UserDefaults
    func save() {
        do {
            let data = try JSONEncoder().encode(self)
            UserDefaults.standard.set(data, forKey: Self.userDefaultsKey)
            print("Shortcut configuration saved successfully")
        } catch {
            print("Failed to save shortcut configuration: \(error)")
        }
    }

    // Load from UserDefaults
    static func load() -> ShortcutConfiguration {
        guard let data = UserDefaults.standard.data(forKey: userDefaultsKey) else {
            print("No saved shortcut configuration found, using defaults")
            return .default
        }

        do {
            let configuration = try JSONDecoder().decode(ShortcutConfiguration.self, from: data)
            print("Shortcut configuration loaded successfully")
            return configuration
        } catch {
            print("Failed to load shortcut configuration: \(error), using defaults")
            return .default
        }
    }

    // Reset to defaults
    static func resetToDefaults() {
        UserDefaults.standard.removeObject(forKey: userDefaultsKey)
        print("Shortcut configuration reset to defaults")
    }
}

// MARK: - Convenience Methods
extension ShortcutConfiguration {

    // Check if a key combination matches any configured shortcut
    func matchesShortcut(keyCode: UInt16, modifiers: EventModifiers) -> String? {
        if keyCode == addMarkerKey && modifiers == addMarkerModifiers {
            return "Add Marker"
        }
        if keyCode == nextMarkerKey && modifiers == nextMarkerModifiers {
            return "Next Marker"
        }
        if keyCode == prevMarkerKey && modifiers == prevMarkerModifiers {
            return "Previous Marker"
        }
        if keyCode == deleteMarkerKey && modifiers == deleteMarkerModifiers {
            return "Delete Marker"
        }

        // Check scrub configurations
        for (index, config) in scrubConfigurations.enumerated() {
            if keyCode == config.forwardKey && modifiers == config.forwardModifiers {
                return "Scrub Forward #\(index + 1)"
            }
            if keyCode == config.backwardKey && modifiers == config.backwardModifiers {
                return "Scrub Backward #\(index + 1)"
            }
        }

        return nil
    }

    // Get all configured shortcuts as display strings
    var allShortcuts: [(name: String, combination: String)] {
        var shortcuts: [(String, String)] = []

        shortcuts.append(("Add Marker", shortcutString(keyCode: addMarkerKey, modifiers: addMarkerModifiers)))
        shortcuts.append(("Next Marker", shortcutString(keyCode: nextMarkerKey, modifiers: nextMarkerModifiers)))
        shortcuts.append(("Previous Marker", shortcutString(keyCode: prevMarkerKey, modifiers: prevMarkerModifiers)))
        shortcuts.append(("Delete Marker", shortcutString(keyCode: deleteMarkerKey, modifiers: deleteMarkerModifiers)))

        for (index, config) in scrubConfigurations.enumerated() {
            shortcuts.append(("Scrub Forward #\(index + 1)", shortcutString(keyCode: config.forwardKey, modifiers: config.forwardModifiers)))
            shortcuts.append(("Scrub Backward #\(index + 1)", shortcutString(keyCode: config.backwardKey, modifiers: config.backwardModifiers)))
        }

        return shortcuts
    }

    private func shortcutString(keyCode: UInt16, modifiers: EventModifiers) -> String {
        var parts: [String] = []

        if modifiers.contains(.control) { parts.append("‚åÉ") }
        if modifiers.contains(.option) { parts.append("‚å•") }
        if modifiers.contains(.shift) { parts.append("‚áß") }
        if modifiers.contains(.command) { parts.append("‚åò") }

        parts.append(keyCodeToString(keyCode))
        return parts.joined()
    }

    private func keyCodeToString(_ keyCode: UInt16) -> String {
        switch keyCode {
        case 123: return "‚Üê"
        case 124: return "‚Üí"
        case 125: return "‚Üì"
        case 126: return "‚Üë"
        case 49: return "Space"
        case 36: return "Return"
        case 53: return "Esc"
        case 48: return "Tab"
        case 51: return "Delete"
        case 46: return "M"
        default: return "Key \(keyCode)"
        }
    }
}

// MARK: - ScrubConfigurationRow Codable Extension
extension ScrubConfigurationRow: Codable {
    enum CodingKeys: String, CodingKey {
        case id, forwardKey, forwardModifiers, backwardKey, backwardModifiers, secondsWhenPlaying, framesWhenPaused
    }

    init(from decoder: Decoder) throws {
        let container = try decoder.container(keyedBy: CodingKeys.self)

        forwardKey = try container.decode(UInt16.self, forKey: .forwardKey)
        forwardModifiers = try container.decode(EventModifiers.self, forKey: .forwardModifiers)
        backwardKey = try container.decode(UInt16.self, forKey: .backwardKey)
        backwardModifiers = try container.decode(EventModifiers.self, forKey: .backwardModifiers)
        secondsWhenPlaying = try container.decode(Double.self, forKey: .secondsWhenPlaying)
        framesWhenPaused = try container.decode(Int.self, forKey: .framesWhenPaused)
    }

    func encode(to encoder: Encoder) throws {
        var container = encoder.container(keyedBy: CodingKeys.self)

        try container.encode(forwardKey, forKey: .forwardKey)
        try container.encode(forwardModifiers, forKey: .forwardModifiers)
        try container.encode(backwardKey, forKey: .backwardKey)
        try container.encode(backwardModifiers, forKey: .backwardModifiers)
        try container.encode(secondsWhenPlaying, forKey: .secondsWhenPlaying)
        try container.encode(framesWhenPaused, forKey: .framesWhenPaused)
    }
}

