    static func frameNumber(from time: CMTime, frameRate: Float) -> Int {
        guard time.isValid && !time.isIndefinite && frameRate > 0 else { return 0 }
        let seconds = CMTimeGetSeconds(time)
        return Int(seconds * Double(frameRate))
