        guard frameRate > 0 else { return .zero }
        let seconds = Double(frameNumber) / Double(frameRate)
        return CMTime(seconds: seconds, preferredTimescale: Int32(frameRate * 100))
    }

    static func formatTime(_ time: CMTime) -> String {
        guard time.isValid && !time.isIndefinite else { return "00:00:00" }

        let totalSeconds = Int(CMTimeGetSeconds(time))
        let hours = totalSeconds / 3600
        let minutes = (totalSeconds % 3600) / 60
        let seconds = totalSeconds % 60

        return String(format: "%02d:%02d:%02d", hours, minutes, seconds)
    }
}

