import Foundation
import AVFoundation

struct FramePrecision {

    // MARK: - Frame Rate Detection

    /// Detect the frame rate from a video asset
    static func detectFrameRate(from asset: AVAsset) -> Float {
        let videoTracks = asset.tracks(withMediaType: .video)
        guard let track = videoTracks.first else { return 30.0 }

        let nominalFrameRate = track.nominalFrameRate

        // Handle common frame rates with precision
        if abs(nominalFrameRate - 23.976) < 0.01 { return 23.976 }
        if abs(nominalFrameRate - 24.0) < 0.01 { return 24.0 }
        if abs(nominalFrameRate - 25.0) < 0.01 { return 25.0 }
        if abs(nominalFrameRate - 29.97) < 0.01 { return 29.97 }
        if abs(nominalFrameRate - 30.0) < 0.01 { return 30.0 }
        if abs(nominalFrameRate - 60.0) < 0.01 { return 60.0 }

        return nominalFrameRate
    }

    // MARK: - Frame-Accurate Conversions

    /// Convert time to frame number with high precision
    static func frameNumber(from time: CMTime, frameRate: Float) -> Int {
        guard time.isValid && !time.isIndefinite && frameRate > 0 else { return 0 }

        let seconds = CMTimeGetSeconds(time)
        let frameNumber = seconds * Double(frameRate)

        // Round to nearest frame to handle floating point precision
        return Int(frameNumber.rounded())
    }

    /// Convert frame number to precise CMTime
    static func time(from frameNumber: Int, frameRate: Float) -> CMTime {
        guard frameRate > 0 else { return .zero }

        let seconds = Double(frameNumber) / Double(frameRate)
        let timescale = Int32(frameRate * 1000) // High timescale for precision

        return CMTime(seconds: seconds, preferredTimescale: timescale)
    }

    /// Get total frame count for a duration
    static func totalFrames(for duration: CMTime, frameRate: Float) -> Int {
        guard duration.isValid && !duration.isIndefinite && frameRate > 0 else { return 0 }

        let seconds = CMTimeGetSeconds(duration)
        return Int((seconds * Double(frameRate)).rounded())
    }

    // MARK: - Frame-Accurate Seeking

    /// Seek to a specific frame with zero tolerance
    static func seekToFrame(_ frameNumber: Int, frameRate: Float, player: AVPlayer, completion: @escaping (Bool) -> Void = { _ in }) {
        let targetTime = time(from: frameNumber, frameRate: frameRate)

        player.seek(to: targetTime, toleranceBefore: .zero, toleranceAfter: .zero) { finished in
            completion(finished)
        }
    }

    /// Seek by frame offset (positive or negative)
    static func seekByFrames(_ frameOffset: Int, frameRate: Float, player: AVPlayer, completion: @escaping (Bool) -> Void = { _ in }) {
        let currentTime = player.currentTime()
        let currentFrame = frameNumber(from: currentTime, frameRate: frameRate)
        let targetFrame = max(0, currentFrame + frameOffset)

        seekToFrame(targetFrame, frameRate: frameRate, player: player, completion: completion)
    }

    // MARK: - Time Formatting

    /// Format time with frame precision (HH:MM:SS:FF)
    static func formatTimeWithFrames(_ time: CMTime, frameRate: Float) -> String {
        guard time.isValid && !time.isIndefinite else { return "00:00:00:00" }

        let totalSeconds = CMTimeGetSeconds(time)
        let hours = Int(totalSeconds) / 3600
        let minutes = (Int(totalSeconds) % 3600) / 60
        let seconds = Int(totalSeconds) % 60

        // Calculate frame component
        let frameInSecond = totalSeconds.truncatingRemainder(dividingBy: 1.0)
        let frame = Int((frameInSecond * Double(frameRate)).rounded())

        return String(format: "%02d:%02d:%02d:%02d", hours, minutes, seconds, frame)
    }

    /// Format frame rate for display
    static func formatFrameRate(_ frameRate: Float) -> String {
        // Show common frame rates with proper precision
        if abs(frameRate - 23.976) < 0.01 { return "23.976 fps" }
        if abs(frameRate - 29.97) < 0.01 { return "29.97 fps" }

        // Show integer frame rates without decimal
        if frameRate == Float(Int(frameRate)) {
            return "\(Int(frameRate)) fps"
        }

        return String(format: "%.2f fps", frameRate)
    }
}

// MARK: - AVPlayer Extensions

extension AVPlayer {

    /// Get current frame number
    func currentFrameNumber(frameRate: Float) -> Int {
        return FramePrecision.frameNumber(from: currentTime(), frameRate: frameRate)
    }

    /// Seek to specific frame
    func seek(toFrame frameNumber: Int, frameRate: Float, completion: @escaping (Bool) -> Void = { _ in }) {
        FramePrecision.seekToFrame(frameNumber, frameRate: frameRate, player: self, completion: completion)
    }

    /// Seek by frame offset
    func seek(byFrames frameOffset: Int, frameRate: Float, completion: @escaping (Bool) -> Void = { _ in }) {
        FramePrecision.seekByFrames(frameOffset, frameRate: frameRate, player: self, completion: completion)
    }
}

