        let currentTime = player.currentTime()
        let currentFrame = frameNumber(from: currentTime, frameRate: frameRate)
        let targetFrame = max(0, currentFrame + frameOffset)

        seekToFrame(targetFrame, frameRate: frameRate, player: player, completion: completion)
    }

    // MARK: - Time Formatting

    /// Format time with frame precision (HH:MM:SS:FF)
    static func formatTimeWithFrames(_ time: CMTime, frameRate: Float) -> String {
        guard time.isValid && !time.isIndefinite else { return "00:00:00:00" }

        let totalSeconds = CMTimeGetSeconds(time)
        let hours = Int(totalSeconds) / 3600
        let minutes = (Int(totalSeconds) % 3600) / 60
        let seconds = Int(totalSeconds) % 60

        // Calculate frame component
        let frameInSecond = totalSeconds.truncatingRemainder(dividingBy: 1.0)
        let frame = Int((frameInSecond * Double(frameRate)).rounded())

        return String(format: "%02d:%02d:%02d:%02d", hours, minutes, seconds, frame)
    }

    /// Format frame rate for display
    static func formatFrameRate(_ frameRate: Float) -> String {
        // Show common frame rates with proper precision
        if abs(frameRate - 23.976) < 0.01 { return "23.976 fps" }
        if abs(frameRate - 29.97) < 0.01 { return "29.97 fps" }

        // Show integer frame rates without decimal
        if frameRate == Float(Int(frameRate)) {
            return "\(Int(frameRate)) fps"
        }

        return String(format: "%.2f fps", frameRate)
    }
}

// MARK: - AVPlayer Extensions

extension AVPlayer {

    /// Get current frame number
    func currentFrameNumber(frameRate: Float) -> Int {
        return FramePrecision.frameNumber(from: currentTime(), frameRate: frameRate)
    }

    /// Seek to specific frame
    func seek(toFrame frameNumber: Int, frameRate: Float, completion: @escaping (Bool) -> Void = { _ in }) {
        FramePrecision.seekToFrame(frameNumber, frameRate: frameRate, player: self, completion: completion)
    }

    /// Seek by frame offset
    func seek(byFrames frameOffset: Int, frameRate: Float, completion: @escaping (Bool) -> Void = { _ in }) {
        FramePrecision.seekByFrames(frameOffset, frameRate: frameRate, player: self, completion: completion)
    }
}

