import SwiftUI
import AVFoundation

struct ProgressBarView: View {
    @ObservedObject var viewModel: PlayerViewModel
    @ObservedObject var markerViewModel: MarkerViewModel
    @ObservedObject var modalViewModel: ModalViewModel
    @State private var hoverPosition: CGFloat?
    @State private var isDragging = false

    private let barHeight: CGFloat = 8
    private let handleWidth: CGFloat = 4

    var body: some View {
        GeometryReader { geometry in
            ZStack(alignment: .leading) {
                // Background
                RoundedRectangle(cornerRadius: barHeight / 2)
                    .fill(Color.gray.opacity(0.3))
                    .frame(height: barHeight)

                // Segments (shaded regions between markers)
                ForEach(markerViewModel.segments) { segment in
                    let startPos = markerViewModel.markerPosition(
                        for: segment.startMarker,
                        in: geometry.size.width,
                        duration: viewModel.duration
                    )
                    let endPos = segment.endMarker.map { endMarker in
                        markerViewModel.markerPosition(
                            for: endMarker,
                            in: geometry.size.width,
                            duration: viewModel.duration
                        )
                    } ?? geometry.size.width

                    SegmentView(
                        segment: segment,
                        startPosition: startPos,
                        endPosition: endPos,
                        height: barHeight
                    )
                }

                // Progress fill
                RoundedRectangle(cornerRadius: barHeight / 2)
                    .fill(Color.accentColor)
                    .frame(width: max(0, geometry.size.width * viewModel.progress), height: barHeight)

                if let position = hoverPosition {
                    VStack(spacing: 4) {
                        Text(timeString(at: position / geometry.size.width))
                            .font(.caption)
                            .padding(.horizontal, 8)
                            .padding(.vertical, 4)
                            .background(Color.black.opacity(0.8))
                            .foregroundColor(.white)
                            .cornerRadius(4)

                        Rectangle()
                            .fill(Color.white.opacity(0.5))
                            .frame(width: 1, height: barHeight + 4)
                    }
                    .position(x: position, y: -10)
                }

                // Markers
                ForEach(markerViewModel.markers) { marker in
                    MarkerView(
                        marker: marker,
                        position: markerViewModel.markerPosition(
                            for: marker,
                            in: geometry.size.width,
                            duration: viewModel.duration
                        ),
                        isSelected: markerViewModel.selectedMarker?.id == marker.id,
                        modalViewModel: modalViewModel
                    ) {
                        markerViewModel.selectMarker(marker)
                        markerViewModel.navigateToMarker(marker)
                    }
                }

                // Progress handle
                RoundedRectangle(cornerRadius: handleWidth / 2)
                    .fill(Color.white)
                    .frame(width: handleWidth, height: barHeight + 6)
                    .position(x: geometry.size.width * viewModel.progress, y: barHeight / 2)
                    .shadow(radius: 2)
            }
            .frame(height: max(barHeight, 30)) // Increased height to accommodate markers
            .contentShape(Rectangle())
            .onHover { hovering in
                withAnimation(.easeInOut(duration: 0.1)) {
                    hoverPosition = hovering ? nil : nil
                }
            }
            .onContinuousHover { phase in
                switch phase {
                case .active(let location):
                    hoverPosition = location.x
                case .ended:
                    hoverPosition = nil
                }
            }
            .gesture(
                DragGesture(minimumDistance: 0)
                    .onChanged { value in
                        isDragging = true
                        let progress = max(0, min(1, value.location.x / geometry.size.width))
                        viewModel.seek(to: progress)
                    }
                    .onEnded { _ in
                        isDragging = false
                    }
            )
        }
        .frame(height: max(barHeight, 30)) // Increased height to accommodate markers
    }

    private func timeString(at progress: Double) -> String {
        guard viewModel.duration.isValid && !viewModel.duration.isIndefinite else { return "00:00:00" }
        let time = CMTimeMultiplyByFloat64(viewModel.duration, multiplier: progress)
        return FrameTime.formatTime(time)
    }
}

