import SwiftUI
import AVFoundation

struct TimeDisplay: View {
    @ObservedObject var playerViewModel: PlayerViewModel
    @ObservedObject var navigationViewModel: NavigationViewModel

    @State private var isEditingTime = false
    @State private var timeInputText = ""

    var body: some View {
        HStack(spacing: 12) {
            // Current time with frame precision
            timeDisplayView

            Divider()
                .frame(height: 16)

            // Frame information
            frameDisplayView

            Divider()
                .frame(height: 16)

            // Frame rate information
            frameRateDisplayView
        }
        .padding(.horizontal, 8)
        .padding(.vertical, 4)
        .background(Color(NSColor.controlBackgroundColor))
        .cornerRadius(6)
        .onTapGesture {
            startTimeEditing()
        }
    }

    // MARK: - Time Display

    private var timeDisplayView: some View {
        VStack(alignment: .leading, spacing: 2) {
            Text("TIME")
                .font(.caption2)
                .foregroundColor(.secondary)

            if isEditingTime {
                TextField("HH:MM:SS:FF", text: $timeInputText)
                    .textFieldStyle(RoundedBorderTextFieldStyle())
                    .font(.system(.body, design: .monospaced))
                    .frame(width: 120)
                    .onSubmit {
                        commitTimeEdit()
                    }
                    .onExitCommand {
                        cancelTimeEdit()
                    }
            } else {
                Text(formattedCurrentTimeWithFrames)
                    .font(.system(.body, design: .monospaced))
                    .foregroundColor(.primary)
            }
        }
    }

    // MARK: - Frame Display

    private var frameDisplayView: some View {
        VStack(alignment: .leading, spacing: 2) {
            Text("FRAME")
                .font(.caption2)
                .foregroundColor(.secondary)

            HStack(spacing: 4) {
                Text("\(navigationViewModel.currentFrame)")
                    .font(.system(.body, design: .monospaced))
                    .foregroundColor(.primary)

                Text("/ \(navigationViewModel.totalFrames)")
                    .font(.system(.caption, design: .monospaced))
                    .foregroundColor(.secondary)
            }
        }
    }

    // MARK: - Frame Rate Display

    private var frameRateDisplayView: some View {
        VStack(alignment: .leading, spacing: 2) {
            Text("FPS")
                .font(.caption2)
                .foregroundColor(.secondary)

            Text(formattedFrameRate)
                .font(.system(.body, design: .monospaced))
                .foregroundColor(.primary)
        }
    }

    // MARK: - Computed Properties

    private var formattedCurrentTimeWithFrames: String {
        guard let frameRate = navigationViewModel.frameRate else {
            return FrameTime.formatTime(playerViewModel.currentTime)
        }

        return FramePrecision.formatTimeWithFrames(playerViewModel.currentTime, frameRate: frameRate)
    }

    private var formattedFrameRate: String {
        guard let frameRate = navigationViewModel.frameRate else { return "-- fps" }
        return FramePrecision.formatFrameRate(frameRate)
    }

    // MARK: - Time Editing

    private func startTimeEditing() {
        isEditingTime = true
        timeInputText = formattedCurrentTimeWithFrames
    }

    private func commitTimeEdit() {
        guard let parsedTime = parseTimeInput(timeInputText),
              let frameRate = navigationViewModel.frameRate else {
            cancelTimeEdit()
            return
        }

        navigationViewModel.seekToTime(parsedTime)
        isEditingTime = false
    }

    private func cancelTimeEdit() {
        isEditingTime = false
        timeInputText = ""
    }

    // MARK: - Time Parsing

    private func parseTimeInput(_ input: String) -> CMTime? {
        let components = input.components(separatedBy: ":")

        // Support different time formats
        if components.count == 4 {
            // HH:MM:SS:FF format
            guard let hours = Int(components[0]),
                  let minutes = Int(components[1]),
                  let seconds = Int(components[2]),
                  let frames = Int(components[3]),
                  let frameRate = navigationViewModel.frameRate else { return nil }

            let totalSeconds = Double(hours * 3600 + minutes * 60 + seconds)
            let frameSeconds = Double(frames) / Double(frameRate)

            return CMTime(seconds: totalSeconds + frameSeconds, preferredTimescale: 600)

        } else if components.count == 3 {
            // HH:MM:SS format
            guard let hours = Int(components[0]),
                  let minutes = Int(components[1]),
                  let seconds = Int(components[2]) else { return nil }

            let totalSeconds = Double(hours * 3600 + minutes * 60 + seconds)
            return CMTime(seconds: totalSeconds, preferredTimescale: 600)

        } else if components.count == 2 {
            // MM:SS format
            guard let minutes = Int(components[0]),
                  let seconds = Int(components[1]) else { return nil }

            let totalSeconds = Double(minutes * 60 + seconds)
            return CMTime(seconds: totalSeconds, preferredTimescale: 600)

        } else if components.count == 1 {
            // Pure frame number
            if let frameNumber = Int(components[0]) {
                navigationViewModel.seekToFrame(frameNumber)
                return nil // Already handled the seek
            }

            // Pure seconds
            if let seconds = Double(components[0]) {
                return CMTime(seconds: seconds, preferredTimescale: 600)
            }
        }

        return nil
    }
}

// MARK: - Preview

struct TimeDisplay_Previews: PreviewProvider {
    static var previews: some View {
        TimeDisplay(
            playerViewModel: PlayerViewModel(),
            navigationViewModel: NavigationViewModel()
        )
        .padding()
    }
}

