import Foundation
import AVFoundation
import Combine
import CryptoKit

class MetadataManager: ObservableObject {
    @Published var currentMetadata: VideoMetadata?
    @Published var metadataDirectory: URL?

    private let encoder = JSONEncoder()
    private let decoder = JSONDecoder()
    private let fileManager = FileManager.default

    // UserDefaults key for metadata directory preference
    private let metadataDirectoryKey = "MetadataDirectory"

    init() {
        setupEncoder()
        loadMetadataDirectory()
    }

    private func setupEncoder() {
        encoder.outputFormatting = [.prettyPrinted, .sortedKeys]
        encoder.dateEncodingStrategy = .iso8601
        decoder.dateDecodingStrategy = .iso8601
    }

    private func loadMetadataDirectory() {
        if let directoryData = UserDefaults.standard.data(forKey: metadataDirectoryKey),
           let url = try? NSKeyedUnarchiver.unarchivedObject(ofClass: NSURL.self, from: directoryData) as URL? {
            metadataDirectory = url
        }
    }

    func setMetadataDirectory(_ url: URL) {
        metadataDirectory = url

        // Save to UserDefaults
        if let data = try? NSKeyedArchiver.archivedData(withRootObject: url, requiringSecureCoding: true) {
            UserDefaults.standard.set(data, forKey: metadataDirectoryKey)
        }
    }

    // MARK: - Auto Import/Export

    func loadMetadataForVideo(at videoURL: URL) -> VideoMetadata? {
        let metadataURL = metadataURL(for: videoURL)

        guard fileManager.fileExists(atPath: metadataURL.path) else {
            return nil
        }

        do {
            let data = try Data(contentsOf: metadataURL)
            let metadata = try decoder.decode(VideoMetadata.self, from: data)

            // Validate that this metadata is for the correct video
            if metadata.videoFileName != videoURL.lastPathComponent {
                print("Warning: Metadata filename mismatch")
                return nil
            }

            currentMetadata = metadata
            return metadata
        } catch {
            print("Failed to load metadata: \(error)")
            return nil
        }
    }

    func saveCurrentMetadata() {
        guard var metadata = currentMetadata else { return }

        metadata.touch()

        do {
            try saveMetadata(metadata)
        } catch {
            print("Failed to save metadata: \(error)")
        }
    }

    func createMetadataForVideo(at videoURL: URL, frameRate: Float?, duration: CMTime?) -> VideoMetadata {
        let checksum = calculateChecksum(for: videoURL)
        var metadata = VideoMetadata(videoFileName: videoURL.lastPathComponent, videoChecksum: checksum)

        metadata.videoFrameRate = frameRate
        metadata.videoDuration = duration

        currentMetadata = metadata
        saveCurrentMetadata()

        return metadata
    }

    private func saveMetadata(_ metadata: VideoMetadata) throws {
        let videoURL = URL(fileURLWithPath: metadata.videoFileName)
        let metadataURL = metadataURL(for: videoURL)

        // Ensure the directory exists
        let directory = metadataURL.deletingLastPathComponent()
        if !fileManager.fileExists(atPath: directory.path) {
            try fileManager.createDirectory(at: directory, withIntermediateDirectories: true)
        }

        let data = try encoder.encode(metadata)
        try data.write(to: metadataURL)
    }

    private func metadataURL(for videoURL: URL) -> URL {
        let fileName = videoURL.deletingPathExtension().lastPathComponent + ".json"

        if let metadataDirectory = metadataDirectory {
            return metadataDirectory.appendingPathComponent(fileName)
        } else {
            // Default to same directory as video
            return videoURL.deletingLastPathComponent().appendingPathComponent(fileName)
        }
    }

    private func calculateChecksum(for url: URL) -> String? {
        do {
            let handle = try FileHandle(forReadingFrom: url)
            defer { handle.closeFile() }

            var hasher = SHA256()
            let chunkSize = 1024 * 1024 // 1MB chunks

            while autoreleasepool(invoking: {
                let chunk = handle.readData(ofLength: chunkSize)
                if !chunk.isEmpty {
                    hasher.update(data: chunk)
                    return true
                }
                return false
            }) {}

            let hash = hasher.finalize()
