import SwiftUI
import AVFoundation
import Combine

class NavigationViewModel: ObservableObject {
    @Published var scrubConfig = ScrubConfiguration.default

    // Dependencies
    weak var player: AVPlayer?
    weak var playerViewModel: PlayerViewModel?

    private var cancellables = Set<AnyCancellable>()

    init() {
        // Configuration could be loaded from UserDefaults or other persistence later
    }

    // MARK: - Setup

    func setup(player: AVPlayer, playerViewModel: PlayerViewModel) {
        self.player = player
        self.playerViewModel = playerViewModel
    }

    // MARK: - Context-Aware Scrubbing

    /// Scrub forward based on current playback state
    func scrubForward() {
        guard let player = player,
              let playerViewModel = playerViewModel,
              let frameRate = playerViewModel.video?.frameRate else { return }

        if playerViewModel.isPlaying {
            // Playing: skip by configured seconds
            scrubBySeconds(scrubConfig.secondsWhenPlaying)
        } else {
            // Paused: advance by configured frames
            scrubByFrames(scrubConfig.framesWhenPaused, frameRate: frameRate)
        }
    }

    /// Scrub backward based on current playback state
    func scrubBackward() {
        guard let player = player,
              let playerViewModel = playerViewModel,
              let frameRate = playerViewModel.video?.frameRate else { return }

        if playerViewModel.isPlaying {
            // Playing: skip back by configured seconds
            scrubBySeconds(-scrubConfig.secondsWhenPlaying)
        } else {
            // Paused: go back by configured frames
            scrubByFrames(-scrubConfig.framesWhenPaused, frameRate: frameRate)
        }
    }

    // MARK: - Precise Navigation

    /// Scrub by specific number of seconds
    private func scrubBySeconds(_ seconds: Double) {
        guard let player = player else { return }

        let currentTime = player.currentTime()
        let targetTime = CMTimeAdd(currentTime, CMTime(seconds: seconds, preferredTimescale: 600))

        // Ensure we don't go past boundaries
        let clampedTime = clampTime(targetTime)

        player.seek(to: clampedTime, toleranceBefore: .zero, toleranceAfter: .zero)
    }

    /// Scrub by specific number of frames
    private func scrubByFrames(_ frameOffset: Int, frameRate: Float) {
        guard let player = player else { return }

        let currentFrame = FramePrecision.frameNumber(from: player.currentTime(), frameRate: frameRate)
        let targetFrame = max(0, currentFrame + frameOffset)

        // Ensure we don't go past the end
        if let duration = playerViewModel?.duration {
            let totalFrames = FramePrecision.totalFrames(for: duration, frameRate: frameRate)
            let clampedFrame = min(targetFrame, totalFrames - 1)

            FramePrecision.seekToFrame(clampedFrame, frameRate: frameRate, player: player)
        } else {
            FramePrecision.seekToFrame(targetFrame, frameRate: frameRate, player: player)
        }
    }

    /// Seek to specific frame number
    func seekToFrame(_ frameNumber: Int) {
        guard let player = player,
              let frameRate = playerViewModel?.video?.frameRate else { return }

        // Ensure frame is within bounds
        var clampedFrame = max(0, frameNumber)
        if let duration = playerViewModel?.duration {
            let totalFrames = FramePrecision.totalFrames(for: duration, frameRate: frameRate)
            clampedFrame = min(clampedFrame, totalFrames - 1)
        }

        FramePrecision.seekToFrame(clampedFrame, frameRate: frameRate, player: player)
    }

    /// Seek to specific time
    func seekToTime(_ time: CMTime) {
        guard let player = player else { return }

        let clampedTime = clampTime(time)
        player.seek(to: clampedTime, toleranceBefore: .zero, toleranceAfter: .zero)
    }

    // MARK: - Utility

    /// Clamp time to valid boundaries (0 to duration)
    private func clampTime(_ time: CMTime) -> CMTime {
        guard let duration = playerViewModel?.duration,
              duration.isValid && !duration.isIndefinite else { return time }

        // Ensure time is not negative
        let positiveTime = CMTimeMaximum(time, .zero)

        // Ensure time doesn't exceed duration
        return CMTimeMinimum(positiveTime, duration)
    }

    // MARK: - Configuration

    /// Update scrub configuration
    func updateConfiguration(_ newConfig: ScrubConfiguration) {
        scrubConfig = newConfig
        // TODO: Persist to UserDefaults in future phase
    }

    /// Check if a key matches the scrub configuration
    func isForwardKey(keyCode: UInt16, modifiers: EventModifiers) -> Bool {
        return keyCode == scrubConfig.forwardKey && modifiers == scrubConfig.forwardModifiers
    }

    /// Check if a key matches the backward scrub configuration
    func isBackwardKey(keyCode: UInt16, modifiers: EventModifiers) -> Bool {
        return keyCode == scrubConfig.backwardKey && modifiers == scrubConfig.backwardModifiers
    }
}

// MARK: - Computed Properties

extension NavigationViewModel {

    /// Get current frame number
    var currentFrame: Int {
        guard let player = player,
              let frameRate = playerViewModel?.video?.frameRate else { return 0 }

        return FramePrecision.frameNumber(from: player.currentTime(), frameRate: frameRate)
    }

    /// Get total frame count
    var totalFrames: Int {
        guard let duration = playerViewModel?.duration,
              let frameRate = playerViewModel?.video?.frameRate else { return 0 }

        return FramePrecision.totalFrames(for: duration, frameRate: frameRate)
    }

    /// Get current frame rate
    var frameRate: Float? {
        return playerViewModel?.video?.frameRate
    }
}

