import Foundation
import AppKit
import Carbon
import SwiftUI

class KeyboardShortcutManager: ObservableObject {
    private var localEventMonitor: Any?
    private var globalEventMonitor: Any?
    private var markerViewModel: MarkerViewModel?
    private var navigationViewModel: NavigationViewModel?
    private var configuration = ShortcutConfiguration.load()
    private var isEnabled = true

    func setup(with markerViewModel: MarkerViewModel, navigationViewModel: NavigationViewModel) {
        self.markerViewModel = markerViewModel
        self.navigationViewModel = navigationViewModel
        loadConfiguration()
        setupEventMonitor()

        // Listen for configuration updates
        NotificationCenter.default.addObserver(
            forName: .shortcutConfigurationUpdated,
            object: nil,
            queue: .main
        ) { [weak self] _ in
            self?.loadConfiguration()
        }
    }

    func loadConfiguration() {
        configuration = ShortcutConfiguration.load()
    }

    func setEnabled(_ enabled: Bool) {
        isEnabled = enabled
        print("[KeyboardShortcut] Shortcuts \(enabled ? "enabled" : "disabled")")
    }

    deinit {
        removeEventMonitors()
        NotificationCenter.default.removeObserver(self)
    }

    private func removeEventMonitors() {
        if let monitor = localEventMonitor {
            NSEvent.removeMonitor(monitor)
            localEventMonitor = nil
        }
        if let monitor = globalEventMonitor {
            NSEvent.removeMonitor(monitor)
            globalEventMonitor = nil
        }
    }

    private func setupEventMonitor() {
        // Remove existing monitors
        removeEventMonitors()

        // Local monitor for when app has focus
        localEventMonitor = NSEvent.addLocalMonitorForEvents(matching: [.keyDown]) { [weak self] event in
            guard let self = self, self.isEnabled else { return event }

            if self.handleKeyEvent(event) {
                return nil // Consume the event
            }
            return event
        }

        // Global monitor for when app doesn't have focus (requires accessibility permissions)
        if KeyboardShortcutManager.hasAccessibilityPermissions() {
            globalEventMonitor = NSEvent.addGlobalMonitorForEvents(matching: [.keyDown]) { [weak self] event in
                guard let self = self, self.isEnabled else { return }
                _ = self.handleKeyEvent(event)
            }
        }

        print("[KeyboardShortcut] Event monitors setup - Local: \(localEventMonitor != nil), Global: \(globalEventMonitor != nil)")
    }

    private func handleKeyEvent(_ event: NSEvent) -> Bool {
        let keyCode = event.keyCode
        let modifierFlags = event.modifierFlags.intersection(.deviceIndependentFlagsMask)

        // Convert NSEvent modifiers to SwiftUI EventModifiers
        var eventModifiers = SwiftUI.EventModifiers()
        if modifierFlags.contains(.command) { eventModifiers.insert(SwiftUI.EventModifiers.command) }
        if modifierFlags.contains(.shift) { eventModifiers.insert(SwiftUI.EventModifiers.shift) }
        if modifierFlags.contains(.option) { eventModifiers.insert(SwiftUI.EventModifiers.option) }
        if modifierFlags.contains(.control) { eventModifiers.insert(SwiftUI.EventModifiers.control) }

        print("[KeyboardShortcut] Key event - Code: \(keyCode), Modifiers: \(eventModifiers)")

        // Check scrub configurations first (higher priority)
        if let navigationViewModel = navigationViewModel {
            for scrubConfig in configuration.scrubConfigurations {
                // Forward scrub
                if keyCode == scrubConfig.forwardKey && eventModifiers == scrubConfig.forwardModifiers {
                    navigationViewModel.scrubForward()
                    print("[KeyboardShortcut] Handled: Scrub Forward")
                    return true // Consume the event
                }

                // Backward scrub
                if keyCode == scrubConfig.backwardKey && eventModifiers == scrubConfig.backwardModifiers {
                    navigationViewModel.scrubBackward()
                    print("[KeyboardShortcut] Handled: Scrub Backward")
                    return true // Consume the event
                }
            }
        }

        // General shortcuts (using saved configuration)
        if keyCode == configuration.addMarkerKey && eventModifiers == configuration.addMarkerModifiers {
            markerViewModel?.addMarkerAtCurrentTime()
            print("[KeyboardShortcut] Handled: Add Marker")
            return true // Consume the event
        }

        if keyCode == configuration.nextMarkerKey && eventModifiers == configuration.nextMarkerModifiers {
            markerViewModel?.navigateToNextMarker()
            print("[KeyboardShortcut] Handled: Next Marker")
            return true // Consume the event
        }

        if keyCode == configuration.prevMarkerKey && eventModifiers == configuration.prevMarkerModifiers {
            markerViewModel?.navigateToPreviousMarker()
            print("[KeyboardShortcut] Handled: Previous Marker")
            return true // Consume the event
        }

        if keyCode == configuration.deleteMarkerKey && eventModifiers == configuration.deleteMarkerModifiers {
            markerViewModel?.deleteSelectedMarker()
            print("[KeyboardShortcut] Handled: Delete Marker")
            return true // Consume the event
        }

        return false // Don't consume other events
    }
}

// MARK: - Key Constants
extension KeyboardShortcutManager {
    private var kVK_Tab: UInt16 { 48 }
    private var kVK_Delete: UInt16 { 51 }
    private var kVK_RightArrow: UInt16 { 124 }
    private var kVK_LeftArrow: UInt16 { 123 }
}

// MARK: - Accessibility Support
extension KeyboardShortcutManager {
    static func requestAccessibilityPermissions() -> Bool {
        let options: NSDictionary = [kAXTrustedCheckOptionPrompt.takeUnretainedValue() as String: true]
        return AXIsProcessTrustedWithOptions(options)
    }

    static func hasAccessibilityPermissions() -> Bool {
        return AXIsProcessTrusted()
    }
}

import Foundation
import SwiftUI

struct ScrubConfiguration: Codable, Equatable {
    var backwardKey: UInt16 = 123 // Left Arrow
    var forwardKey: UInt16 = 124 // Right Arrow
    var backwardModifiers: EventModifiers = []
    var forwardModifiers: EventModifiers = []
    var secondsWhenPlaying: Double = 5.0
    var framesWhenPaused: Int = 1

    // Default configuration
    static let `default` = ScrubConfiguration()

    // Common frame rates for video content
    static let supportedFrameRates: [Float] = [23.976, 24.0, 25.0, 29.97, 30.0, 60.0]
}

extension EventModifiers: Codable {
    private enum CodingKeys: String, CodingKey {
        case rawValue
    }

    public init(from decoder: Decoder) throws {
        let container = try decoder.container(keyedBy: CodingKeys.self)
        let rawValue = try container.decode(Int.self, forKey: .rawValue)
        self.init(rawValue: rawValue)
    }

    public func encode(to encoder: Encoder) throws {
        var container = encoder.container(keyedBy: CodingKeys.self)
        try container.encode(rawValue, forKey: .rawValue)
    }
}

// Key code constants for common navigation keys
extension ScrubConfiguration {
    static let kVK_LeftArrow: UInt16 = 123
    static let kVK_RightArrow: UInt16 = 124
    static let kVK_UpArrow: UInt16 = 126
    static let kVK_DownArrow: UInt16 = 125
    static let kVK_Space: UInt16 = 49
}

