    let id = UUID()
    let url: URL
    let asset: AVAsset

    var filename: String {
        url.lastPathComponent
    }

    var duration: CMTime? {
        guard asset.duration.isValid && !asset.duration.isIndefinite else { return nil }
        return asset.duration
    }

    var frameRate: Float? {
        guard let track = asset.tracks(withMediaType: .video).first else { return nil }
        return track.nominalFrameRate
    }

    init(url: URL) {
        self.url = url
        self.asset = AVAsset(url: url)
    }
}

