import Foundation
import AVFoundation

struct TimeCode: Equatable {
    let hours: Int
    let minutes: Int
    let seconds: Int
    let frames: Int
    let frameRate: Float

    init(time: CMTime, frameRate: Float) {
        self.frameRate = frameRate

        let totalSeconds = CMTimeGetSeconds(time)
        let totalFrames = Int(totalSeconds * Double(frameRate))

        self.frames = totalFrames % Int(frameRate)
        let totalWholeSeconds = totalFrames / Int(frameRate)

        self.seconds = totalWholeSeconds % 60
        let totalMinutes = totalWholeSeconds / 60

        self.minutes = totalMinutes % 60
        self.hours = totalMinutes / 60
    }

    init(frames: Int, frameRate: Float) {
        self.frameRate = frameRate
        self.frames = frames % Int(frameRate)

        let totalWholeSeconds = frames / Int(frameRate)
        self.seconds = totalWholeSeconds % 60

        let totalMinutes = totalWholeSeconds / 60
        self.minutes = totalMinutes % 60
        self.hours = totalMinutes / 60
    }

    var formattedString: String {
        String(format: "%02d:%02d:%02d:%02d", hours, minutes, seconds, frames)
    }

    var totalFrames: Int {
        let framesPerSecond = Int(frameRate)
        return hours * 3600 * framesPerSecond +
               minutes * 60 * framesPerSecond +
               seconds * framesPerSecond +
               frames
    }

    var cmTime: CMTime {
        let totalSeconds = Double(totalFrames) / Double(frameRate)
        return CMTime(seconds: totalSeconds, preferredTimescale: Int32(frameRate * 100))
    }
}

