     1â†’//
     2â†’//  VideoPlayerViewModel.swift
     3â†’//  SwiftMedia
     4â†’//
     5â†’//  Created by Ojash Neopane on 6/24/25.
     6â†’//
     7â†’
     8â†’import Foundation
     9â†’import AVFoundation
    10â†’import Combine
    11â†’
    12â†’enum PlayerError: LocalizedError {
    13â†’    case fileNotFound
    14â†’    case unsupportedFormat
    15â†’    case decodingFailed
    16â†’
    17â†’    var errorDescription: String? {
    18â†’        switch self {
    19â†’        case .fileNotFound: return "Video file not found"
    20â†’        case .unsupportedFormat: return "This video format is not supported"
    21â†’        case .decodingFailed: return "Failed to decode video"
    22â†’        }
    23â†’    }
    24â†’}
    25â†’
    26â†’@MainActor
    27â†’class VideoPlayerViewModel: ObservableObject {
    28â†’    @Published var player: AVPlayer?
    29â†’    @Published var currentTime: CMTime = .zero
    30â†’    @Published var duration: CMTime = .zero
    31â†’    @Published var isPlaying = false
    32â†’    @Published var frameRate: Float = 30.0
    33â†’    @Published var videoURL: URL?
    34â†’    @Published var playerError: PlayerError?
    35â†’    @Published var loadTime: TimeInterval = 0
    36â†’    @Published var markers: [Marker] = [] {
    37â†’        didSet {
    38â†’            // Invalidate cache when markers change
    39â†’            markersCacheInvalidated = true
    40â†’            
    41â†’            // Auto-save markers when changed
    42â†’            if oldValue != markers {
    43â†’                scheduleMarkerSave()
    44â†’            }
    45â†’        }
    46â†’    }
    47â†’    @Published var selectedMarker: Marker?
    48â†’    @Published var scrubbingSettings = ScrubbingSettings()
    49â†’    @Published var seekFeedback: SeekFeedback?
    50â†’    @Published var showPerformanceOverlay = false
    51â†’
    52â†’    private var timeObserver: Any?
    53â†’    private var cancellables = Set<AnyCancellable>()
    54â†’    private var loadStartTime: Date?
    55â†’    private var sortedMarkersCache: [Marker] = []
    56â†’    private var markersCacheInvalidated = true
    57â†’    private var saveWorkItem: DispatchWorkItem?
    58â†’    private var seekManager: SeekManager?
    59â†’
    60â†’    private func updateMarkersCache() {
    61â†’        if markersCacheInvalidated {
    62â†’            sortedMarkersCache = markers.sorted()
    63â†’            markersCacheInvalidated = false
    64â†’        }
    65â†’    }
    66â†’    
    67â†’    var segments: [Segment] {
    68â†’        // Use cached markers if available, otherwise compute without modifying state
    69â†’        let sortedMarkers = markersCacheInvalidated ? markers.sorted() : sortedMarkersCache
    70â†’        
    71â†’        var result = [Segment]()
    72â†’        guard let duration = player?.currentItem?.duration,
    73â†’              duration.isValid && duration.value > 0 else { return result }
    74â†’
    75â†’        if sortedMarkers.isEmpty {
    76â†’            // No markers - entire video is one segment
    77â†’            result.append(Segment(startMarker: nil, endMarker: nil))
    78â†’        } else {
    79â†’            // First segment: start to first marker
    80â†’            if let firstMarker = sortedMarkers.first {
    81â†’                result.append(Segment(startMarker: nil, endMarker: firstMarker))
    82â†’            }
    83â†’
    84â†’            // Middle segments
    85â†’            if sortedMarkers.count > 1 {
    86â†’                for markerIndex in 0..<sortedMarkers.count - 1 {
    87â†’                    result.append(Segment(
    88â†’                        startMarker: sortedMarkers[markerIndex],
    89â†’                        endMarker: sortedMarkers[markerIndex + 1]
    90â†’                    ))
    91â†’                }
    92â†’            }
    93â†’
    94â†’            // Last segment: last marker to end
    95â†’            if let lastMarker = sortedMarkers.last {
    96â†’                result.append(Segment(startMarker: lastMarker, endMarker: nil))
    97â†’            }
    98â†’        }
    99â†’
   100â†’        return result
   101â†’    }
   102â†’    
   103â†’    var currentSegment: Segment? {
   104â†’        let time = currentTime
   105â†’        
   106â†’        for segment in segments {
   107â†’            let startTime = segment.startMarker?.timestamp ?? .zero
   108â†’            let endTime = segment.endMarker?.timestamp ?? duration
   109â†’            
   110â†’            // Check if current time is within this segment
   111â†’            if CMTimeCompare(time, startTime) >= 0 && CMTimeCompare(time, endTime) < 0 {
   112â†’                return segment
   113â†’            }
   114â†’        }
   115â†’        
   116â†’        // If we're at the very end, return the last segment
   117â†’        if let lastSegment = segments.last,
   118â†’           CMTimeCompare(time, duration) >= 0 {
   119â†’            return lastSegment
   120â†’        }
   121â†’        
   122â†’        return nil
   123â†’    }
   124â†’
   125â†’    deinit {
   126â†’        // Note: Cannot access @MainActor properties from deinit
   127â†’        // Cleanup is handled in loadVideo when replacing player
   128â†’    }
   129â†’
   130â†’    func loadVideo(url: URL) {
   131â†’        // Clean up existing player
   132â†’        player?.pause()  // Explicitly pause current player to prevent multiple audio streams
   133â†’        isPlaying = false
   134â†’        
   135â†’        // Stop performance monitoring for old player
   136â†’        PerformanceMonitor.shared.stopMonitoring()
   137â†’        
   138â†’        // Clear existing observers to prevent dangling subscriptions
   139â†’        cancellables.removeAll()
   140â†’        
   141â†’        // Cancel any pending seeks from old SeekManager
   142â†’        seekManager?.cancelPendingSeeks()
   143â†’        
   144â†’        if let observer = timeObserver {
   145â†’            player?.removeTimeObserver(observer)
   146â†’            timeObserver = nil
   147â†’        }
   148â†’
   149â†’        // Reset error state
   150â†’        playerError = nil
   151â†’
   152â†’        // Reset markers for new video (will load after video is ready)
   153â†’        markers = []
   154â†’        selectedMarker = nil
   155â†’        markersCacheInvalidated = true
   156â†’
   157â†’        // Start performance monitoring
   158â†’        loadStartTime = Date()
   159â†’
   160â†’        // Check if file exists
   161â†’        guard FileManager.default.fileExists(atPath: url.path) else {
   162â†’            playerError = .fileNotFound
   163â†’            return
   164â†’        }
   165â†’
   166â†’        videoURL = url
   167â†’        let asset = AVURLAsset(url: url)
   168â†’        let playerItem = AVPlayerItem(asset: asset)
   169â†’
   170â†’        player = AVPlayer(playerItem: playerItem)
   171â†’
   172â†’        // Initialize SeekManager
   173â†’        seekManager = SeekManager(player: player!)
   174â†’
   175â†’        // Configure player for large files
   176â†’        configurePlayerForLargeFiles()
   177â†’
   178â†’        // Get video duration
   179â†’        Task {
   180â†’            do {
   181â†’                let duration = try await asset.load(.duration)
   182â†’                await MainActor.run {
   183â†’                    self.duration = duration
   184â†’                }
   185â†’
   186â†’                // Extract frame rate
   187â†’                if let track = try await asset.loadTracks(withMediaType: .video).first {
   188â†’                    let frameRate = try await track.load(.nominalFrameRate)
   189â†’                    await MainActor.run {
   190â†’                        self.frameRate = frameRate > 0 ? frameRate : 30.0
   191â†’                    }
   192â†’                }
   193â†’            } catch {
   194â†’                await MainActor.run {
   195â†’                    self.playerError = .decodingFailed
   196â†’                }
   197â†’                print("Error loading video metadata: \(error)")
   198â†’            }
   199â†’        }
   200â†’
   201â†’        // Set up time observer
   202â†’        setupTimeObserver()
   203â†’
   204â†’        // Observe player status
   205â†’        playerItem.publisher(for: \.status)
   206â†’            .sink { [weak self] status in
   207â†’                if status == .failed {
   208â†’                    self?.handlePlayerItemError(playerItem.error)
   209â†’                } else if status == .readyToPlay {
   210â†’                    // Record load time
   211â†’                    if let startTime = self?.loadStartTime {
   212â†’                        self?.loadTime = Date().timeIntervalSince(startTime)
   213â†’                        print("Video loaded in \(String(format: "%.2f", self?.loadTime ?? 0)) seconds")
   214â†’                    }
   215â†’
   216â†’                    // Load markers for this video
   217â†’                    self?.loadMarkers()
   218â†’                    
   219â†’                    // Start performance monitoring
   220â†’                    if let player = self?.player {
   221â†’                        PerformanceMonitor.shared.startMonitoring(player: player)
   222â†’                        PerformanceAnalytics.shared.record(PerformanceMonitor.shared.currentMetrics ?? PerformanceMetrics(
   223â†’                            timestamp: Date(),
   224â†’                            cpuUsage: 0,
   225â†’                            memoryUsage: 0,
   226â†’                            droppedFrames: 0,
   227â†’                            frameRate: 30,
   228â†’                            frameRateStability: 100,
   229â†’                            gpuUsage: nil,
   230â†’                            decoderEfficiency: nil
   231â†’                        ))
   232â†’                    }
   233â†’                    
   234â†’                    // Auto-play the video when it loads
   235â†’                    self?.play()
   236â†’                }
   237â†’            }
   238â†’            .store(in: &cancellables)
   239â†’
   240â†’        // Observe when video ends
   241â†’        NotificationCenter.default.publisher(for: .AVPlayerItemDidPlayToEndTime, object: playerItem)
   242â†’            .sink { [weak self] _ in
   243â†’                self?.isPlaying = false
   244â†’            }
   245â†’            .store(in: &cancellables)
   246â†’    }
   247â†’
   248â†’    private func setupTimeObserver() {
   249â†’        let interval = CMTime(seconds: 1.0/30.0, preferredTimescale: CMTimeScale(NSEC_PER_SEC))
   250â†’        timeObserver = player?.addPeriodicTimeObserver(forInterval: interval, queue: .main) { [weak self] time in
   251â†’            Task { @MainActor in
   252â†’                self?.currentTime = time
   253â†’            }
   254â†’        }
   255â†’    }
   256â†’
   257â†’    func play() {
   258â†’        player?.play()
   259â†’        isPlaying = true
   260â†’    }
   261â†’
   262â†’    func pause() {
   263â†’        player?.pause()
   264â†’        isPlaying = false
   265â†’    }
   266â†’
   267â†’    func togglePlayPause() {
   268â†’        if isPlaying {
   269â†’            pause()
   270â†’        } else {
   271â†’            play()
   272â†’        }
   273â†’    }
   274â†’    
   275â†’    func togglePerformanceOverlay() {
   276â†’        showPerformanceOverlay.toggle()
   277â†’    }
   278â†’    
   279â†’    // MARK: - Volume Control
   280â†’    
   281â†’    func increaseVolume() {
   282â†’        guard let player = player else { return }
   283â†’        let newVolume = min(player.volume + 0.1, 1.0)
   284â†’        player.volume = newVolume
   285â†’        showVolumeFeedback(volume: newVolume)
   286â†’    }
   287â†’    
   288â†’    func decreaseVolume() {
   289â†’        guard let player = player else { return }
   290â†’        let newVolume = max(player.volume - 0.1, 0.0)
   291â†’        player.volume = newVolume
   292â†’        showVolumeFeedback(volume: newVolume)
   293â†’    }
   294â†’    
   295â†’    private func showVolumeFeedback(volume: Float) {
   296â†’        let percentage = Int(volume * 100)
   297â†’        seekFeedback = SeekFeedback(text: "Volume: \(percentage)%", direction: .forward)
   298â†’        
   299â†’        // Auto-hide after 1 second
   300â†’        DispatchQueue.main.asyncAfter(deadline: .now() + 1.0) { [weak self] in
   301â†’            self?.seekFeedback = nil
   302â†’        }
   303â†’    }
   304â†’
   305â†’    func seek(to time: CMTime, toleranceBefore: CMTime = .zero, toleranceAfter: CMTime = .zero) {
   306â†’        let clampedTime = clampTime(time)
   307â†’
   308â†’        if let seekManager = seekManager {
   309â†’            seekManager.seek(
   310â†’                to: clampedTime,
   311â†’                toleranceBefore: toleranceBefore,
   312â†’                toleranceAfter: toleranceAfter
   313â†’            ) { finished in
   314â†’                if finished {
   315â†’                    // UI will update automatically via @Published currentTime
   316â†’                }
   317â†’            }
   318â†’        } else {
   319â†’            player?.seek(to: clampedTime, toleranceBefore: toleranceBefore, toleranceAfter: toleranceAfter)
   320â†’        }
   321â†’    }
   322â†’
   323â†’    func seek(to progress: Double) {
   324â†’        let time = CMTime(seconds: progress * CMTimeGetSeconds(duration), preferredTimescale: duration.timescale)
   325â†’        seek(to: time)
   326â†’    }
   327â†’
   328â†’    private func configurePlayerForLargeFiles() {
   329â†’        // Configure player for 4K+ videos
   330â†’        player?.currentItem?.preferredForwardBufferDuration = 2.0
   331â†’        player?.automaticallyWaitsToMinimizeStalling = false
   332â†’        
   333â†’        // Enable hardware acceleration optimizations
   334â†’        VideoMemoryManager.optimizeForLargeFiles()
   335â†’        
   336â†’        // Configure for hardware decoding if available
   337â†’        if let currentItem = player?.currentItem {
   338â†’            // Set video composition pixel buffer attributes for optimal performance
   339â†’            _ = VideoMemoryManager.optimizedPixelBufferAttributes()
   340â†’            
