import Foundation
import AVFoundation
import AppKit

@MainActor
class VideoDirectoryManager: ObservableObject {
    @Published var videos: [URL] = []
    @Published var currentIndex: Int = -1
    @Published var isScanning = false
    @Published var lastScanError: Error?

    private let supportedExtensions = ["mp4", "mov", "m4v", "avi", "mkv", "mpg", "mpeg", "webm"]
    private var currentDirectoryURL: URL?

    var currentVideo: URL? {
        guard currentIndex >= 0 && currentIndex < videos.count else { return nil }
        return videos[currentIndex]
    }

    var hasNext: Bool {
        currentIndex < videos.count - 1
    }

    var hasPrevious: Bool {
        currentIndex > 0
    }

    var positionDescription: String {
        guard !videos.isEmpty && currentIndex >= 0 else { 
            if isScanning {
                return "Scanning..."
            } else if lastScanError != nil {
                return "Scan failed"
            }
            return "" 
        }
        return "\(currentIndex + 1) of \(videos.count)"
    }

    func scanDirectory(at url: URL) async {
        let timestamp = Date().timeIntervalSince1970
        print("[\(String(format: "%.3f", timestamp))] VideoDirectoryManager.scanDirectory() called for: \(url.lastPathComponent)")

        isScanning = true
        lastScanError = nil

        do {
            let fileManager = FileManager.default
            let directoryURL = url.deletingLastPathComponent()

            // Store current directory for comparison
            currentDirectoryURL = directoryURL

            print("[\(String(format: "%.3f", timestamp))] Scanning directory: \(directoryURL.path)")

            // Try to use stored bookmark first
            var bookmarkURL: URL?
            var bookmarkShouldStopAccessing = false

            if let bookmarkData = UserDefaults.standard.data(forKey: "DirectoryBookmark-\(directoryURL.path)") {
                do {
                    var isStale = false
                    bookmarkURL = try URL(resolvingBookmarkData: bookmarkData,
                                        options: .withSecurityScope,
                                        relativeTo: nil,
                                        bookmarkDataIsStale: &isStale)
                    if !isStale && bookmarkURL != nil {
                        bookmarkShouldStopAccessing = bookmarkURL!.startAccessingSecurityScopedResource()
                        print("[\(String(format: "%.3f", timestamp))] Using stored bookmark for directory access")
                    } else if isStale {
                        print("[\(String(format: "%.3f", timestamp))] Bookmark is stale, removing it")
                        UserDefaults.standard.removeObject(forKey: "DirectoryBookmark-\(directoryURL.path)")
                    }
                } catch {
                    print("[\(String(format: "%.3f", timestamp))] Failed to resolve bookmark: \(error)")
                    UserDefaults.standard.removeObject(forKey: "DirectoryBookmark-\(directoryURL.path)")
                }
            }

            // If no bookmark or bookmark failed, try direct access
            var shouldStopAccessing = false
            var fileSecurityScopeActive = false

            if bookmarkURL == nil {
                // First try if the current file URL gives us access to the directory
                if url.startAccessingSecurityScopedResource() {
                    shouldStopAccessing = true
                    fileSecurityScopeActive = true
                    print("[\(String(format: "%.3f", timestamp))] Using file's security scope for directory access")

                    // When we have file access, we often have implicit access to the parent directory
                    // Try to create a bookmark while we have access
                    do {
                        let bookmarkData = try directoryURL.bookmarkData(
                            options: [.withSecurityScope, .securityScopeAllowOnlyReadAccess],
                            includingResourceValuesForKeys: nil,
                            relativeTo: nil
                        )
                        UserDefaults.standard.set(bookmarkData, forKey: "DirectoryBookmark-\(directoryURL.path)")
                        print("[\(String(format: "%.3f", timestamp))] Created bookmark for directory while file scope is active")
                    } catch {
                        print("[\(String(format: "%.3f", timestamp))] Could not create bookmark: \(error)")
                        // If bookmark creation fails, we might still have access via the file scope
                        // Continue scanning rather than failing immediately
                    }
                } else if directoryURL.startAccessingSecurityScopedResource() {
                    shouldStopAccessing = true
                    print("[\(String(format: "%.3f", timestamp))] Started accessing security-scoped resource for directory")
                }
            }

            defer {
                if shouldStopAccessing {
                    if fileSecurityScopeActive {
                        url.stopAccessingSecurityScopedResource()
                    } else {
                        directoryURL.stopAccessingSecurityScopedResource()
                    }
                    print("[\(String(format: "%.3f", timestamp))] Stopped accessing security-scoped resource")
                }
                if bookmarkShouldStopAccessing, let bookmarkURL = bookmarkURL {
                    bookmarkURL.stopAccessingSecurityScopedResource()
                    print("[\(String(format: "%.3f", timestamp))] Stopped accessing bookmark resource")
                }
            }

            // Validate directory access
            guard fileManager.fileExists(atPath: directoryURL.path) else {
                print("[\(String(format: "%.3f", timestamp))] Directory does not exist: \(directoryURL.path)")
                lastScanError = ScanError.directoryNotFound
                isScanning = false
                return
            }

            // Check if we have permission to read the directory
            // Even with file security scope, we need to test actual access
            var canAccessDirectory = false

            // Always test directory access by trying to read its contents
            do {
                _ = try fileManager.contentsOfDirectory(at: directoryURL, includingPropertiesForKeys: nil, options: .skipsHiddenFiles)
                canAccessDirectory = true
                print("[\(String(format: "%.3f", timestamp))] Directory access confirmed")
            } catch {
                print("[\(String(format: "%.3f", timestamp))] Cannot access directory contents: \(error)")
                canAccessDirectory = false
            }

            guard canAccessDirectory else {
                print("[\(String(format: "%.3f", timestamp))] No read permission for directory: \(directoryURL.path)")
                lastScanError = ScanError.permissionDenied
                isScanning = false
                return
            }

            let resourceKeys: [URLResourceKey] = [.nameKey, .isDirectoryKey, .fileSizeKey, .creationDateKey]
            let enumerator = fileManager.enumerator(
                at: directoryURL,
                includingPropertiesForKeys: resourceKeys,
                options: [.skipsHiddenFiles, .skipsSubdirectoryDescendants]
            )

            guard enumerator != nil else {
                print("[\(String(format: "%.3f", timestamp))] Failed to create enumerator for directory")
                lastScanError = ScanError.permissionDenied
                isScanning = false
                return
            }

            var videoFiles: [URL] = []
            var filesChecked = 0

            while let fileURL = enumerator?.nextObject() as? URL {
                filesChecked += 1
                let resourceValues = try fileURL.resourceValues(forKeys: Set(resourceKeys))

                guard let isDirectory = resourceValues.isDirectory, !isDirectory else { continue }

                let fileExtension = fileURL.pathExtension.lowercased()
                if supportedExtensions.contains(fileExtension) {
                    videoFiles.append(fileURL)
                }
            }

            videos = videoFiles.sorted { $0.lastPathComponent < $1.lastPathComponent }
            print("[\(String(format: "%.3f", timestamp))] Scanned \(filesChecked) files, found \(videos.count) video files")

            // If we found no files at all (not even non-video files), it's likely a permission issue
            if filesChecked == 0 && fileSecurityScopeActive && bookmarkURL == nil {
                print("[\(String(format: "%.3f", timestamp))] No files found with file security scope - likely insufficient permissions")
                lastScanError = ScanError.permissionDenied
                videos = []
                currentIndex = -1
                isScanning = false
                return
            }

            // Set current index to the scanned video
            currentIndex = videos.firstIndex(of: url) ?? -1
            print("[\(String(format: "%.3f", timestamp))] Set currentIndex to \(currentIndex) for video: \(url.lastPathComponent)")

            // Log all found videos for debugging
            for (index, video) in videos.enumerated() {
                print("[\(String(format: "%.3f", timestamp))] Video[\(index)]: \(video.lastPathComponent)")
            }

            // If we couldn't find the current video in the list, it might be due to path differences
            if currentIndex == -1 && !videos.isEmpty {
                print("[\(String(format: "%.3f", timestamp))] Warning: Current video not found in scanned list. Checking for path match...")
                // Try to find by filename
                let currentFilename = url.lastPathComponent
                if let matchingIndex = videos.firstIndex(where: { $0.lastPathComponent == currentFilename }) {
                    currentIndex = matchingIndex
                    print("[\(String(format: "%.3f", timestamp))] Found match by filename at index \(currentIndex)")
                }
            }

        } catch {
            lastScanError = error
            print("[\(String(format: "%.3f", timestamp))] Error scanning directory: \(error)")
        }

        isScanning = false
        print("[\(String(format: "%.3f", timestamp))] Scanning completed. Final state: videos=\(videos.count), currentIndex=\(currentIndex)")
    }

    func navigateToNext() -> URL? {
        let timestamp = Date().timeIntervalSince1970
        print("[\(String(format: "%.3f", timestamp))] VideoDirectoryManager.navigateToNext() called")
        print("[\(String(format: "%.3f", timestamp))] Current state: videos.count=\(videos.count), currentIndex=\(currentIndex), hasNext=\(hasNext)")

        guard hasNext else {
            if videos.isEmpty {
                print("[\(String(format: "%.3f", timestamp))] No videos in directory - directory not yet scanned")
            } else if currentIndex >= videos.count - 1 {
                print("[\(String(format: "%.3f", timestamp))] Already at last video in directory")
            }
            return nil
        }

        currentIndex += 1
        print("[\(String(format: "%.3f", timestamp))] Updated currentIndex to \(currentIndex)")

        let video = currentVideo
        print("[\(String(format: "%.3f", timestamp))] Returning video: \(video?.lastPathComponent ?? "nil")")
        return video
    }

    func navigateToPrevious() -> URL? {
        let timestamp = Date().timeIntervalSince1970
        print("[\(String(format: "%.3f", timestamp))] VideoDirectoryManager.navigateToPrevious() called")
        print("[\(String(format: "%.3f", timestamp))] Current state: videos.count=\(videos.count), currentIndex=\(currentIndex), hasPrevious=\(hasPrevious)")

        guard hasPrevious else { 
            if videos.isEmpty {
                print("[\(String(format: "%.3f", timestamp))] No videos in directory - directory not yet scanned")
            } else if currentIndex <= 0 {
                print("[\(String(format: "%.3f", timestamp))] Already at first video in directory")
            }
            return nil 
        }

        currentIndex -= 1
        print("[\(String(format: "%.3f", timestamp))] Updated currentIndex to \(currentIndex)")

        let video = currentVideo
        print("[\(String(format: "%.3f", timestamp))] Returning video: \(video?.lastPathComponent ?? "nil")")
        return video
    }

    func setCurrentVideo(_ url: URL) {
        if let index = videos.firstIndex(of: url) {
            currentIndex = index
        }
    }

    func rescanCurrentDirectory() async {
        guard let current = currentVideo else { 
            print("VideoDirectoryManager: Cannot rescan - no current video")
            return 
        }
        print("VideoDirectoryManager: Rescanning directory for current video: \(current.lastPathComponent)")
        await scanDirectory(at: current)
    }

    func findFirstVideo(in directoryURL: URL) async -> URL? {
        // Start accessing security-scoped resource if needed
        var shouldStopAccessing = false
        if directoryURL.startAccessingSecurityScopedResource() {
            shouldStopAccessing = true
        }

        defer {
            if shouldStopAccessing {
                directoryURL.stopAccessingSecurityScopedResource()
            }
        }

        do {
            let fileManager = FileManager.default
            let resourceKeys: [URLResourceKey] = [.nameKey, .isDirectoryKey]
            let enumerator = fileManager.enumerator(
                at: directoryURL,
                includingPropertiesForKeys: resourceKeys,
                options: [.skipsHiddenFiles, .skipsSubdirectoryDescendants]
            )

            var videoFiles: [URL] = []

            while let fileURL = enumerator?.nextObject() as? URL {
                let resourceValues = try fileURL.resourceValues(forKeys: Set(resourceKeys))

                guard let isDirectory = resourceValues.isDirectory, !isDirectory else { continue }

                let fileExtension = fileURL.pathExtension.lowercased()
                if supportedExtensions.contains(fileExtension) {
                    videoFiles.append(fileURL)
                }
            }

            // Return first video sorted by name
            return videoFiles.sorted { $0.lastPathComponent < $1.lastPathComponent }.first

        } catch {
            print("Error finding videos in directory: \(error)")
            return nil
        }
    }

    func requestDirectoryAccess(for videoURL: URL) async -> Bool {
        let directoryURL = videoURL.deletingLastPathComponent()

        // Use NSOpenPanel to request directory access
        return await MainActor.run {
            let panel = NSOpenPanel()
            panel.title = "Grant Directory Access"
            panel.message = "Grant access to '\(directoryURL.lastPathComponent)' to enable video navigation with keyboard shortcuts."
            panel.prompt = "Grant Access"
            panel.canChooseFiles = false
            panel.canChooseDirectories = true
            panel.allowsMultipleSelection = false
            panel.directoryURL = directoryURL
            panel.canCreateDirectories = false

            if panel.runModal() == .OK,
               let selectedURL = panel.url,
               selectedURL == directoryURL {
                // Try to create a security-scoped bookmark
                do {
                    let bookmarkData = try selectedURL.bookmarkData(
                        options: [.withSecurityScope, .securityScopeAllowOnlyReadAccess],
                        includingResourceValuesForKeys: nil,
                        relativeTo: nil
                    )
                    // Store bookmark for future use (you might want to persist this)
                    UserDefaults.standard.set(bookmarkData, forKey: "DirectoryBookmark-\(directoryURL.path)")
                    return true
                } catch {
                    print("Failed to create bookmark: \(error)")
                    return false
                }
            }
            return false
        }
    }
}

extension VideoDirectoryManager {
    enum ScanError: LocalizedError {
        case permissionDenied
        case directoryNotFound

        var errorDescription: String? {
            switch self {
            case .permissionDenied:
                return "Cannot access the video's directory for navigation."
            case .directoryNotFound:
                return "Directory not found. The video's parent folder may have been moved or deleted."
            }
        }

        var recoverySuggestion: String? {
            switch self {
            case .permissionDenied:
                return "Try: 1) File > Grant Directory Access, or 2) File > Open and select the folder instead of a file."
            case .directoryNotFound:
                return "Try opening a video from a different location."
            }
        }
    }
}

