//
//  MarkerManagementView.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import SwiftUI
import AVFoundation

struct MarkerManagementView: View {
    @Environment(\.dismiss) var dismiss
    @ObservedObject var viewModel: VideoPlayerViewModel
    @State private var selectedMarker: Marker?
    @State private var labelsText: String = ""
    @State private var showingDeleteConfirmation = false
    @State private var markerToDelete: Marker?
    @State private var frameNumbers: [UUID: Int] = [:]

    var body: some View {
        VStack(spacing: 0) {
            // Title Bar
            HStack {
                Text("Manage Markers")
                    .font(.headline)
                Spacer()
                Text("\(viewModel.markers.count) markers")
                    .font(.caption)
                    .foregroundColor(.secondary)
                Spacer()
                Button {
                    dismiss()
                } label: {
                    Image(systemName: "xmark.circle.fill")
                        .foregroundColor(.secondary)
                }
                .buttonStyle(.plain)
            }
            .padding()
            .background(Color(NSColor.separatorColor).opacity(0.5))

            // Content
            VStack(spacing: 0) {
                // Marker List
                ScrollViewReader { proxy in
                    ScrollView {
                        VStack(spacing: 0) {
                            ForEach(sortedMarkers) { marker in
                                MarkerRow(
                                    marker: marker,
                                    isSelected: selectedMarker?.id == marker.id,
                                    isCurrent: isCurrentSegmentMarker(marker),
                                    frameNumber: frameNumbers[marker.id],
                                    onSelect: {
                                        selectMarker(marker)
                                        viewModel.seek(to: marker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
                                    },
                                    onDelete: {
                                        markerToDelete = marker
                                        showingDeleteConfirmation = true
                                    }
                                )
                                .id(marker.id)

                                if marker.id != sortedMarkers.last?.id {
                                    Divider()
                                        .padding(.leading, 16)
                                }
                            }
                        }
                        .padding(.vertical, 8)
                    }
                    .frame(height: 250)
                    .background(Color(NSColor.controlBackgroundColor))
                    .onChange(of: currentSegmentMarkerId) { _, newId in
                        if let id = newId {
                            withAnimation(.easeInOut(duration: 0.3)) {
                                proxy.scrollTo(id, anchor: .center)
                            }
                        }
                    }
                }

                Divider()

                // Edit Form
                if let marker = selectedMarker {
                    VStack(alignment: .leading, spacing: 16) {
                        // Selected marker info
                        HStack {
                            Text("Editing marker at \(marker.timestamp.timeString)")
                                .font(.subheadline)
                                .foregroundColor(.secondary)

                            if let frameNum = frameNumbers[marker.id] {
                                Text("Frame \(frameNum)")
                                    .font(.caption)
                                    .foregroundColor(.secondary)
                            }

                            Spacer()
                        }

                        // Rating
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Rating")
                                .font(.subheadline)
                                .foregroundColor(.secondary)

                            StarRatingView(rating: .init(
                                get: { selectedMarker?.rating ?? 3 },
                                set: { newRating in
                                    if var updatedMarker = selectedMarker {
                                        updatedMarker.rating = newRating
                                        selectedMarker = updatedMarker
                                    }
                                }
                            ))
                            .padding(.vertical, 4)
                        }

                        // Labels
                        VStack(alignment: .leading, spacing: 8) {
                            Text("Labels")
                                .font(.subheadline)
                                .foregroundColor(.secondary)

                            TextField("Enter labels separated by commas...", text: $labelsText)
                                .textFieldStyle(.roundedBorder)
                                .onSubmit {
                                    updateLabels()
                                }

                            Text("Separate multiple labels with commas")
                                .font(.caption)
                                .foregroundColor(.secondary)
                        }

                        // Save button
                        HStack {
                            Spacer()

                            Button("Save Changes") {
                                saveChanges()
                            }
                            .keyboardShortcut(.defaultAction)
                        }
                    }
                    .padding()
                    .background(Color(NSColor.windowBackgroundColor))
                } else {
                    // No marker selected
                    VStack {
                        Text("Select a marker to edit")
                            .font(.subheadline)
                            .foregroundColor(.secondary)
                            .padding(.vertical, 40)
                    }
                    .frame(maxWidth: .infinity)
                    .background(Color(NSColor.windowBackgroundColor))
                }
            }
        }
        .frame(width: 400, height: 650)
        .background(Color(NSColor.windowBackgroundColor))
        .onAppear {
            // Select the first marker or current segment marker on appear
            if selectedMarker == nil {
                if let currentMarker = sortedMarkers.first(where: { isCurrentSegmentMarker($0) }) {
                    selectMarker(currentMarker)
                } else if let firstMarker = sortedMarkers.first {
                    selectMarker(firstMarker)
                }
            }
        }
        .task {
            await loadFrameNumbers()
        }
        .alert("Delete Marker", isPresented: $showingDeleteConfirmation) {
            Button("Cancel", role: .cancel) { }
            Button("Delete", role: .destructive) {
                if let marker = markerToDelete {
                    deleteMarker(marker)
                }
            }
        } message: {
            Text("Are you sure you want to delete this marker?")
        }
    }

    // MARK: - Computed Properties

    var sortedMarkers: [Marker] {
        viewModel.markers.sorted()
    }

    var currentSegmentMarkerId: UUID? {
        viewModel.currentSegment?.startMarker?.id
    }

    // MARK: - Helper Methods

    private func isCurrentSegmentMarker(_ marker: Marker) -> Bool {
        marker.id == currentSegmentMarkerId
    }

    private func selectMarker(_ marker: Marker) {
        selectedMarker = marker
        labelsText = marker.labels.joined(separator: ", ")
    }

    private func updateLabels() {
        guard var marker = selectedMarker else { return }

        let labels = labelsText
            .split(separator: ",")
            .map { $0.trimmingCharacters(in: .whitespaces) }
            .filter { !$0.isEmpty }
        marker.labels = labels
        selectedMarker = marker
    }

    private func saveChanges() {
        guard let marker = selectedMarker else { return }
        updateLabels()
        viewModel.updateMarker(marker)
    }

    private func deleteMarker(_ marker: Marker) {
        viewModel.deleteMarker(marker)
        if selectedMarker?.id == marker.id {
            selectedMarker = nil
            labelsText = ""
        }
    }

    private func loadFrameNumbers() async {
        guard let asset = viewModel.player?.currentItem?.asset else { return }

        do {
            let tracks = try await asset.loadTracks(withMediaType: .video)
            if let track = tracks.first {
                let fps = try await track.load(.nominalFrameRate)
                await MainActor.run {
                    // Calculate frame numbers for all markers
                    var numbers: [UUID: Int] = [:]
                    for marker in sortedMarkers {
                        numbers[marker.id] = Int(marker.timestamp.seconds * Double(fps))
                    }
                    self.frameNumbers = numbers
                }
            }
        } catch {
            print("Error loading track info: \(error)")
        }
    }
}

// MARK: - MarkerRow

struct MarkerRow: View {
    let marker: Marker
    let isSelected: Bool
    let isCurrent: Bool
    let frameNumber: Int?
    let onSelect: () -> Void
    let onDelete: () -> Void

    private var backgroundColor: Color {
        if isCurrent {
            return Color.accentColor.opacity(0.3)
        } else if isSelected {
            return Color.accentColor.opacity(0.1)
        } else {
            return Color.clear
        }
    }

    private var borderColor: Color {
        if isCurrent {
            return Color.accentColor
        } else {
            return Color.clear
        }
    }

    var body: some View {
        HStack(spacing: 12) {
            // Timestamp
            Text(marker.timestamp.timeString)
                .font(.system(.body, design: .monospaced))
                .foregroundColor(isCurrent ? Color.accentColor : Color(NSColor.labelColor))
                .frame(width: 80, alignment: .leading)

            // Frame number
            if let frameNum = frameNumber {
                Text("F\(frameNum)")
                    .font(.caption)
                    .foregroundColor(Color(NSColor.secondaryLabelColor))
                    .frame(width: 60, alignment: .leading)
            } else {
                Color.clear.frame(width: 60)
            }

            // Rating stars
            HStack(spacing: 2) {
                ForEach(0..<5) { index in
                    Image(systemName: index < marker.rating ? "star.fill" : "star")
                        .font(.system(size: 11))
                        .foregroundColor(.yellow)
                }
            }

            // Labels
            if !marker.labels.isEmpty {
                Text(marker.labels.joined(separator: ", "))
                    .font(.system(size: 11))
                    .foregroundColor(Color(NSColor.secondaryLabelColor))
                    .lineLimit(1)
                    .truncationMode(.tail)
            }

            Spacer()

            // Delete button
            Button {
                onDelete()
            } label: {
                Image(systemName: "xmark.circle.fill")
                    .font(.system(size: 14))
                    .foregroundColor(Color(NSColor.tertiaryLabelColor))
                    .opacity(0.6)
            }
            .buttonStyle(.plain)
            .help("Delete marker")
        }
        .padding(.horizontal, 16)
        .padding(.vertical, 8)
        .background(backgroundColor)
        .overlay(
            RoundedRectangle(cornerRadius: 4)
                .stroke(borderColor, lineWidth: 2)
        )
        .contentShape(Rectangle())
        .onTapGesture(perform: onSelect)
    }
}

//
//  PerformanceOverlay.swift
//  SwiftMedia
//
//  Created by Claude on 6/25/25.
//

import SwiftUI

struct PerformanceOverlay: View {
    @ObservedObject var monitor: PerformanceMonitor
    @ObservedObject var analytics: PerformanceAnalytics
    @State private var showDetails = false
    @State private var position: CGPoint = CGPoint(x: 20, y: 100)
    @State private var isDragging = false

    var body: some View {
        GeometryReader { geometry in
            VStack(alignment: .leading, spacing: 8) {
                // Header with drag handle
                HStack {
                    Image(systemName: "speedometer")
                        .font(.caption)

                    Text("Performance Monitor")
                        .font(.caption.weight(.medium))

                    Spacer()

                    // Quick stats
                    if let metrics = monitor.currentMetrics {
                        PerformanceScoreBadge(score: metrics.performanceScore)
                    }

                    Button(action: { showDetails.toggle() }) {
                        Image(systemName: showDetails ? "chevron.up" : "chevron.down")
                            .font(.caption)
                    }
                }
                .padding(.horizontal, 12)
                .padding(.vertical, 6)
                .background(Color.black.opacity(0.3))
                .contentShape(Rectangle())
                .onDrag {
                    isDragging = true
                    return NSItemProvider()
                }

                // Compact metrics
                HStack(spacing: 16) {
                    MetricPill(
                        icon: "cpu",
                        value: monitor.cpuUsage,
                        label: "CPU",
                        threshold: 40,
                        showGraph: true,
                        graphData: monitor.cpuHistory
                    )

                    MetricPill(
                        icon: "memorychip",
                        value: monitor.memoryUsage,
                        label: "Memory",
                        threshold: 80,
                        showGraph: true,
                        graphData: monitor.memoryHistory
                    )

                    if monitor.droppedFrames > 0 {
                        HStack(spacing: 4) {
                            Image(systemName: "exclamationmark.triangle.fill")
                                .font(.caption)
                                .foregroundColor(.red)
                            Text("\(monitor.droppedFrames)")
                                .font(.caption.monospacedDigit())
                            Text("drops")
                                .font(.caption2)
                                .foregroundColor(.secondary)
                        }
                    }
                }
                .padding(.horizontal, 12)
                .padding(.bottom, 8)

                // Detailed view
                if showDetails {
                    VStack(spacing: 12) {
                        // Performance graphs
                        PerformanceGraph(
                            data: monitor.cpuHistory,
                            label: "CPU Usage",
                            threshold: 40,
                            color: .blue
                        )

                        PerformanceGraph(
                            data: monitor.memoryHistory,
                            label: "Memory Usage",
                            threshold: 80,
                            color: .purple
                        )

                        PerformanceGraph(
                            data: monitor.frameRateHistory,
                            label: "Frame Rate",
                            threshold: 59,
                            color: .green
                        )

                        // Frame stability
                        HStack {
                            Label("Frame Stability", systemImage: "waveform.path.ecg")
                                .font(.caption)

                            Spacer()

                            Text("\(Int(monitor.frameRateStability))%")
                                .font(.caption.monospacedDigit())
                                .foregroundColor(monitor.frameRateStability > 95 ? .green : .orange)
                        }

                        // Trends
                        if let trends = analytics.performanceTrends {
                            HStack(spacing: 16) {
                                TrendIndicator(label: "CPU", trend: trends.cpuTrend)
                                TrendIndicator(label: "Memory", trend: trends.memoryTrend)
                            }
                        }

                        Divider()

                        // Actions
                        HStack {
                            Button(action: exportReport) {
                                Label("Export", systemImage: "square.and.arrow.up")
                                    .font(.caption)
                            }

                            Spacer()

                            if monitor.isMonitoring {
                                Button(action: { analytics.reset() }) {
                                    Label("Reset", systemImage: "arrow.clockwise")
                                        .font(.caption)
                                }
                            }
                        }
                    }
                    .padding(12)
                    .transition(.asymmetric(
                        insertion: .move(edge: .top).combined(with: .opacity),
                        removal: .move(edge: .top).combined(with: .opacity)
                    ))
                }
            }
            .background(Color.black.opacity(0.85))
            .cornerRadius(8)
            .shadow(radius: 10)
            .frame(width: showDetails ? 320 : 280)
            .position(position)
            .gesture(
                DragGesture()
                    .onChanged { value in
                        position = CGPoint(
                            x: min(max(160, value.location.x + value.translation.width), geometry.size.width - 160),
                            y: min(max(50, value.location.y + value.translation.height), geometry.size.height - 50)
                        )
                    }
            )
            .animation(.spring(response: 0.3), value: showDetails)
        }
    }

    private func exportReport() {
        guard let data = analytics.exportData() else { return }

        let savePanel = NSSavePanel()
        savePanel.allowedContentTypes = [.json]
        savePanel.nameFieldStringValue = "performance-report.json"

        savePanel.begin { response in
            if response == .OK, let url = savePanel.url {
                try? data.write(to: url)
            }
        }
    }
}

struct MetricPill: View {
    let icon: String
    let value: Double
    let label: String
    let threshold: Double
    let showGraph: Bool
    let graphData: [Double]

    var isWarning: Bool { value > threshold }

    var body: some View {
        HStack(spacing: 6) {
            Image(systemName: icon)
                .font(.caption)
                .foregroundColor(isWarning ? .orange : .green)

            VStack(alignment: .leading, spacing: 2) {
                Text(String(format: "%.1f%%", value))
                    .font(.caption.monospacedDigit())
                    .foregroundColor(isWarning ? .orange : .primary)

                Text(label)
                    .font(.caption2)
                    .foregroundColor(.secondary)
            }

            if showGraph && !graphData.isEmpty {
                MiniPerformanceGraph(
                    data: Array(graphData.suffix(20)),
                    color: isWarning ? .orange : .green,
                    size: CGSize(width: 40, height: 20)
                )
            }
        }
        .padding(.horizontal, 8)
        .padding(.vertical, 4)
        .background(isWarning ? Color.orange.opacity(0.1) : Color.green.opacity(0.1))
        .cornerRadius(6)
    }
}

struct PerformanceScoreBadge: View {
    let score: Double

    var level: PerformanceLevel { PerformanceLevel(score: score) }

    var body: some View {
        HStack(spacing: 4) {
            Circle()
                .fill(Color(level.color))
                .frame(width: 6, height: 6)

            Text(String(format: "%.0f", score))
                .font(.caption.monospacedDigit())
        }
        .padding(.horizontal, 8)
        .padding(.vertical, 2)
        .background(Color(level.color).opacity(0.2))
        .cornerRadius(10)
    }
}

struct TrendIndicator: View {
    let label: String
    let trend: Trend

    var body: some View {
        VStack(spacing: 2) {
            Text(label)
                .font(.caption2)
                .foregroundColor(.secondary)

            Text(trend.description)
                .font(.caption.monospacedDigit())
                .foregroundColor(trendColor)
        }
    }

    var trendColor: Color {
        switch trend {
        case .stable:
            return .primary
        case .increasing:
            return .orange
        case .decreasing:
            return .green
        }
    }
}

