//
//  ScrubbingTabView.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import SwiftUI

struct ScrubbingTabView: View {
    @ObservedObject var viewModel: SettingsViewModel
    @State private var selectedConfigIndex: Int = 0
    @State private var showingConflictAlert = false
    @State private var conflictMessage = ""

    var body: some View {
        VStack(alignment: .leading, spacing: 20) {
            HStack {
                Text("Scrubbing Configurations")
                    .font(.headline)
                Spacer()

                // Action buttons
                HStack(spacing: 12) {
                    Button("Cancel") {
                        viewModel.cancelChanges()
                    }
                    .buttonStyle(.borderless)
                    .disabled(!viewModel.hasUnsavedChanges)

                    Button("Reset to Defaults") {
                        viewModel.resetScrubbingToDefaults()
                    }
                    .buttonStyle(.borderless)

                    Button(action: addNewConfiguration) {
                        Label("Add Configuration", systemImage: "plus")
                    }
                    .buttonStyle(.borderless)

                    Button("Save") {
                        if viewModel.hasShortcutConflicts().isEmpty {
                            viewModel.saveSettings()
                        } else {
                            showConflictAlert()
                        }
                    }
                    .buttonStyle(.borderedProminent)
                    .disabled(!viewModel.hasUnsavedChanges)
                }
            }
            .padding(.top)

            // Configuration Table
            VStack(spacing: 0) {
                // Header
                HStack(spacing: 0) {
                    Text("Name")
                        .frame(width: 120, alignment: .leading)
                        .padding(.horizontal, 12)

                    Text("Backward")
                        .frame(width: 100, alignment: .center)

                    Text("Forward")
                        .frame(width: 100, alignment: .center)

                    Text("Playing (sec)")
                        .frame(width: 100, alignment: .center)

                    Text("Paused (frames)")
                        .frame(width: 110, alignment: .center)

                    Spacer()
                        .frame(width: 40)
                }
                .font(.caption)
                .fontWeight(.semibold)
                .foregroundColor(.secondary)
                .padding(.vertical, 8)
                .background(Color(NSColor.separatorColor).opacity(0.3))

                Divider()

                // Rows
                ScrollView {
                    VStack(spacing: 0) {
                        ForEach(viewModel.tempScrubbingConfigs.indices, id: \.self) { index in
                            ScrubbingConfigRow(
                                config: $viewModel.tempScrubbingConfigs[index],
                                onDelete: {
                                    deleteConfiguration(at: index)
                                },
                                onChanged: { viewModel.markAsChanged() }
                            )

                            if index < viewModel.tempScrubbingConfigs.count - 1 {
                                Divider()
                            }
                        }
                    }
                }
                .frame(maxHeight: 200)
            }
            .background(Color(NSColor.controlBackgroundColor))
            .cornerRadius(8)

            if viewModel.hasUnsavedChanges {
                Text("You have unsaved changes. Click Save to apply them.")
                    .font(.caption)
                    .foregroundColor(.orange)
            } else {
                Text("Configure different scrubbing behaviors for various editing workflows. " +
                     "The active configuration will be used for arrow key navigation.")
                    .font(.caption)
                    .foregroundColor(.secondary)
            }

            Spacer()
        }
        .padding()
        .onChange(of: viewModel.tempScrubbingConfigs) { _, _ in
            checkForConflicts()
        }
        .alert("Keyboard Shortcut Conflict", isPresented: $showingConflictAlert) {
            Button("OK") { }
        } message: {
            Text(conflictMessage)
        }
    }

    private func checkForConflicts() {
        // Check internal scrubbing conflicts
        let internalConflicts = ScrubbingConfigValidator.hasConflicts(viewModel.tempScrubbingConfigs)
        let internalMessages = internalConflicts.map { "\($0.0) and \($0.1) have conflicting shortcuts" }

        // Check global shortcut conflicts
        let globalConflicts = viewModel.hasShortcutConflicts()

        // Combine all conflicts
        let allConflicts = internalMessages + globalConflicts

        if !allConflicts.isEmpty {
            conflictMessage = allConflicts.joined(separator: "\n")
            showingConflictAlert = true
        }
    }

    private func showConflictAlert() {
        checkForConflicts()
    }

    private func addNewConfiguration() {
        let newConfig = ScrubbingConfig(
            name: "Custom \(viewModel.tempScrubbingConfigs.count + 1)",
            backwardKey: "â†",
            forwardKey: "â†’",
            playingSeekSeconds: 5.0,
            pausedSeekFrames: 1
        )
        viewModel.tempScrubbingConfigs.append(newConfig)
        viewModel.markAsChanged()
    }

    private func deleteConfiguration(at index: Int) {
        guard viewModel.tempScrubbingConfigs.count > 1 else { return }

        viewModel.tempScrubbingConfigs.remove(at: index)
        viewModel.markAsChanged()
    }
}

struct ScrubbingConfigRow: View {
    @Binding var config: ScrubbingConfig
    let onDelete: () -> Void
    let onChanged: () -> Void
    @State private var showingValidationError = false
    @State private var validationErrors: [String] = []

    var body: some View {
        HStack(spacing: 0) {
            TextField("Name", text: $config.name)
                .textFieldStyle(.plain)
                .frame(width: 120)
                .padding(.horizontal, 12)
                .onChange(of: config.name) { _, _ in onChanged() }

            KeyboardShortcutRecorder(shortcut: $config.backwardKey)
                .frame(width: 100)
                .onChange(of: config.backwardKey) { _, _ in onChanged() }

            KeyboardShortcutRecorder(shortcut: $config.forwardKey)
                .frame(width: 100)
                .onChange(of: config.forwardKey) { _, _ in onChanged() }

            HStack {
                TextField("", value: $config.playingSeekSeconds, format: .number.precision(.fractionLength(1)))
                    .textFieldStyle(.roundedBorder)
                    .frame(width: 60)
                    .multilineTextAlignment(.center)
                    .onSubmit {
                        validateConfig()
                    }
                    .onChange(of: config.playingSeekSeconds) { _, _ in onChanged() }
            }
            .frame(width: 100)

            HStack {
                TextField("", value: $config.pausedSeekFrames, format: .number)
                    .textFieldStyle(.roundedBorder)
                    .frame(width: 60)
                    .multilineTextAlignment(.center)
                    .onSubmit {
                        validateConfig()
                    }
                    .onChange(of: config.pausedSeekFrames) { _, _ in onChanged() }
            }
            .frame(width: 110)

            Button(action: onDelete) {
                Image(systemName: "trash")
                    .foregroundColor(.secondary)
            }
            .buttonStyle(.plain)
            .frame(width: 40)
        }
        .padding(.vertical, 8)
        .onChange(of: config) { _, _ in
            validateConfig()
        }
        .alert("Configuration Error", isPresented: $showingValidationError) {
            Button("OK") { }
        } message: {
            Text(validationErrors.joined(separator: "\n"))
        }
    }

    private func validateConfig() {
        validationErrors = ScrubbingConfigValidator.validate(config)
        showingValidationError = !validationErrors.isEmpty
    }
}

//
//  PerformanceAnalytics.swift
//  SwiftMedia
//
//  Created by Claude on 6/25/25.
//

import Foundation
import os.log

class PerformanceAnalytics: ObservableObject {
    static let shared = PerformanceAnalytics()

    @Published var metricsHistory: [PerformanceMetrics] = []
    @Published var averageMetrics: PerformanceMetrics?
    @Published var performanceTrends: PerformanceTrends?

    private let logger = Logger(subsystem: "com.swiftmedia", category: "Analytics")
    private let maxHistorySize = 300 // 5 minutes at 1 sample/second
    private var adaptiveOptimizationEnabled = true

    private init() {}

    func record(_ metrics: PerformanceMetrics) {
        metricsHistory.append(metrics)

        // Maintain history size
        if metricsHistory.count > maxHistorySize {
            metricsHistory.removeFirst()
        }

        // Update analytics
        updateAverages()
        analyzeTrends()

        // Apply adaptive optimizations if needed
        if adaptiveOptimizationEnabled {
            applyAdaptiveOptimizations(metrics)
        }
    }

    private func updateAverages() {
        guard metricsHistory.count > 10 else { return }

        let recentMetrics = Array(metricsHistory.suffix(30))

        let avgCPU = recentMetrics.map { $0.cpuUsage }.reduce(0, +) / Double(recentMetrics.count)
        let avgMemory = recentMetrics.map { $0.memoryUsage }.reduce(0, +) / Double(recentMetrics.count)
        let avgFrameRate = recentMetrics.map { $0.frameRate }.reduce(0, +) / Double(recentMetrics.count)
        let avgStability = recentMetrics.map { $0.frameRateStability }.reduce(0, +) / Double(recentMetrics.count)
        let totalDrops = recentMetrics.map { $0.droppedFrames }.max() ?? 0

        averageMetrics = PerformanceMetrics(
            timestamp: Date(),
            cpuUsage: avgCPU,
            memoryUsage: avgMemory,
            droppedFrames: totalDrops,
            frameRate: avgFrameRate,
            frameRateStability: avgStability,
            gpuUsage: nil,
            decoderEfficiency: nil
        )
    }

    private func analyzeTrends() {
        guard metricsHistory.count > 60 else { return }

        let oldMetrics = Array(metricsHistory.prefix(30))
        let newMetrics = Array(metricsHistory.suffix(30))

        let oldAvgCPU = oldMetrics.map { $0.cpuUsage }.reduce(0, +) / Double(oldMetrics.count)
        let newAvgCPU = newMetrics.map { $0.cpuUsage }.reduce(0, +) / Double(newMetrics.count)

        let oldAvgMemory = oldMetrics.map { $0.memoryUsage }.reduce(0, +) / Double(oldMetrics.count)
        let newAvgMemory = newMetrics.map { $0.memoryUsage }.reduce(0, +) / Double(newMetrics.count)

        performanceTrends = PerformanceTrends(
            cpuTrend: calculateTrend(old: oldAvgCPU, new: newAvgCPU),
            memoryTrend: calculateTrend(old: oldAvgMemory, new: newAvgMemory),
            isImproving: newAvgCPU < oldAvgCPU && newAvgMemory < oldAvgMemory
        )
    }

    private func calculateTrend(old: Double, new: Double) -> Trend {
        let change = new - old
        let percentChange = (change / old) * 100

        if abs(percentChange) < 5 {
            return .stable
        } else if percentChange > 0 {
            return .increasing(percentChange)
        } else {
            return .decreasing(abs(percentChange))
        }
    }

    private func applyAdaptiveOptimizations(_ metrics: PerformanceMetrics) {
        // High CPU usage optimization
        if metrics.cpuUsage > 60 {
            logger.warning("High CPU usage detected: \(String(format: "%.1f", metrics.cpuUsage))%")
            NotificationCenter.default.post(
                name: .performanceOptimizationNeeded,
                object: nil,
                userInfo: ["type": "cpu", "level": "high"]
            )
        }

        // Memory pressure optimization
        if metrics.memoryUsage > 80 {
            logger.warning("High memory usage detected: \(String(format: "%.1f", metrics.memoryUsage))%")
            NotificationCenter.default.post(
                name: .performanceOptimizationNeeded,
                object: nil,
                userInfo: ["type": "memory", "level": "high"]
            )
        }

        // Frame drop optimization
        if metrics.droppedFrames > 10 {
            logger.error("Excessive frame drops detected: \(metrics.droppedFrames)")
            NotificationCenter.default.post(
                name: .performanceOptimizationNeeded,
                object: nil,
                userInfo: ["type": "frames", "level": "critical"]
            )
        }
    }

    func generateReport() -> PerformanceReport {
        let report = PerformanceReport(
            timestamp: Date(),
            sessionDuration: calculateSessionDuration(),
            averageMetrics: averageMetrics,
            peakCPU: metricsHistory.map { $0.cpuUsage }.max() ?? 0,
            peakMemory: metricsHistory.map { $0.memoryUsage }.max() ?? 0,
            totalDroppedFrames: metricsHistory.map { $0.droppedFrames }.max() ?? 0,
            performanceScore: averageMetrics?.performanceScore ?? 0,
            recommendations: generateRecommendations()
        )

        logger.info("Generated performance report with score: \(String(format: "%.1f", report.performanceScore))")
        return report
    }

    private func calculateSessionDuration() -> TimeInterval {
        guard let first = metricsHistory.first?.timestamp,
              let last = metricsHistory.last?.timestamp else { return 0 }
        return last.timeIntervalSince(first)
    }

    private func generateRecommendations() -> [String] {
        var recommendations: [String] = []

        if let avg = averageMetrics {
            if avg.cpuUsage > 40 {
                recommendations.append("Consider reducing video quality or enabling hardware acceleration")
            }

            if avg.memoryUsage > 70 {
                recommendations.append("Close unnecessary applications to free up memory")
            }

            if avg.droppedFrames > 5 {
                recommendations.append("Ensure your storage device has sufficient read speed")
            }

            if avg.frameRateStability < 95 {
                recommendations.append("Check for background processes that may be affecting performance")
            }
        }

        return recommendations
    }

    func exportData() -> Data? {
        let encoder = JSONEncoder()
        encoder.outputFormatting = .prettyPrinted
        encoder.dateEncodingStrategy = .iso8601

        do {
            return try encoder.encode(generateReport())
        } catch {
            logger.error("Failed to export performance data: \(error)")
            return nil
        }
    }

    func reset() {
        metricsHistory.removeAll()
        averageMetrics = nil
        performanceTrends = nil
        logger.info("Performance analytics reset")
    }
}

// Supporting types
struct PerformanceTrends {
    let cpuTrend: Trend
    let memoryTrend: Trend
    let isImproving: Bool
}

enum Trend {
    case stable
    case increasing(Double)
    case decreasing(Double)

    var description: String {
        switch self {
        case .stable:
            return "Stable"
        case .increasing(let percent):
            return "â†‘ \(String(format: "%.1f", percent))%"
        case .decreasing(let percent):
            return "â†“ \(String(format: "%.1f", percent))%"
        }
    }
}

struct PerformanceReport: Codable {
    let timestamp: Date
    let sessionDuration: TimeInterval
    let averageMetrics: PerformanceMetrics?
    let peakCPU: Double
    let peakMemory: Double
    let totalDroppedFrames: Int
    let performanceScore: Double
    let recommendations: [String]
}

// Notification names
extension Notification.Name {
    static let performanceOptimizationNeeded = Notification.Name("performanceOptimizationNeeded")
}

