//
//  ContentView.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import SwiftUI
import AVKit
import UniformTypeIdentifiers

struct ContentView: View {
    @EnvironmentObject var viewModel: VideoPlayerViewModel
    @StateObject private var settingsViewModel = SettingsViewModel()
    @StateObject private var directoryManager = VideoDirectoryManager()

    @State private var showingSettings = false
    @State private var showingMarkerDetails = false
    @State private var selectedMarkerForEdit: Marker?
    @State private var showingError = false
    @State private var errorMessage = ""
    @State private var showingMarkerList = false

    var body: some View {
        ZStack {
            DesignSystem.Colors.background
                .ignoresSafeArea()

            // Keyboard handling layer
            KeyboardHandlingView(
                viewModel: viewModel,
                settingsViewModel: settingsViewModel,
                showingSettings: $showingSettings,
                selectedMarkerForEdit: $selectedMarkerForEdit,
                showingMarkerList: $showingMarkerList,
                onNavigateNext: navigateToNextVideo,
                onNavigatePrevious: navigateToPreviousVideo,
                onOpenFile: openFilePicker
            )

            if viewModel.videoURL != nil {
                HStack(spacing: 0) {
                    // Marker/Segment list sidebar
                    if showingMarkerList {
                        MarkerSegmentListContainer(
                            viewModel: viewModel,
                            selectedMarkerForEdit: $selectedMarkerForEdit
                        )
                    }

                    // Main video content
                    ZStack(alignment: .bottom) {
                        VideoPlayerView(player: viewModel.player)
                            .ignoresSafeArea()

                        // Progress bar overlay
                        ProgressBarView(viewModel: viewModel, selectedMarkerForEdit: $selectedMarkerForEdit)

                    // Scrubbing feedback overlay
                    ScrubbingFeedbackOverlay(viewModel: viewModel)

                    // Video position indicator
                    if !directoryManager.videos.isEmpty && directoryManager.currentIndex >= 0 {
                        VStack {
                            HStack {
                                Spacer()
                                HStack(spacing: 8) {
                                    if directoryManager.isScanning {
                                        ProgressView()
                                            .scaleEffect(0.7)
                                    }
                                    Text(directoryManager.positionDescription)
                                        .font(.caption)
                                        .foregroundColor(DesignSystem.Colors.textSecondary)
                                }
                                .padding(.horizontal, 12)
                                .padding(.vertical, 6)
                                .background(DesignSystem.Colors.surfaceSecondary.opacity(0.8))
                                .cornerRadius(6)
                                .padding(DesignSystem.Spacing.md)
                            }
                            Spacer()
                        }
                    } else if directoryManager.isScanning {
                        VStack {
                            HStack {
                                Spacer()
                                HStack(spacing: 8) {
                                    ProgressView()
                                        .scaleEffect(0.7)
                                    Text("Scanning directory...")
                                        .font(.caption)
                                        .foregroundColor(DesignSystem.Colors.textSecondary)
                                }
                                .padding(.horizontal, 12)
                                .padding(.vertical, 6)
                                .background(DesignSystem.Colors.surfaceSecondary.opacity(0.8))
                                .cornerRadius(6)
                                .padding(DesignSystem.Spacing.md)
                            }
                            Spacer()
                        }
                    }

                    // Performance overlay
                    if viewModel.showPerformanceOverlay {
                        PerformanceOverlay(
                            monitor: PerformanceMonitor.shared,
                            analytics: PerformanceAnalytics.shared
                        )
                    }

                    }

                    // Error banner overlay
                    if let error = viewModel.playerError {
                        VStack {
                            ErrorBanner(error: error) {
                                viewModel.playerError = nil
                            }
                            .padding(DesignSystem.Spacing.md)

                            Spacer()
                        }
                    }
                }
            } else {
                VStack(spacing: DesignSystem.Spacing.lg) {
                    Image(systemName: "video.fill")
                        .font(.system(size: 64))
                        .foregroundColor(DesignSystem.Colors.textTertiary)

                    Text("Open a video file to begin")
                        .font(DesignSystem.Typography.title)
                        .foregroundColor(DesignSystem.Colors.textSecondary)

                    Button("Open Video...") {
                        openFilePicker()
                    }
                    .buttonStyle(PrimaryButtonStyle())
                }
            }
        }
        .sheet(isPresented: $showingSettings) {
            SettingsView()
                .environmentObject(settingsViewModel)
        }
        .sheet(item: $selectedMarkerForEdit) { _ in
            MarkerManagementView(viewModel: viewModel)
                .frame(width: 400, height: 650)
        }
        .task {
            setupManagers()
        }
        .onChange(of: settingsViewModel.scrubbingConfigs) {
            // Keep scrubbing settings in sync
            viewModel.scrubbingSettings = ScrubbingSettings(configs: settingsViewModel.scrubbingConfigs)
        }
        .alert("Error", isPresented: $showingError) {
            Button("OK") { }
        } message: {
            Text(errorMessage)
        }
        .onReceive(NotificationCenter.default.publisher(for: .openVideoFile)) { _ in
            openFilePicker()
        }
        .onReceive(NotificationCenter.default.publisher(for: .grantDirectoryAccess)) { _ in
            Task {
                await handleGrantDirectoryAccess()
            }
        }
        .onChange(of: viewModel.videoURL) { _, newURL in
            // Scan directory whenever a new video is loaded
            if let url = newURL {
                Task {
                    print("ContentView: Video URL changed, starting directory scan for: \(url.lastPathComponent)")
                    await directoryManager.scanDirectory(at: url)
                    print("ContentView: Directory scan completed. Found \(directoryManager.videos.count) videos, current index: \(directoryManager.currentIndex)")

                    // Handle scan failure
                    if directoryManager.videos.isEmpty && directoryManager.lastScanError != nil {
                        // If scan failed due to permissions, automatically request access
                        if let error = directoryManager.lastScanError as? VideoDirectoryManager.ScanError,
                           error == .permissionDenied {
                            print("ContentView: Directory scan failed due to permissions, requesting access...")
                            let granted = await directoryManager.requestDirectoryAccess(for: url)
                            if granted {
                                print("ContentView: Directory access granted, retrying scan...")
                                // Retry scan after permission granted
                                await directoryManager.scanDirectory(at: url)
                                if directoryManager.videos.isEmpty {
                                    // Still failed after permission grant
                                    errorMessage = "Failed to scan directory: \(directoryManager.lastScanError?.localizedDescription ?? "Unknown error")"
                                    showingError = true
                                }
                            } else {
                                errorMessage = "Directory access denied. Use File > Grant Directory Access to enable navigation."
                                showingError = true
                            }
                        } else {

