// MARK: - Video Navigation
private func navigateToNextVideo() {
    // Check if a video is loaded first
    guard let currentVideoURL = viewModel.videoURL else {
        errorMessage = "No video loaded. Open a video file to use directory navigation."
        showingError = true
        return
    }

    // Check if directory scanning is in progress
    if directoryManager.isScanning {
        errorMessage = "Directory scanning in progress. Please wait..."
        showingError = true
        return
    }

    // Check if directory has been scanned
    if directoryManager.videos.isEmpty {
        if let scanError = directoryManager.lastScanError {
            errorMessage = "Directory scan failed: \(scanError.localizedDescription)"
        } else if directoryManager.isScanning {
            errorMessage = "Directory is currently being scanned. Please wait..."
        } else {
            // Attempt to scan directory if not done yet
            errorMessage = "Scanning directory..."
            showingError = true
            Task {
                await directoryManager.scanDirectory(at: currentVideoURL)
                if !directoryManager.videos.isEmpty {
                    showingError = false
                    navigateToNextVideo() // Retry after scan
                } else if directoryManager.lastScanError != nil {
                    // If scan failed due to permissions, offer to request access
                    if let error = directoryManager.lastScanError as? VideoDirectoryManager.ScanError,
                       error == .permissionDenied {
                        let granted = await directoryManager.requestDirectoryAccess(for: currentVideoURL)
                        if granted {
                            // Retry scan after permission granted
                            await directoryManager.scanDirectory(at: currentVideoURL)
                            if !directoryManager.videos.isEmpty {
                                showingError = false
                                navigateToNextVideo()
                            }
                        }
                    }
                }
            }
            return
        }
        showingError = true
        return
    }

    if let nextVideoURL = directoryManager.navigateToNext() {
        viewModel.loadVideo(url: nextVideoURL)
        Task {
            await directoryManager.rescanCurrentDirectory()
        }
    } else {
        errorMessage = "Already at the last video in directory (\(directoryManager.currentIndex + 1) of \(directoryManager.videos.count))"
        showingError = true
    }
}

private func navigateToPreviousVideo() {
    let timestamp = Date().timeIntervalSince1970
    print("[\(String(format: "%.3f", timestamp))] navigateToPreviousVideo() called")

    // Check if a video is loaded first
    guard let currentVideoURL = viewModel.videoURL else {
        print("[\(String(format: "%.3f", timestamp))] No video loaded - cannot navigate")
        errorMessage = "No video loaded. Open a video file to use directory navigation."
        showingError = true
        return
    }

    // Check if directory scanning is in progress
    if directoryManager.isScanning {
        errorMessage = "Directory scanning in progress. Please wait..."
        showingError = true
        return
    }

    // Check if directory has been scanned
    if directoryManager.videos.isEmpty {
        if let scanError = directoryManager.lastScanError {
            errorMessage = "Directory scan failed: \(scanError.localizedDescription)"
        } else if directoryManager.isScanning {
            errorMessage = "Directory is currently being scanned. Please wait..."
        } else {
            // Attempt to scan directory if not done yet
            errorMessage = "Scanning directory..."
            showingError = true
            Task {
                await directoryManager.scanDirectory(at: currentVideoURL)
                if !directoryManager.videos.isEmpty {
                    showingError = false
                    navigateToPreviousVideo() // Retry after scan
                } else if directoryManager.lastScanError != nil {
                    // If scan failed due to permissions, offer to request access
                    if let error = directoryManager.lastScanError as? VideoDirectoryManager.ScanError,
                       error == .permissionDenied {
                        let granted = await directoryManager.requestDirectoryAccess(for: currentVideoURL)
                        if granted {
                            // Retry scan after permission granted
                            await directoryManager.scanDirectory(at: currentVideoURL)
                            if !directoryManager.videos.isEmpty {
                                showingError = false
                                navigateToPreviousVideo()
                            }
                        }
                    }
                }
            }
            return
        }
        showingError = true
        return
    }

    print("[\(String(format: "%.3f", timestamp))] Directory manager state: videos=\(directoryManager.videos.count), currentIndex=\(directoryManager.currentIndex), hasPrevious=\(directoryManager.hasPrevious)")

    if let previousVideoURL = directoryManager.navigateToPrevious() {
        print("[\(String(format: "%.3f", timestamp))] Got previous video URL: \(previousVideoURL.lastPathComponent)")
        print("[\(String(format: "%.3f", timestamp))] Loading video...")
        viewModel.loadVideo(url: previousVideoURL)
        Task {
            print("[\(String(format: "%.3f", timestamp))] Rescanning directory...")
            await directoryManager.rescanCurrentDirectory()
        }
    } else {
        print("[\(String(format: "%.3f", timestamp))] No previous video available")
        errorMessage = "Already at the first video in directory (\(directoryManager.currentIndex + 1) of \(directoryManager.videos.count))"
        showingError = true
    }

