   461â†’    func jumpToNextMarker() {
   462â†’        let currentTime = currentTime
   463â†’        
   464â†’        // Ensure cache is up to date
   465â†’        updateMarkersCache()
   466â†’
   467â†’        // Use cached sorted markers for O(log n) lookup
   468â†’        let index = sortedMarkersCache.binarySearch { marker in
   469â†’            CMTimeCompare(marker.timestamp, currentTime) <= 0
   470â†’        }
   471â†’
   472â†’        if index < sortedMarkersCache.count {
   473â†’            let nextMarker = sortedMarkersCache[index]
   474â†’            seek(to: nextMarker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
   475â†’            selectedMarker = nextMarker
   476â†’        } else if !sortedMarkersCache.isEmpty {
   477â†’            // Wrap to first marker
   478â†’            let firstMarker = sortedMarkersCache[0]
   479â†’            seek(to: firstMarker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
   480â†’            selectedMarker = firstMarker
   481â†’        }
   482â†’    }
   483â†’
   484â†’    func jumpToPreviousMarker() {
   485â†’        let currentTime = currentTime
   486â†’        
   487â†’        // Ensure cache is up to date
   488â†’        updateMarkersCache()
   489â†’
   490â†’        // Binary search for previous marker
   491â†’        var index = sortedMarkersCache.binarySearch { marker in
   492â†’            CMTimeCompare(marker.timestamp, currentTime) < 0
   493â†’        }
   494â†’
   495â†’        // If we're exactly on a marker, go to the one before it
   496â†’        if index > 0 && index < sortedMarkersCache.count &&
   497â†’           CMTimeCompare(sortedMarkersCache[index - 1].timestamp, currentTime) == 0 {
   498â†’            index -= 1
   499â†’        }
   500â†’
   501â†’        if index > 0 {
   502â†’            let prevMarker = sortedMarkersCache[index - 1]
   503â†’            seek(to: prevMarker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
   504â†’            selectedMarker = prevMarker
   505â†’        } else if !sortedMarkersCache.isEmpty {
   506â†’            // Wrap to last marker
   507â†’            let lastMarker = sortedMarkersCache.last!
   508â†’            seek(to: lastMarker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
   509â†’            selectedMarker = lastMarker
   510â†’        }
   511â†’    }
