   513â†’    func selectMarkerAt(time: CMTime) {
   514â†’        // Ensure cache is up to date
   515â†’        updateMarkersCache()
   516â†’        
   517â†’        // Find the closest marker to the given time
   518â†’        let closestMarker = sortedMarkersCache.min { marker1, marker2 in
   519â†’            let diff1 = abs(CMTimeGetSeconds(CMTimeSubtract(marker1.timestamp, time)))
   520â†’            let diff2 = abs(CMTimeGetSeconds(CMTimeSubtract(marker2.timestamp, time)))
   521â†’            return diff1 < diff2
   522â†’        }
   523â†’
   524â†’        if let marker = closestMarker {
   525â†’            let diff = abs(CMTimeGetSeconds(CMTimeSubtract(marker.timestamp, time)))
   526â†’            // Only select if within 0.5 seconds
   527â†’            if diff < 0.5 {
   528â†’                selectedMarker = marker
   529â†’            }
   530â†’        }
   531â†’    }
