   617â†’    private func seekWhilePaused(direction: SeekDirection, frames: Int) {
   618â†’        let currentTime = currentTime
   619â†’        let frameDuration = CMTimeMake(value: 1, timescale: Int32(frameRate))
   620â†’        let seekOffset = CMTimeMultiply(frameDuration, multiplier: Int32(frames))
   621â†’
   622â†’        let seekTime = direction == .forward
   623â†’            ? CMTimeAdd(currentTime, seekOffset)
   624â†’            : CMTimeSubtract(currentTime, seekOffset)
   625â†’
   626â†’        let frameText = frames == 1 ? "frame" : "frames"
   627â†’        showSeekFeedback(direction: direction, amount: "\(frames) \(frameText)")
   628â†’
   629â†’        seekManager?.seek(to: seekTime,
   630â†’                         toleranceBefore: .zero,
   631â†’                         toleranceAfter: .zero) { finished in
   632â†’            if finished {
   633â†’                // UI will update automatically via @Published currentTime
   634â†’            }
   635â†’        }
