   638â†’    private func clampTime(_ time: CMTime) -> CMTime {
   639â†’        guard let duration = player?.currentItem?.duration,
   640â†’              duration.isValid && duration.value > 0 else { return time }
   641â†’
   642â†’        if CMTimeCompare(time, .zero) < 0 {
   643â†’            return .zero
   644â†’        } else if CMTimeCompare(time, duration) > 0 {
   645â†’            return duration
   646â†’        } else {
   647â†’            return time
   648â†’        }
   649â†’    }
