   679â†’    private func saveMarkers() {
   680â†’        guard let videoURL = videoURL else { return }
   681â†’        
   682â†’        // Only save if we have user-added markers (not just the start marker)
   683â†’        let userMarkers = markers.filter { marker in
   684â†’            // Exclude start markers (timestamp 0 with "Start" label)
   685â†’            !(marker.timestamp == .zero && marker.labels.contains("Start"))
   686â†’        }
   687â†’        
   688â†’        // Only create/update metadata file if we have actual user markers
   689â†’        if !userMarkers.isEmpty {
   690â†’            MarkerPersistence.shared.saveMarkers(markers, for: videoURL)
   691â†’        } else {
   692â†’            // If we only have the start marker, delete the metadata file if it exists
   693â†’            if MarkerPersistence.shared.shouldDeleteMetadataFile(markers: markers) {
