import Foundation
import AVFoundation

extension CMTime {
    var timeString: String {
        guard self.isValid && !self.isIndefinite else { return "00:00:00" }

        let totalSeconds = Int(CMTimeGetSeconds(self))
        let hours = totalSeconds / 3600
        let minutes = (totalSeconds % 3600) / 60
        let seconds = totalSeconds % 60

        return String(format: "%02d:%02d:%02d", hours, minutes, seconds)
    }

    func frameNumber(fps: Float) -> Int {
        guard self.isValid && !self.isIndefinite && fps > 0 else { return 0 }
        return Int(CMTimeGetSeconds(self) * Double(fps))
    }

    func timeStringWithFrame(fps: Float) -> String {
        let time = timeString
        let frame = frameNumber(fps: fps)
        return "\(time) (Frame: \(frame))"
    }
}

//
//  SettingsViewModel.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
