import SwiftUI

class SettingsViewModel: ObservableObject {
    @AppStorage("defaultVideoDirectory") var videoDirectory = DefaultConfiguration.defaultVideoDirectory
    @AppStorage("defaultMetadataDirectory") var metadataDirectory = DefaultConfiguration.defaultMetadataDirectory

    // Persistent storage for all shortcut categories
    @AppStorage("playPauseKey") var playPauseKey = DefaultShortcuts.playPauseKey
//    @AppStorage("scrubBackwardKey") var scrubBackwardKey = "q"
//    @AppStorage("scrubForwardKey") var scrubForwardKey = "w"
    @AppStorage("addMarkerKey") var addMarkerKey = DefaultShortcuts.addMarkerKey
    @AppStorage("previousMarkerKey") var previousMarkerKey = DefaultShortcuts.previousMarkerKey
    @AppStorage("nextMarkerKey") var nextMarkerKey = DefaultShortcuts.nextMarkerKey
    @AppStorage("deleteMarkerKey") var deleteMarkerKey = DefaultShortcuts.deleteMarkerKey
    @AppStorage("editMarkerKey") var editMarkerKey = DefaultShortcuts.editMarkerKey
    @AppStorage("openSettingsKey") var openSettingsKey = DefaultShortcuts.openSettingsKey
    @AppStorage("nextVideoInDirectoryKey") var nextVideoInDirectoryKey = DefaultShortcuts.nextVideoInDirectoryKey
    @AppStorage("previousVideoInDirectoryKey") var previousVideoInDirectoryKey = DefaultShortcuts.previousVideoInDirectoryKey

    @Published var scrubbingConfigs: [ScrubbingConfig] = []
    @Published var hasUnsavedChanges = false

    // Temporary storage for unsaved changes
    @Published var tempPlayPauseKey = DefaultShortcuts.playPauseKey
    @Published var tempAddMarkerKey = DefaultShortcuts.addMarkerKey
    @Published var tempPreviousMarkerKey = DefaultShortcuts.previousMarkerKey
    @Published var tempNextMarkerKey = DefaultShortcuts.nextMarkerKey
    @Published var tempDeleteMarkerKey = DefaultShortcuts.deleteMarkerKey
    @Published var tempEditMarkerKey = DefaultShortcuts.editMarkerKey
    @Published var tempOpenSettingsKey = DefaultShortcuts.openSettingsKey
    @Published var tempNextVideoInDirectoryKey = DefaultShortcuts.nextVideoInDirectoryKey
    @Published var tempPreviousVideoInDirectoryKey = DefaultShortcuts.previousVideoInDirectoryKey
    @Published var tempScrubbingConfigs: [ScrubbingConfig] = []

    // Temporary storage for directory settings
    @Published var tempVideoDirectory = ""
    @Published var tempMetadataDirectory = ""

    init() {
        loadSettings()
    }

    func loadSettings() {
        // Load shortcuts into temp variables (excluding scrub shortcuts which are managed separately)
        tempPlayPauseKey = playPauseKey
        tempAddMarkerKey = addMarkerKey
        tempPreviousMarkerKey = previousMarkerKey
        tempNextMarkerKey = nextMarkerKey
        tempDeleteMarkerKey = deleteMarkerKey
        tempEditMarkerKey = editMarkerKey
        tempOpenSettingsKey = openSettingsKey
        tempNextVideoInDirectoryKey = nextVideoInDirectoryKey
        tempPreviousVideoInDirectoryKey = previousVideoInDirectoryKey

        // Load scrubbing configs from UserDefaults
        if let data = UserDefaults.standard.data(forKey: "scrubbingConfigs"),
           let decoded = try? JSONDecoder().decode([ScrubbingConfig].self, from: data) {
            scrubbingConfigs = decoded
        } else if scrubbingConfigs.isEmpty {
            // Load default scrubbing configs if none exist
            scrubbingConfigs = DefaultShortcuts.scrubbingConfigs
        }
        tempScrubbingConfigs = scrubbingConfigs

        // Load directory settings
        tempVideoDirectory = videoDirectory
        tempMetadataDirectory = metadataDirectory

        hasUnsavedChanges = false
    }

    func saveSettings() {
        // Save shortcuts (excluding scrub shortcuts which are managed separately)
        playPauseKey = tempPlayPauseKey
        addMarkerKey = tempAddMarkerKey
        previousMarkerKey = tempPreviousMarkerKey
        nextMarkerKey = tempNextMarkerKey
        deleteMarkerKey = tempDeleteMarkerKey
        editMarkerKey = tempEditMarkerKey
        openSettingsKey = tempOpenSettingsKey
        nextVideoInDirectoryKey = tempNextVideoInDirectoryKey
        previousVideoInDirectoryKey = tempPreviousVideoInDirectoryKey

        // Save scrubbing configs
        scrubbingConfigs = tempScrubbingConfigs

        // Save directory settings
        videoDirectory = tempVideoDirectory
        metadataDirectory = tempMetadataDirectory

        hasUnsavedChanges = false

        // Encode and save scrubbing configs to UserDefaults
        if let encoded = try? JSONEncoder().encode(scrubbingConfigs) {
            UserDefaults.standard.set(encoded, forKey: "scrubbingConfigs")
        }
    }

    func cancelChanges() {
        loadSettings()
    }

    func resetShortcutsToDefaults() {
        tempPlayPauseKey = DefaultShortcuts.playPauseKey
        tempAddMarkerKey = DefaultShortcuts.addMarkerKey
        tempPreviousMarkerKey = DefaultShortcuts.previousMarkerKey
        tempNextMarkerKey = DefaultShortcuts.nextMarkerKey
        tempDeleteMarkerKey = DefaultShortcuts.deleteMarkerKey
        tempEditMarkerKey = DefaultShortcuts.editMarkerKey
        tempOpenSettingsKey = DefaultShortcuts.openSettingsKey
        tempNextVideoInDirectoryKey = DefaultShortcuts.nextVideoInDirectoryKey
        tempPreviousVideoInDirectoryKey = DefaultShortcuts.previousVideoInDirectoryKey
        hasUnsavedChanges = true
    }

    func resetScrubbingToDefaults() {
        tempScrubbingConfigs = DefaultShortcuts.scrubbingConfigs
        hasUnsavedChanges = true
    }

    func markAsChanged() {
        hasUnsavedChanges = true
    }

    // Shortcut info for validation
    struct ShortcutInfo {
        let key: String
        let description: String
        let category: String
    }

    // Get all shortcuts for validation (excluding scrub shortcuts which are managed separately)
    var allCurrentShortcuts: [ShortcutInfo] {
        [
            ShortcutInfo(key: tempPlayPauseKey, description: "Play/Pause", category: "Playback"),
            ShortcutInfo(key: tempAddMarkerKey, description: "Add Marker", category: "Markers"),
            ShortcutInfo(key: tempPreviousMarkerKey, description: "Previous Marker", category: "Markers"),
            ShortcutInfo(key: tempNextMarkerKey, description: "Next Marker", category: "Markers"),
            ShortcutInfo(key: tempDeleteMarkerKey, description: "Delete Marker", category: "Markers"),
            ShortcutInfo(key: tempEditMarkerKey, description: "Edit Marker", category: "Markers"),
            ShortcutInfo(key: tempOpenSettingsKey, description: "Settings", category: "Application"),
            ShortcutInfo(key: tempNextVideoInDirectoryKey, description: "Next Video", category: "Navigation"),
            ShortcutInfo(key: tempPreviousVideoInDirectoryKey, description: "Previous Video", category: "Navigation")
        ]
    }

    // Check for conflicts across all shortcut types
    func hasShortcutConflicts() -> [String] {
        var conflicts: [String] = []
        let shortcuts = allCurrentShortcuts
        let scrubbingKeys = tempScrubbingConfigs.flatMap { [$0.backwardKey, $0.forwardKey] }

        // Check for duplicates in main shortcuts using efficient Set-based approach
        let mainKeys = shortcuts.map { $0.key }
        let duplicates = findDuplicates(in: mainKeys)

        for duplicate in duplicates {
            let conflictingShortcuts = shortcuts.filter { $0.key == duplicate }.map { $0.description }
            conflicts.append("Conflict: '\(duplicate)' is used by \(conflictingShortcuts.joined(separator: ", "))")
        }

        // Check for conflicts between main shortcuts and scrubbing
        for scrubbingKey in scrubbingKeys {
            if let conflictingShortcut = shortcuts.first(where: { $0.key == scrubbingKey }) {
                let message = "Conflict: '\(scrubbingKey)' is used by both \(conflictingShortcut.description) " +
                              "and scrubbing"
                conflicts.append(message)
            }
        }

        return conflicts
    }

    // Efficient duplicate detection using Set-based approach
    private func findDuplicates<T: Hashable>(in array: [T]) -> Set<T> {
        var seen = Set<T>()
        var duplicates = Set<T>()

        for item in array {
            if seen.contains(item) {
                duplicates.insert(item)
            } else {
                seen.insert(item)
            }
        }

        return duplicates
    }
}

