    60â†’    private func updateMarkersCache() {
    61â†’        if markersCacheInvalidated {
    62â†’            sortedMarkersCache = markers.sorted()
    63â†’            markersCacheInvalidated = false
    64â†’        }
    65â†’    }
    66â†’    
    67â†’    var segments: [Segment] {
    68â†’        // Use cached markers if available, otherwise compute without modifying state
    69â†’        let sortedMarkers = markersCacheInvalidated ? markers.sorted() : sortedMarkersCache
    70â†’        
    71â†’        var result = [Segment]()
    72â†’        guard let duration = player?.currentItem?.duration,
    73â†’              duration.isValid && duration.value > 0 else { return result }
    74â†’
    75â†’        if sortedMarkers.isEmpty {
    76â†’            // No markers - entire video is one segment
    77â†’            result.append(Segment(startMarker: nil, endMarker: nil))
    78â†’        } else {
    79â†’            // First segment: start to first marker
    80â†’            if let firstMarker = sortedMarkers.first {
    81â†’                result.append(Segment(startMarker: nil, endMarker: firstMarker))
    82â†’            }
    83â†’
    84â†’            // Middle segments
    85â†’            if sortedMarkers.count > 1 {
    86â†’                for markerIndex in 0..<sortedMarkers.count - 1 {
    87â†’                    result.append(Segment(
    88â†’                        startMarker: sortedMarkers[markerIndex],
    89â†’                        endMarker: sortedMarkers[markerIndex + 1]
    90â†’                    ))
    91â†’                }
    92â†’            }
    93â†’
    94â†’            // Last segment: last marker to end
    95â†’            if let lastMarker = sortedMarkers.last {
    96â†’                result.append(Segment(startMarker: lastMarker, endMarker: nil))
    97â†’            }
    98â†’        }
    99â†’
   100â†’        return result
   101â†’    }
   102â†’    
   103â†’    var currentSegment: Segment? {
   104â†’        let time = currentTime
   105â†’        
   106â†’        for segment in segments {
   107â†’            let startTime = segment.startMarker?.timestamp ?? .zero
   108â†’            let endTime = segment.endMarker?.timestamp ?? duration
   109â†’            
   110â†’            // Check if current time is within this segment
   111â†’            if CMTimeCompare(time, startTime) >= 0 && CMTimeCompare(time, endTime) < 0 {
   112â†’                return segment
   113â†’            }
   114â†’        }
   115â†’        
   116â†’        // If we're at the very end, return the last segment
   117â†’        if let lastSegment = segments.last,
   118â†’           CMTimeCompare(time, duration) >= 0 {
   119â†’            return lastSegment
   120â†’        }
   121â†’        
   122â†’        return nil
   123â†’    }
