   130â†’    func loadVideo(url: URL) {
   131â†’        // Clean up existing player
   132â†’        player?.pause()  // Explicitly pause current player to prevent multiple audio streams
   133â†’        isPlaying = false
   134â†’        
   135â†’        // Stop performance monitoring for old player
   136â†’        PerformanceMonitor.shared.stopMonitoring()
   137â†’        
   138â†’        // Clear existing observers to prevent dangling subscriptions
   139â†’        cancellables.removeAll()
   140â†’        
   141â†’        // Cancel any pending seeks from old SeekManager
   142â†’        seekManager?.cancelPendingSeeks()
   143â†’        
   144â†’        if let observer = timeObserver {
   145â†’            player?.removeTimeObserver(observer)
   146â†’            timeObserver = nil
   147â†’        }
   148â†’
   149â†’        // Reset error state
   150â†’        playerError = nil
   151â†’
   152â†’        // Reset markers for new video (will load after video is ready)
   153â†’        markers = []
   154â†’        selectedMarker = nil
   155â†’        markersCacheInvalidated = true
   156â†’
   157â†’        // Start performance monitoring
   158â†’        loadStartTime = Date()
   159â†’
   160â†’        // Check if file exists
   161â†’        guard FileManager.default.fileExists(atPath: url.path) else {
   162â†’            playerError = .fileNotFound
   163â†’            return
   164â†’        }
   165â†’
   166â†’        videoURL = url
   167â†’        let asset = AVURLAsset(url: url)
   168â†’        let playerItem = AVPlayerItem(asset: asset)
   169â†’
   170â†’        player = AVPlayer(playerItem: playerItem)
   171â†’
   172â†’        // Initialize SeekManager
   173â†’        seekManager = SeekManager(player: player!)
   174â†’
   175â†’        // Configure player for large files
   176â†’        configurePlayerForLargeFiles()
   177â†’
   178â†’        // Get video duration
   179â†’        Task {
   180â†’            do {
   181â†’                let duration = try await asset.load(.duration)
   182â†’                await MainActor.run {
   183â†’                    self.duration = duration
   184â†’                }
   185â†’
   186â†’                // Extract frame rate
   187â†’                if let track = try await asset.loadTracks(withMediaType: .video).first {
   188â†’                    let frameRate = try await track.load(.nominalFrameRate)
   189â†’                    await MainActor.run {
   190â†’                        self.frameRate = frameRate > 0 ? frameRate : 30.0
   191â†’                    }
   192â†’                }
   193â†’            } catch {
   194â†’                await MainActor.run {
   195â†’                    self.playerError = .decodingFailed
   196â†’                }
   197â†’                print("Error loading video metadata: \(error)")
   198â†’            }
   199â†’        }
   200â†’
   201â†’        // Set up time observer
   202â†’        setupTimeObserver()
   203â†’
   204â†’        // Observe player status
   205â†’        playerItem.publisher(for: \.status)
   206â†’            .sink { [weak self] status in
   207â†’                if status == .failed {
   208â†’                    self?.handlePlayerItemError(playerItem.error)
   209â†’                } else if status == .readyToPlay {
   210â†’                    // Record load time
   211â†’                    if let startTime = self?.loadStartTime {
   212â†’                        self?.loadTime = Date().timeIntervalSince(startTime)
   213â†’                        print("Video loaded in \(String(format: "%.2f", self?.loadTime ?? 0)) seconds")
   214â†’                    }
   215â†’
   216â†’                    // Load markers for this video
   217â†’                    self?.loadMarkers()
   218â†’                    
   219â†’                    // Start performance monitoring
   220â†’                    if let player = self?.player {
   221â†’                        PerformanceMonitor.shared.startMonitoring(player: player)
   222â†’                        PerformanceAnalytics.shared.record(PerformanceMonitor.shared.currentMetrics ?? PerformanceMetrics(
   223â†’                            timestamp: Date(),
   224â†’                            cpuUsage: 0,
   225â†’                            memoryUsage: 0,
   226â†’                            droppedFrames: 0,
   227â†’                            frameRate: 30,
   228â†’                            frameRateStability: 100,
   229â†’                            gpuUsage: nil,
   230â†’                            decoderEfficiency: nil
   231â†’                        ))
   232â†’                    }
   233â†’                    
   234â†’                    // Auto-play the video when it loads
   235â†’                    self?.play()
   236â†’                }
   237â†’            }
   238â†’            .store(in: &cancellables)
   239â†’
   240â†’        // Observe when video ends
   241â†’        NotificationCenter.default.publisher(for: .AVPlayerItemDidPlayToEndTime, object: playerItem)
   242â†’            .sink { [weak self] _ in
   243â†’                self?.isPlaying = false
   244â†’            }
   245â†’            .store(in: &cancellables)
   246â†’    }
