   305â†’    func seek(to time: CMTime, toleranceBefore: CMTime = .zero, toleranceAfter: CMTime = .zero) {
   306â†’        let clampedTime = clampTime(time)
   307â†’
   308â†’        if let seekManager = seekManager {
