   315â†’                    // UI will update automatically via @Published currentTime
   316â†’                }
   317â†’            }
   318â†’        } else {
   319â†’            player?.seek(to: clampedTime, toleranceBefore: toleranceBefore, toleranceAfter: toleranceAfter)
   320â†’        }
