   410â†’    func deleteNearestMarker() {
   411â†’        let currentTime = currentTime
   412â†’        
   413â†’        // Ensure cache is up to date
   414â†’        updateMarkersCache()
   415â†’
   416â†’        // Find all markers before or at current time
   417â†’        let markersBefore = sortedMarkersCache.filter { marker in
   418â†’            CMTimeCompare(marker.timestamp, currentTime) <= 0
   419â†’        }
   420â†’
   421â†’        // Get the closest one (last in the sorted array)
   422â†’        guard let nearestMarker = markersBefore.last else {
   423â†’            print("No marker found before current time")
   424â†’            return
   425â†’        }
   426â†’
   427â†’        // Don't delete start marker
   428â†’        if nearestMarker.timestamp == .zero {
   429â†’            print("Cannot delete start marker")
   430â†’            return
   431â†’        }
   432â†’
   433â†’        deleteMarker(id: nearestMarker.id)
   434â†’        print("Deleted marker at \(nearestMarker.timestamp.timeString)")
   435â†’    }
