   461â†’    func jumpToNextMarker() {
   462â†’        let currentTime = currentTime
   463â†’        
   464â†’        // Ensure cache is up to date
   465â†’        updateMarkersCache()
   466â†’
   467â†’        // Use cached sorted markers for O(log n) lookup
   468â†’        let index = sortedMarkersCache.binarySearch { marker in
   469â†’            CMTimeCompare(marker.timestamp, currentTime) <= 0
   470â†’        }
   471â†’
   472â†’        if index < sortedMarkersCache.count {
   473â†’            let nextMarker = sortedMarkersCache[index]
   474â†’            seek(to: nextMarker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
   475â†’            selectedMarker = nextMarker
   476â†’        } else if !sortedMarkersCache.isEmpty {
   477â†’            // Wrap to first marker
   478â†’            let firstMarker = sortedMarkersCache[0]
   479â†’            seek(to: firstMarker.timestamp, toleranceBefore: .zero, toleranceAfter: .zero)
   480â†’            selectedMarker = firstMarker
   481â†’        }
   482â†’    }
