   569â†’    func scrubForward(with config: ScrubbingConfig) {
   570â†’        if isPlaying {
   571â†’            seekWhilePlaying(direction: .forward, seconds: config.playingSeekSeconds)
   572â†’        } else {
   573â†’            seekWhilePaused(direction: .forward, frames: config.pausedSeekFrames)
   574â†’        }
   575â†’    }
