   600â†’    private func seekWhilePlaying(direction: SeekDirection, seconds: Double) {
   601â†’        let currentTime = currentTime
   602â†’        let seekTime = direction == .forward
   603â†’            ? CMTimeAdd(currentTime, CMTimeMakeWithSeconds(seconds, preferredTimescale: 600))
   604â†’            : CMTimeSubtract(currentTime, CMTimeMakeWithSeconds(seconds, preferredTimescale: 600))
   605â†’
   606â†’        showSeekFeedback(direction: direction, amount: "\(Int(seconds))s")
   607â†’
   608â†’        seekManager?.seek(to: seekTime,
   609â†’                         toleranceBefore: CMTimeMakeWithSeconds(0.5, preferredTimescale: 600),
   610â†’                         toleranceAfter: CMTimeMakeWithSeconds(0.5, preferredTimescale: 600)) { finished in
   611â†’            if finished {
   612â†’                // UI will update automatically via @Published currentTime
   613â†’            }
   614â†’        }
