//
//  ContentView.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import SwiftUI
import AVKit
import UniformTypeIdentifiers

struct ContentView: View {
    @EnvironmentObject var viewModel: VideoPlayerViewModel
    @StateObject private var settingsViewModel = SettingsViewModel()
    @StateObject private var directoryManager = VideoDirectoryManager()

    @State private var showingSettings = false
    @State private var showingMarkerDetails = false
    @State private var selectedMarkerForEdit: Marker?
    @State private var showingError = false
    @State private var errorMessage = ""
    @State private var showingMarkerList = false

    var body: some View {
        ZStack {
            DesignSystem.Colors.background
                .ignoresSafeArea()

            // Keyboard handling layer
            KeyboardHandlingView(
                viewModel: viewModel,
                settingsViewModel: settingsViewModel,
                showingSettings: $showingSettings,
                selectedMarkerForEdit: $selectedMarkerForEdit,
                showingMarkerList: $showingMarkerList,
                onNavigateNext: navigateToNextVideo,
                onNavigatePrevious: navigateToPreviousVideo,
                onOpenFile: openFilePicker
            )

            if viewModel.videoURL != nil {
                HStack(spacing: 0) {
                    // Marker/Segment list sidebar
                    if showingMarkerList {
                        MarkerSegmentListContainer(
                            viewModel: viewModel,
                            selectedMarkerForEdit: $selectedMarkerForEdit
                        )
                    }

                    // Main video content
                    ZStack(alignment: .bottom) {
                        VideoPlayerView(player: viewModel.player)
                            .ignoresSafeArea()

                        // Progress bar overlay
                        ProgressBarView(viewModel: viewModel, selectedMarkerForEdit: $selectedMarkerForEdit)

                    // Scrubbing feedback overlay
                    ScrubbingFeedbackOverlay(viewModel: viewModel)

                    // Video position indicator
                    if !directoryManager.videos.isEmpty && directoryManager.currentIndex >= 0 {
                        VStack {
                            HStack {
                                Spacer()
                                HStack(spacing: 8) {
                                    if directoryManager.isScanning {
                                        ProgressView()
                                            .scaleEffect(0.7)
                                    }
                                    Text(directoryManager.positionDescription)
                                        .font(.caption)
                                        .foregroundColor(DesignSystem.Colors.textSecondary)
                                }
                                .padding(.horizontal, 12)
                                .padding(.vertical, 6)
                                .background(DesignSystem.Colors.surfaceSecondary.opacity(0.8))
                                .cornerRadius(6)
                                .padding(DesignSystem.Spacing.md)
                            }
                            Spacer()
                        }
                    } else if directoryManager.isScanning {
                        VStack {
                            HStack {
                                Spacer()
                                HStack(spacing: 8) {
                                    ProgressView()
                                        .scaleEffect(0.7)
                                    Text("Scanning directory...")
                                        .font(.caption)
                                        .foregroundColor(DesignSystem.Colors.textSecondary)
                                }
                                .padding(.horizontal, 12)
                                .padding(.vertical, 6)
                                .background(DesignSystem.Colors.surfaceSecondary.opacity(0.8))
                                .cornerRadius(6)
                                .padding(DesignSystem.Spacing.md)
                            }
                            Spacer()
                        }
                    }

                    // Performance overlay
                    if viewModel.showPerformanceOverlay {
                        PerformanceOverlay(
                            monitor: PerformanceMonitor.shared,
                            analytics: PerformanceAnalytics.shared
                        )
                    }

                    }

                    // Error banner overlay
                    if let error = viewModel.playerError {
                        VStack {
                            ErrorBanner(error: error) {
                                viewModel.playerError = nil
                            }
                            .padding(DesignSystem.Spacing.md)

                            Spacer()
                        }
                    }
                }
            } else {
                VStack(spacing: DesignSystem.Spacing.lg) {
                    Image(systemName: "video.fill")
                        .font(.system(size: 64))
                        .foregroundColor(DesignSystem.Colors.textTertiary)

                    Text("Open a video file to begin")
                        .font(DesignSystem.Typography.title)
                        .foregroundColor(DesignSystem.Colors.textSecondary)

                    Button("Open Video...") {
                        openFilePicker()
                    }
                    .buttonStyle(PrimaryButtonStyle())
                }
            }
        }
        .sheet(isPresented: $showingSettings) {
            SettingsView()
                .environmentObject(settingsViewModel)
        }
        .sheet(item: $selectedMarkerForEdit) { _ in
            MarkerManagementView(viewModel: viewModel)
                .frame(width: 400, height: 650)
        }
        .task {
            setupManagers()
        }
        .onChange(of: settingsViewModel.scrubbingConfigs) {
            // Keep scrubbing settings in sync
            viewModel.scrubbingSettings = ScrubbingSettings(configs: settingsViewModel.scrubbingConfigs)
        }
        .alert("Error", isPresented: $showingError) {
            Button("OK") { }
        } message: {
            Text(errorMessage)
        }
        .onReceive(NotificationCenter.default.publisher(for: .openVideoFile)) { _ in
            openFilePicker()
        }
        .onReceive(NotificationCenter.default.publisher(for: .grantDirectoryAccess)) { _ in
            Task {
                await handleGrantDirectoryAccess()
            }
        }
        .onChange(of: viewModel.videoURL) { _, newURL in
            // Scan directory whenever a new video is loaded
            if let url = newURL {
                Task {
                    print("ContentView: Video URL changed, starting directory scan for: \(url.lastPathComponent)")
                    await directoryManager.scanDirectory(at: url)
                    print("ContentView: Directory scan completed. Found \(directoryManager.videos.count) videos, current index: \(directoryManager.currentIndex)")

                    // Handle scan failure
                    if directoryManager.videos.isEmpty && directoryManager.lastScanError != nil {
                        // If scan failed due to permissions, automatically request access
                        if let error = directoryManager.lastScanError as? VideoDirectoryManager.ScanError,
                           error == .permissionDenied {
                            print("ContentView: Directory scan failed due to permissions, requesting access...")
                            let granted = await directoryManager.requestDirectoryAccess(for: url)
                            if granted {
                                print("ContentView: Directory access granted, retrying scan...")
                                // Retry scan after permission granted
                                await directoryManager.scanDirectory(at: url)
                                if directoryManager.videos.isEmpty {
                                    // Still failed after permission grant
                                    errorMessage = "Failed to scan directory: \(directoryManager.lastScanError?.localizedDescription ?? "Unknown error")"
                                    showingError = true
                                }
                            } else {
                                errorMessage = "Directory access denied. Use File > Grant Directory Access to enable navigation."
                                showingError = true
                            }
                        } else {
                            // Other errors
                            errorMessage = "Failed to scan directory: \(directoryManager.lastScanError?.localizedDescription ?? "Unknown error")"
                            showingError = true
                        }
                    }

                }
            }
        }
    }

    private func openFilePicker() {
        let panel = NSOpenPanel()
        panel.title = "Select a video file or folder"
        panel.message = "You can select either a video file or a folder containing videos"
        panel.allowsMultipleSelection = false
        panel.canChooseDirectories = true
        panel.canChooseFiles = true
        panel.allowedContentTypes = [
            UTType(filenameExtension: "mp4")!,
            UTType(filenameExtension: "mov")!,
            UTType(filenameExtension: "m4v")!,
            UTType(filenameExtension: "avi")!,
            UTType(filenameExtension: "mkv")!,
            UTType(filenameExtension: "mpg")!,
            UTType(filenameExtension: "mpeg")!,
            UTType(filenameExtension: "webm")!,
            UTType.folder
        ]

        if panel.runModal() == .OK {
            if let url = panel.url {
                Task {
                    await handleSelectedURL(url)
                }
            }
        }
    }

    private func handleSelectedURL(_ url: URL) async {
        var isDirectory: ObjCBool = false
        let fileManager = FileManager.default

        if fileManager.fileExists(atPath: url.path, isDirectory: &isDirectory) {
            if isDirectory.boolValue {
                // User selected a directory
                print("User selected directory: \(url.path)")

                // Create bookmark for the directory
                if url.startAccessingSecurityScopedResource() {
                    defer { url.stopAccessingSecurityScopedResource() }

                    do {
                        let bookmarkData = try url.bookmarkData(
                            options: [.withSecurityScope, .securityScopeAllowOnlyReadAccess],
                            includingResourceValuesForKeys: nil,
                            relativeTo: nil
                        )
                        UserDefaults.standard.set(bookmarkData, forKey: "DirectoryBookmark-\(url.path)")
                        print("Created bookmark for directory: \(url.path)")
                    } catch {
                        print("Failed to create directory bookmark: \(error)")
                    }
                }

                // Find first video in directory
                if let firstVideo = await directoryManager.findFirstVideo(in: url) {
                    viewModel.loadVideo(url: firstVideo)
                } else {
                    errorMessage = "No video files found in the selected directory"
                    showingError = true
                }
            } else {
                // User selected a file
                print("User selected file: \(url.path)")
                viewModel.loadVideo(url: url)

                // Try to create a bookmark for the parent directory using the file's security scope
                let directoryURL = url.deletingLastPathComponent()

                // First, start accessing the file's security-scoped resource
                if url.startAccessingSecurityScopedResource() {
                    defer { url.stopAccessingSecurityScopedResource() }

                    // Try to create bookmark for the directory
                    // The file's security scope should give us access to its parent directory
                    do {
                        let bookmarkData = try directoryURL.bookmarkData(
                            options: [.withSecurityScope, .securityScopeAllowOnlyReadAccess],
                            includingResourceValuesForKeys: nil,
                            relativeTo: nil
                        )
                        UserDefaults.standard.set(bookmarkData, forKey: "DirectoryBookmark-\(directoryURL.path)")
                        print("Created bookmark for parent directory: \(directoryURL.path)")
                    } catch {
                        print("Failed to create directory bookmark: \(error)")
                    }
                }
            }
        }
    }

    // MARK: - Manager Setup
    private func setupManagers() {
        // Sync scrubbing settings from SettingsViewModel to VideoPlayerViewModel
        viewModel.scrubbingSettings = ScrubbingSettings(configs: settingsViewModel.scrubbingConfigs)
    }

    // MARK: - Video Navigation
    private func navigateToNextVideo() {
        // Check if a video is loaded first
        guard let currentVideoURL = viewModel.videoURL else {
            errorMessage = "No video loaded. Open a video file to use directory navigation."
            showingError = true
            return
        }

        // Check if directory scanning is in progress
        if directoryManager.isScanning {
            errorMessage = "Directory scanning in progress. Please wait..."
            showingError = true
            return
        }

        // Check if directory has been scanned
        if directoryManager.videos.isEmpty {
            if let scanError = directoryManager.lastScanError {
                errorMessage = "Directory scan failed: \(scanError.localizedDescription)"
            } else if directoryManager.isScanning {
                errorMessage = "Directory is currently being scanned. Please wait..."
            } else {
                // Attempt to scan directory if not done yet
                errorMessage = "Scanning directory..."
                showingError = true
                Task {
                    await directoryManager.scanDirectory(at: currentVideoURL)
                    if !directoryManager.videos.isEmpty {
                        showingError = false
                        navigateToNextVideo() // Retry after scan
                    } else if directoryManager.lastScanError != nil {
                        // If scan failed due to permissions, offer to request access
                        if let error = directoryManager.lastScanError as? VideoDirectoryManager.ScanError,
                           error == .permissionDenied {
                            let granted = await directoryManager.requestDirectoryAccess(for: currentVideoURL)
                            if granted {
                                // Retry scan after permission granted
                                await directoryManager.scanDirectory(at: currentVideoURL)
                                if !directoryManager.videos.isEmpty {
                                    showingError = false
                                    navigateToNextVideo()
                                }
                            }
                        }
                    }
                }
                return
            }
            showingError = true
            return
        }

        if let nextVideoURL = directoryManager.navigateToNext() {
            viewModel.loadVideo(url: nextVideoURL)
            Task {
                await directoryManager.rescanCurrentDirectory()
            }
        } else {
            errorMessage = "Already at the last video in directory (\(directoryManager.currentIndex + 1) of \(directoryManager.videos.count))"
            showingError = true
        }
    }

    private func navigateToPreviousVideo() {
        let timestamp = Date().timeIntervalSince1970
        print("[\(String(format: "%.3f", timestamp))] navigateToPreviousVideo() called")

        // Check if a video is loaded first
        guard let currentVideoURL = viewModel.videoURL else {
            print("[\(String(format: "%.3f", timestamp))] No video loaded - cannot navigate")
            errorMessage = "No video loaded. Open a video file to use directory navigation."
            showingError = true
            return
        }

        // Check if directory scanning is in progress
        if directoryManager.isScanning {
            errorMessage = "Directory scanning in progress. Please wait..."
            showingError = true
            return
        }

        // Check if directory has been scanned
        if directoryManager.videos.isEmpty {
            if let scanError = directoryManager.lastScanError {
                errorMessage = "Directory scan failed: \(scanError.localizedDescription)"
            } else if directoryManager.isScanning {
                errorMessage = "Directory is currently being scanned. Please wait..."
            } else {
                // Attempt to scan directory if not done yet
                errorMessage = "Scanning directory..."
                showingError = true
                Task {
                    await directoryManager.scanDirectory(at: currentVideoURL)
                    if !directoryManager.videos.isEmpty {
                        showingError = false
                        navigateToPreviousVideo() // Retry after scan
                    } else if directoryManager.lastScanError != nil {
                        // If scan failed due to permissions, offer to request access
                        if let error = directoryManager.lastScanError as? VideoDirectoryManager.ScanError,
                           error == .permissionDenied {
                            let granted = await directoryManager.requestDirectoryAccess(for: currentVideoURL)
                            if granted {
                                // Retry scan after permission granted
                                await directoryManager.scanDirectory(at: currentVideoURL)
                                if !directoryManager.videos.isEmpty {
                                    showingError = false
                                    navigateToPreviousVideo()
                                }
                            }
                        }
                    }
                }
                return
            }
            showingError = true
            return
        }

        print("[\(String(format: "%.3f", timestamp))] Directory manager state: videos=\(directoryManager.videos.count), currentIndex=\(directoryManager.currentIndex), hasPrevious=\(directoryManager.hasPrevious)")

        if let previousVideoURL = directoryManager.navigateToPrevious() {
            print("[\(String(format: "%.3f", timestamp))] Got previous video URL: \(previousVideoURL.lastPathComponent)")
            print("[\(String(format: "%.3f", timestamp))] Loading video...")
            viewModel.loadVideo(url: previousVideoURL)
            Task {
                print("[\(String(format: "%.3f", timestamp))] Rescanning directory...")
                await directoryManager.rescanCurrentDirectory()
            }
        } else {
            print("[\(String(format: "%.3f", timestamp))] No previous video available")
            errorMessage = "Already at the first video in directory (\(directoryManager.currentIndex + 1) of \(directoryManager.videos.count))"
            showingError = true
        }
    }

    // MARK: - Helper Functions

    private func showError(_ error: Error) {
        errorMessage = error.localizedDescription

        // Check for recovery suggestions from various error types
        if let localizedError = error as? LocalizedError,
           let suggestion = localizedError.recoverySuggestion {
            errorMessage += "\n\n" + suggestion
        }

        showingError = true
    }

    private func handleGrantDirectoryAccess() async {
        guard let videoURL = viewModel.videoURL else {
            errorMessage = "Please open a video file first"
            showingError = true
            return
        }

        let granted = await directoryManager.requestDirectoryAccess(for: videoURL)
        if granted {
            // Rescan the directory after access is granted
            await directoryManager.scanDirectory(at: videoURL)
            if !directoryManager.videos.isEmpty {
                errorMessage = "Directory access granted. Found \(directoryManager.videos.count) videos."
                showingError = true
            }
        } else {
            errorMessage = "Directory access was not granted."
            showingError = true
        }
    }
}

