//
//  SeekManager.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import Foundation
import AVFoundation

class SeekManager {
    private let seekQueue = DispatchQueue(label: "com.swiftmedia.seekQueue", qos: .userInteractive)
    private var currentSeekID: UUID?
    private let player: AVPlayer
    private var seekStartTime: Date?
    private var completionTimes: [TimeInterval] = []

    init(player: AVPlayer) {
        self.player = player
    }

    func seek(to time: CMTime,
              toleranceBefore: CMTime = .zero,
              toleranceAfter: CMTime = .zero,
              completion: @escaping (Bool) -> Void) {
        let seekID = UUID()
        currentSeekID = seekID
        seekStartTime = Date()

        seekQueue.async { [weak self] in
            guard let self = self, self.currentSeekID == seekID else {
                DispatchQueue.main.async {
                    completion(false)
                }
                return
            }

            DispatchQueue.main.async {
                guard self.currentSeekID == seekID else {
                    completion(false)
                    return
                }

                self.player.seek(to: time,
                               toleranceBefore: toleranceBefore,
                               toleranceAfter: toleranceAfter) { finished in
                    if self.currentSeekID == seekID {
                        if let startTime = self.seekStartTime {
                            let completionTime = Date().timeIntervalSince(startTime)
                            self.recordCompletionTime(completionTime)
                        }
                        completion(finished)
                    } else {
                        completion(false)
                    }
                }
            }
        }
    }

    func cancelPendingSeeks() {
        currentSeekID = nil
    }

    func seekWithDebounce(to time: CMTime,
                          toleranceBefore: CMTime = .zero,
                          toleranceAfter: CMTime = .zero,
                          debounceInterval: TimeInterval = 0.05,
                          completion: @escaping (Bool) -> Void) {
        cancelPendingSeeks()

        seekQueue.asyncAfter(deadline: .now() + debounceInterval) { [weak self] in
            self?.seek(to: time,
                      toleranceBefore: toleranceBefore,
                      toleranceAfter: toleranceAfter,
                      completion: completion)
        }
    }

    private func recordCompletionTime(_ time: TimeInterval) {
        completionTimes.append(time)
        if completionTimes.count > 100 {
            completionTimes.removeFirst()
        }
    }

    var averageSeekTime: TimeInterval {
        guard !completionTimes.isEmpty else { return 0 }
        return completionTimes.reduce(0, +) / Double(completionTimes.count)
    }

    var lastSeekTime: TimeInterval? {
        completionTimes.last
    }

    func performanceReport() -> String {
        let avg = averageSeekTime * 1000
        let last = (lastSeekTime ?? 0) * 1000
        let max = (completionTimes.max() ?? 0) * 1000
        let min = (completionTimes.min() ?? 0) * 1000

        return String(format: "Seek Performance - Avg: %.1fms, Last: %.1fms, Min: %.1fms, Max: %.1fms",
                     avg, last, min, max)
    }
}

