//
//  MarkerPersistence.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import Foundation
import CryptoKit
import SwiftUI

class MarkerPersistence: ObservableObject {
    static let shared = MarkerPersistence()

    @AppStorage("defaultMetadataDirectory") private var metadataDirectory = DefaultConfiguration.defaultMetadataDirectory

    private var markersDirectory: URL {
        // Use configured metadata directory
        if !metadataDirectory.isEmpty,
           let url = DefaultConfiguration.url(fromPath: metadataDirectory) {
            return url
        }

        // Fallback to default
        let appSupport = FileManager.default.urls(for: .applicationSupportDirectory, in: .userDomainMask).first!
        return appSupport.appendingPathComponent("SwiftMedia/Metadata", isDirectory: true)
    }

    private init() {
        // Directory will be created on demand when saving
    }

    // Generate SHA256 hash of first 1MB of video file
    func generateVideoHash(for url: URL) -> String? {
        guard let fileHandle = try? FileHandle(forReadingFrom: url) else { return nil }
        defer { fileHandle.closeFile() }

        // Read first 1MB or entire file if smaller
        let data = fileHandle.readData(ofLength: 1024 * 1024)

        let hash = SHA256.hash(data: data)
        return hash.compactMap { String(format: "%02x", $0) }.joined()
    }

    func markerFileURL(for videoURL: URL) -> URL? {
        guard let hash = generateVideoHash(for: videoURL) else { return nil }
        return markersDirectory.appendingPathComponent("\(hash).json")
    }

    func saveMarkers(_ markers: [Marker], for videoURL: URL) {
        guard let fileURL = markerFileURL(for: videoURL) else {
            print("[MarkerPersistence] Failed to generate marker file URL")
            return
        }

        // Ensure directory exists
        do {
            try FileManager.default.createDirectory(at: markersDirectory, withIntermediateDirectories: true)
            print("[MarkerPersistence] Using metadata directory: \(markersDirectory.path)")
        } catch {
            print("[MarkerPersistence] Failed to create metadata directory: \(error)")
            return
        }

        do {
            let encoder = JSONEncoder()
            encoder.outputFormatting = [.prettyPrinted, .sortedKeys]
            encoder.dateEncodingStrategy = .iso8601
            let data = try encoder.encode(markers)
            try data.write(to: fileURL, options: .atomic)
            print("[MarkerPersistence] Saved \(markers.count) markers to: \(fileURL.path)")
        } catch {
            print("[MarkerPersistence] Failed to save markers: \(error)")
        }
    }

    func loadMarkers(for videoURL: URL) -> [Marker] {
        guard let fileURL = markerFileURL(for: videoURL),
              FileManager.default.fileExists(atPath: fileURL.path) else {
            print("[MarkerPersistence] No saved markers found for video")
            return []
        }

        do {
            let data = try Data(contentsOf: fileURL)
            let decoder = JSONDecoder()
            decoder.dateDecodingStrategy = .iso8601
            let markers = try decoder.decode([Marker].self, from: data)
            print("[MarkerPersistence] Loaded \(markers.count) markers from: \(fileURL.path)")
            return markers
        } catch {
            print("[MarkerPersistence] Failed to load markers: \(error)")
            return []
        }
    }

    func deleteMarkers(for videoURL: URL) {
        guard let fileURL = markerFileURL(for: videoURL) else { return }

        try? FileManager.default.removeItem(at: fileURL)
        print("[MarkerPersistence] Deleted markers for video")
    }

    func shouldDeleteMetadataFile(markers: [Marker]) -> Bool {
        // Check if we only have the start marker
        return markers.count == 1 && 
               markers.first?.timestamp == .zero && 
               markers.first?.labels.contains("Start") == true
    }
}

//
//  KeyboardHandlingView.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import SwiftUI

struct KeyboardHandlingView: View {
    @ObservedObject var viewModel: VideoPlayerViewModel
    @ObservedObject var settingsViewModel: SettingsViewModel
    @Binding var showingSettings: Bool
    @Binding var selectedMarkerForEdit: Marker?
    @Binding var showingMarkerList: Bool
    let onNavigateNext: () -> Void
    let onNavigatePrevious: () -> Void
    let onOpenFile: () -> Void
    @FocusState private var isFocused: Bool

    // Shortcut registry for managing all shortcuts
    @StateObject private var shortcutRegistry: ShortcutRegistry

    init(
        viewModel: VideoPlayerViewModel,
        settingsViewModel: SettingsViewModel,
        showingSettings: Binding<Bool>,
        selectedMarkerForEdit: Binding<Marker?>,
        showingMarkerList: Binding<Bool>,
        onNavigateNext: @escaping () -> Void,
        onNavigatePrevious: @escaping () -> Void,
        onOpenFile: @escaping () -> Void
    ) {
        self.viewModel = viewModel
        self.settingsViewModel = settingsViewModel
        self._showingSettings = showingSettings
        self._selectedMarkerForEdit = selectedMarkerForEdit
        self._showingMarkerList = showingMarkerList
        self.onNavigateNext = onNavigateNext
        self.onNavigatePrevious = onNavigatePrevious
        self.onOpenFile = onOpenFile
        self._shortcutRegistry = StateObject(wrappedValue: ShortcutRegistry(settingsViewModel: settingsViewModel))
    }

    // Build keyboard shortcuts from registry
    private var keyboardShortcuts: [String: KeyboardShortcut] {
        shortcutRegistry.buildKeyboardShortcuts(
            viewModel: viewModel,
            showingSettings: $showingSettings,
            selectedMarkerForEdit: $selectedMarkerForEdit,
            showingMarkerList: $showingMarkerList,
            onNavigateNext: onNavigateNext,
            onNavigatePrevious: onNavigatePrevious,
            onOpenFile: onOpenFile
        )
    }

    var body: some View {
        let baseView = Color.clear
            .focusable()
            .focused($isFocused)
            .onAppear {
                isFocused = true
            }

        let withChangeHandlers = baseView
            .onChange(of: settingsViewModel.scrubbingConfigs) { _, newConfigs in
                // Update registry when scrubbing configs change
                shortcutRegistry.updateScrubbingConfigs(newConfigs)
            }
            .onChange(of: settingsViewModel.playPauseKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.addMarkerKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.previousMarkerKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.nextMarkerKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.deleteMarkerKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.editMarkerKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.openSettingsKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.nextVideoInDirectoryKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }
            .onChange(of: settingsViewModel.previousVideoInDirectoryKey) { _, _ in
                shortcutRegistry.refreshShortcuts()
            }

        return withChangeHandlers
            // Unified keyboard shortcut handling
            .onKeyPress { keyPress in
                let timestamp = Date().timeIntervalSince1970
                let identifier = KeyboardShortcut.identifier(from: keyPress)

                // Log all key presses with Unicode for debugging
                let unicodeValues = keyPress.characters.unicodeScalars.map { String($0.value, radix: 16) }.joined(separator: " ")
                print("[\(String(format: "%.3f", timestamp))] Key pressed: '\(keyPress.characters)' (Unicode: \(unicodeValues)) modifiers: \(keyPress.modifiers) identifier: '\(identifier)'")

                // Debug: Print available shortcuts if space is pressed
                if keyPress.characters == " " {
                    print("DEBUG: Space key detected. Available shortcuts:")
                    for (id, shortcut) in keyboardShortcuts {
                        print("  - ID: '\(id)', Key: '\(shortcut.key)', Description: \(shortcut.description)")
                    }
                    print("DEBUG: playPauseKey from settings: '\(settingsViewModel.playPauseKey)'")
                    print("DEBUG: Looking for identifier: '\(identifier)'")
                }

                // Look up shortcut in registry
                if let shortcut = keyboardShortcuts[identifier] {
                    print("[\(String(format: "%.3f", timestamp))] Shortcut triggered: \(shortcut.description)")
                    shortcut.action()
                    return .handled
                }

                print("[\(String(format: "%.3f", timestamp))] Key '\(keyPress.characters)' ignored - no matching shortcut")
                return .ignored
            }
    }
}

//
//  PerformanceMonitor.swift
//  SwiftMedia
//
//  Created by Claude on 6/25/25.
//

import Foundation
import AVFoundation
import os.log
import Combine

class PerformanceMonitor: ObservableObject {
    static let shared = PerformanceMonitor()

    @Published var cpuUsage: Double = 0.0
    @Published var memoryUsage: Double = 0.0
    @Published var droppedFrames: Int = 0
    @Published var frameRate: Double = 0.0
    @Published var frameRateStability: Double = 100.0
    @Published var currentMetrics: PerformanceMetrics?
    @Published var isMonitoring = false

    // History for graphs
    @Published var cpuHistory: [Double] = []
    @Published var memoryHistory: [Double] = []
    @Published var frameRateHistory: [Double] = []

    private let logger = Logger(subsystem: "com.swiftmedia", category: "Performance")
    private var timer: Timer?
    private var player: AVPlayer?
    private var lastAccessLogEntry: AVPlayerItemAccessLogEvent?
    private var frameRateSamples: [Double] = []
    private let maxHistorySize = 60
    private let maxFrameRateSamples = 30

    private var cancellables = Set<AnyCancellable>()

    private init() {}

    func startMonitoring(player: AVPlayer) {
        guard !isMonitoring else { return }

        self.player = player
        isMonitoring = true

        // Clear previous data
        clearHistory()

        // Monitor player access logs
        NotificationCenter.default.publisher(for: .AVPlayerItemNewAccessLogEntry, object: player.currentItem)
            .sink { [weak self] _ in
                self?.handleAccessLogUpdate()
            }
            .store(in: &cancellables)

        // Monitor error logs
        NotificationCenter.default.publisher(for: .AVPlayerItemNewErrorLogEntry, object: player.currentItem)
            .sink { [weak self] _ in
                self?.handleErrorLogUpdate()
            }
            .store(in: &cancellables)

        // Start system metrics monitoring
        timer = Timer.scheduledTimer(withTimeInterval: 1.0, repeats: true) { [weak self] _ in
            self?.updateSystemMetrics()
            self?.updateFrameRateStability()
            self?.updateCurrentMetrics()
        }

        logger.info("Performance monitoring started")
    }

    func stopMonitoring() {
        isMonitoring = false
        timer?.invalidate()
        timer = nil
        cancellables.removeAll()
        logger.info("Performance monitoring stopped")
    }

    private func clearHistory() {
        cpuHistory.removeAll()
        memoryHistory.removeAll()
        frameRateHistory.removeAll()
        frameRateSamples.removeAll()
        droppedFrames = 0
    }

    private func handleAccessLogUpdate() {
        guard let item = player?.currentItem,
              let events = item.accessLog()?.events,
              let latestEvent = events.last else { return }

        // Track dropped frames
        if let lastEvent = lastAccessLogEntry {
            let newDrops = Int(latestEvent.numberOfDroppedVideoFrames - lastEvent.numberOfDroppedVideoFrames)
            if newDrops > 0 {
                droppedFrames += newDrops
                logger.warning("Dropped \(newDrops) frames (total: \(self.droppedFrames))")
            }
        }

        lastAccessLogEntry = latestEvent

        // Calculate frame rate from playback info
        if latestEvent.indicatedBitrate > 0 && latestEvent.observedBitrate > 0 {
            // Estimate frame rate based on segment download rate
            let segmentDuration = latestEvent.segmentsDownloadedDuration
            if segmentDuration > 0 {
                frameRate = 30.0 // Default to expected frame rate

                // Adjust based on playback performance
                let efficiency = latestEvent.observedBitrate / latestEvent.indicatedBitrate
                if efficiency < 0.95 {
                    frameRate *= efficiency
                }
            }
        }

        // Check for performance issues
        if latestEvent.numberOfStalls > 0 {
            logger.error("Playback stalled \(latestEvent.numberOfStalls) times")
        }
    }

    private func handleErrorLogUpdate() {
        guard let item = player?.currentItem,
              let events = item.errorLog()?.events else { return }

        for event in events {
            logger.error("Player error: \(event.errorComment ?? "Unknown error")")
        }
    }

    private func updateSystemMetrics() {
        // Update CPU usage
        cpuUsage = getCurrentCPUUsage()
        cpuHistory.append(cpuUsage)
        if cpuHistory.count > maxHistorySize {
            cpuHistory.removeFirst()
        }

        // Update memory usage
        memoryUsage = getCurrentMemoryUsage()
        memoryHistory.append(memoryUsage)
        if memoryHistory.count > maxHistorySize {
            memoryHistory.removeFirst()
        }

