//
//  SwiftMediaApp.swift
//  SwiftMedia
//
//  Created by Ojash Neopane on 6/24/25.
//

import SwiftUI

// MARK: - Notification Names
extension Notification.Name {
    static let openVideoFile = Notification.Name("openVideoFile")
    static let grantDirectoryAccess = Notification.Name("grantDirectoryAccess")
}

class AppDelegate: NSObject, NSApplicationDelegate {
    func applicationShouldTerminateAfterLastWindowClosed(_ sender: NSApplication) -> Bool {
        return true
    }
}

@main
struct SwiftMediaApp: App {
    @NSApplicationDelegateAdaptor(AppDelegate.self) var appDelegate
    @StateObject private var videoPlayerViewModel = VideoPlayerViewModel()

    init() {
        setupMemoryPressureHandling()

        // Verify permissions for default directories at launch
        Task {
            await PermissionVerificationManager.shared.verifyPermissionsAtLaunch()
        }
    }

    var body: some Scene {
        WindowGroup {
            ContentView()
                .frame(minWidth: 800, minHeight: 600)
                .environmentObject(videoPlayerViewModel)
        }
        .windowStyle(.automatic)
        .defaultSize(width: 1200, height: 800)
        .windowResizability(.contentSize)
        .commands {
            // File Menu
            CommandGroup(replacing: .newItem) {
                Button("Open Video...") {
                    NotificationCenter.default.post(name: .openVideoFile, object: nil)
                }
                .keyboardShortcut("o", modifiers: .command)

                Button("Grant Directory Access...") {
                    NotificationCenter.default.post(name: .grantDirectoryAccess, object: nil)
                }
                .keyboardShortcut("g", modifiers: [.command, .shift])

            }
        }
        .onChange(of: videoPlayerViewModel.videoURL) { _, newValue in
            updateWindowTitle()
        }
    }

    private func updateWindowTitle() {
        guard let window = NSApplication.shared.windows.first else { return }

        var title = "SwiftMedia"

        // Include video name if a video is loaded
        if let videoURL = videoPlayerViewModel.videoURL {
            let videoName = videoURL.deletingPathExtension().lastPathComponent
            title = videoName
            window.representedURL = videoURL
        } else {
            window.representedURL = nil
        }

        window.title = title
        window.isDocumentEdited = false
    }

    private func setupMemoryPressureHandling() {
        // Monitor memory pressure notifications
        let memoryPressureSource = DispatchSource.makeMemoryPressureSource(eventMask: [.warning, .critical], queue: .main)
        memoryPressureSource.setEventHandler {
            VideoMemoryManager.handleMemoryWarning()
        }
        memoryPressureSource.resume()

        // Monitor performance optimization notifications
        NotificationCenter.default.addObserver(
            forName: .performanceOptimizationNeeded,
            object: nil,
            queue: .main
        ) { notification in
            if let userInfo = notification.userInfo,
               let type = userInfo["type"] as? String,
               let level = userInfo["level"] as? String {

                switch (type, level) {
                case ("memory", "critical"):
                    // Force memory cleanup
                    VideoMemoryManager.handleMemoryWarning()
                case ("cpu", "high"):
                    // Could reduce quality settings here
                    print("High CPU usage detected - consider reducing video quality")
                case ("frames", "critical"):
                    // Could adjust playback settings
                    print("Frame drops detected - adjusting playback settings")
                default:
                    break
                }
            }
        }
    }
}

