import SwiftUI
import AVKit

struct VideoPlayerView: NSViewRepresentable {
    let player: AVPlayer?

    func makeNSView(context: Context) -> AVPlayerView {
        let playerView = AVPlayerView()
        playerView.player = player
        playerView.controlsStyle = .none // For custom controls
        playerView.allowsPictureInPicturePlayback = false

        // Enable hardware acceleration
        if let player = player, let playerLayer = playerView.layer as? AVPlayerLayer {
            VideoMemoryManager.shared.configureForHardwareAcceleration(
                player: player,
                playerLayer: playerLayer
            )
        }

        // Additional optimizations
        playerView.videoGravity = .resizeAspect

        return playerView
    }

    func updateNSView(_ nsView: AVPlayerView, context: Context) {
        nsView.player = player

        // Re-configure hardware acceleration if player changes
        if let player = player, let playerLayer = nsView.layer as? AVPlayerLayer {
            VideoMemoryManager.shared.configureForHardwareAcceleration(
                player: player,
                playerLayer: playerLayer
            )
        }
    }
}

